</head>
<body>
<div id="main-container">
    <!-- CONTROL PANEL -->
    <div id="control-panel">
        <!-- Time -->
        <label for="slider-time">time relative to collision (s)
            <span id="label-time" class="value-label"></span></label>
        <input type="range" id="slider-time" min="-30" max="30" step="0.1" value="-10">

        <!-- Object 1 -->
        <h4>object 1</h4>
        <label for="slider-m1">mass (kg)
            <span id="label-m1" class="value-label"></span></label>
        <input type="range" id="slider-m1" min="1" max="10" step="0.01" value="5">

        <label for="slider-v1">speed (m/s)
            <span id="label-v1" class="value-label"></span></label>
        <input type="range" id="slider-v1" min="1" max="20" step="0.1" value="10">

        <!-- Object 2 -->
        <h4>object 2</h4>
        <label for="slider-m2">mass (kg)
            <span id="label-m2" class="value-label"></span></label>
        <input type="range" id="slider-m2" min="1" max="10" step="0.01" value="3">

        <label for="slider-v2">speed (m/s)
            <span id="label-v2" class="value-label"></span></label>
        <input type="range" id="slider-v2" min="1" max="20" step="0.1" value="5">

