    <div id="canvas-container"></div>
</div>

<script>
/*--------------- GLOBAL STATE ---------------*/
let params = {
    t: -10,
    m1: 5,
    m2: 3,
    v1_slider: 10,
    v2_slider: 5,          // ignored in physics
    v0x: 1.1,
    v0y: 1.1
};
// joystick configs
const JOY_MAX = 5; // m/s
const joySize = 150;
const knobSize = 28;

/*--------------- DOM ELEMENTS ---------------*/
const sliderTime = document.getElementById('slider-time');
const sliderM1   = document.getElementById('slider-m1');
const sliderV1   = document.getElementById('slider-v1');
const sliderM2   = document.getElementById('slider-m2');
const sliderV2   = document.getElementById('slider-v2');

const labelTime = document.getElementById('label-time');
const labelM1   = document.getElementById('label-m1');
const labelV1   = document.getElementById('label-v1');
const labelM2   = document.getElementById('label-m2');
const labelV2   = document.getElementById('label-v2');
const labelV0x  = document.getElementById('label-v0x');
const labelV0y  = document.getElementById('label-v0y');

function updateLabels(){
    labelTime.textContent = params.t.toFixed(1);
    labelM1.textContent   = params.m1.toFixed(2);
    labelV1.textContent   = params.v1_slider.toFixed(1);
    labelM2.textContent   = params.m2.toFixed(2);
    labelV2.textContent   = params.v2_slider.toFixed(1);
    labelV0x.textContent  = 'v0x: '+params.v0x.toFixed(2);
    labelV0y.textContent  = 'v0y: '+params.v0y.toFixed(2);
}
// attach events
[sliderTime,sliderM1,sliderV1,sliderM2,sliderV2].forEach(sl=>{
    sl.addEventListener('input',()=>{
        params.t = parseFloat(sliderTime.value);
        params.m1= parseFloat(sliderM1.value);
        params.v1_slider=parseFloat(sliderV1.value);
        params.m2= parseFloat(sliderM2.value);
        params.v2_slider=parseFloat(sliderV2.value); // display only
        updateLabels();
    });
});

/*--------------- JOYSTICK IMPLEMENTATION ---------------*/
const joyContainer = document.getElementById('joystick-container');
const knob = document.getElementById('joystick-knob');
let dragging = false;

function setKnobPositionFromVelocity(){
    const center = joySize/2;
    const px = center + (params.v0x/JOY_MAX)*center - knobSize/2;
    const py = center - (params.v0y/JOY_MAX)*center - knobSize/2;
    knob.style.left = `${px}px`;
    knob.style.top  = `${py}px`;
}
function updateVelocityFromPointer(clientX,clientY){
    const rect = joyContainer.getBoundingClientRect();
    const x = clientX - rect.left;
    const y = clientY - rect.top;
    const center = joySize/2;
    let dx = x - center;
    let dy = y - center;
    // clamp to circle (square clamp simpler)
    const max = center;
    dx = Math.max(-max,Math.min(max,dx));
    dy = Math.max(-max,Math.min(max,dy));
    // velocity mapping
    params.v0x = (dx/center)*JOY_MAX;
    params.v0y = (-dy/center)*JOY_MAX;
    // move knob
    knob.style.left = `${center+dx - knobSize/2}px`;
    knob.style.top  = `${center+dy - knobSize/2}px`;
    updateLabels();
}
joyContainer.addEventListener('pointerdown',e=>{
    dragging=true;
    updateVelocityFromPointer(e.clientX,e.clientY);
});
window.addEventListener('pointermove',e=>{
    if(dragging) updateVelocityFromPointer(e.clientX,e.clientY);
});
window.addEventListener('pointerup',()=>dragging=false);

/* initial positions */
updateLabels();
setKnobPositionFromVelocity();

/*--------------- PHYSICS HELPERS ---------------*/
function rotate(vec,angleRad){
    const [x,y]=vec;
    return [
        x*Math.cos(angleRad) - y*Math.sin(angleRad),
        x*Math.sin(angleRad) + y*Math.cos(angleRad)
    ];
}
function magnitude([x,y]){return Math.hypot(x,y);}
function add(v1,v2){return [v1[0]+v2[0], v1[1]+v2[1]];}
function scale(v,s){return [v[0]*s, v[1]*s];}

/*--------------- P5 SKETCH ---------------*/
const sketch = p=>{
    let radiusK;
    p.setup=()=>{
        const cnv = p.createCanvas(p.windowWidth-320, p.windowHeight);
        cnv.parent('canvas-container');
        p.pixelDensity(1);
        radiusK = 3.5; // radius scaling factor
        p.textFont('monospace',14);
    };
    p.windowResized = ()=>{
        p.resizeCanvas(p.windowWidth-320, p.windowHeight);
    }
    p.draw=()=>{
        p.background(255);

        // --- physics calculations ---
        const {m1,m2,v1_slider,t,v0x,v0y} = params;
        const s1_cm = v1_slider;
        const s2_cm = (m1/m2)*s1_cm; // enforces momentum=0 in CM

        const u1_cm = [ s1_cm, 0];
        const u2_cm = [-s2_cm, 0];
        const angle = -p.PI/4; // -45°
        const v1_cm_final = rotate(u1_cm,angle);
        const v2_cm_final = rotate(u2_cm,angle);

        const v0 = [v0x,v0y];

        // decide which velocity to use based on time
        const v1_cm = t<0 ? u1_cm : v1_cm_final;
        const v2_cm = t<0 ? u2_cm : v2_cm_final;

        // positions in CM and then observer frame
        const pos1_cm = scale(v1_cm,t);
        const pos2_cm = scale(v2_cm,t);

        const pos1_obs = add(pos1_cm, scale(v0,t));
        const pos2_obs = add(pos2_cm, scale(v0,t));

        // Pre-collision observer-frame velocities for metrics
        const u1_obs = add(u1_cm, v0);
        const u2_obs = add(u2_cm, v0);

        const v0_mag = magnitude(v0);
        const v1_mag = magnitude(u1_obs);
        const v2_mag = magnitude(u2_obs);
        const energy = 0.5*m1*v1_mag*v1_mag + 0.5*m2*v2_mag*v2_mag;

        // --- drawing helpers ---
        const centerX = p.width/2;
        const centerY = p.height/2;
        function toScreen([x,y]){
            const scaleFac = 10; // pixels per m/s-second
            return [centerX + x*scaleFac, centerY - y*scaleFac];
        }

        // draw trajectories (dashed)
        p.push();
        p.strokeWeight(1.5);
        p.drawingContext.setLineDash([6,6]);

        // past portion
        p.stroke(255,0,0);
        let start = toScreen(scale(u1_cm,-30)); // t=-30
        let mid   = toScreen([0,0]);
        let end1  = toScreen(scale(v1_cm_final,30)); // t=+30 future
        p.line(start[0]+v0x*(-30)*10, start[1]-v0y*(-30)*10,
               mid[0]+v0x*0, mid[1]-v0y*0);
        p.line(mid[0]+v0x*0, mid[1]-v0y*0,
               end1[0]+v0x*30*10, end1[1]-v0y*30*10);

        p.stroke(0,0,255);
        start = toScreen(scale(u2_cm,-30));
        end1  = toScreen(scale(v2_cm_final,30));
        p.line(start[0]+v0x*(-30)*10, start[1]-v0y*(-30)*10,
               mid[0]+v0x*0, mid[1]-v0y*0);
        p.line(mid[0]+v0x*0, mid[1]-v0y*0,
               end1[0]+v0x*30*10, end1[1]-v0y*30*10);

        p.pop();

        // draw particles
        const r1 = radiusK*Math.sqrt(m1);
        const r2 = radiusK*Math.sqrt(m2);
        const [x1,y1]=toScreen(pos1_obs);
        const [x2,y2]=toScreen(pos2_obs);

        p.fill(255,0,0);
        p.noStroke();
        p.circle(x1,y1,r1*2);

        p.fill(0,0,255);
        p.circle(x2,y2,r2*2);

        // observer velocity vector (arrow showing -v0)
        p.push();
        const arrowOrigin = [50, p.height-70];
        const arrowEnd = [arrowOrigin[0]-v0x*15, arrowOrigin[1]+v0y*15];
        p.stroke(0);
        p.strokeWeight(2);
        p.line(arrowOrigin[0],arrowOrigin[1],arrowEnd[0],arrowEnd[1]);
        // arrow head
        const dir = Math.atan2(arrowOrigin[1]-arrowEnd[1], arrowOrigin[0]-arrowEnd[0]);
        const headSize=8;
        p.line(arrowEnd[0],arrowEnd[1],
               arrowEnd[0]+headSize*Math.cos(dir+0.4),
               arrowEnd[1]+headSize*Math.sin(dir+0.4));
        p.line(arrowEnd[0],arrowEnd[1],
               arrowEnd[0]+headSize*Math.cos(dir-0.4),
               arrowEnd[1]+headSize*Math.sin(dir-0.4));
        p.noStroke();
        p.fill(0);
        p.text('observer vel (-v0)', arrowOrigin[0]-20, arrowOrigin[1]+20);
        p.pop();

        // metrics text
        p.fill(0);
        p.noStroke();
        const lines = [
            `|v0|  = ${v0_mag.toFixed(5)} m/s`,
            `|v1|  = ${v1_mag.toFixed(5)} m/s`,
            `|v2|  = ${v2_mag.toFixed(5)} m/s`,
            `Energy = ${energy.toFixed(2)} J`
        ];
        let ty = 24;
        lines.forEach(l=>{
            p.text(l, 20, ty);
            ty += 20;
        });
    };
};
new p5(sketch);
</script>
</body>
