    <div id="main-container">
        <!-- 1. Control Panel Section -->
        <div id="control-panel" class="panel">
            <div class="control-group">
                <label for="shift-slider">shift</label>
                <input type="range" id="shift-slider" min="0" max="25" step="1" value="12">
            </div>
            <div>
                <input type="text" id="text-input" list="phrase-list" value="abcd">
                <datalist id="phrase-list">
                    <option value="abcd"></option>
                    <option value="test phrase"></option>
                    <option value="to be or not to be"></option>
                    <option value="the quick brown fox jumped over the lazy dog"></option>
                </datalist>
            </div>
        </div>

        <!-- 2. Display Area Section -->
        <div id="display-area" class="panel">
            <!-- 3. Visualization Section -->
            <div id="alphabet-visualization">
                <div id="plaintext-alphabet"></div>
                <div id="ciphertext-alphabet"></div>
            </div>

            <!-- 4. Key Section -->
            <div id="key-section">
                <p id="encryption-key-display">encryption key: 12</p>
                <p id="decryption-key-display">decryption key: 14</p>
            </div>

            <!-- 5. Text Output Section -->
            <div id="text-output">
                <p id="plaintext-output">plaintext : abcd</p>
                <p id="ciphertext-output">ciphertext: MNOP</p>
            </div>
        </div>
    </div>

    <script>
        document.addEventListener('DOMContentLoaded', () => {
            // Get references to all DOM elements
            const shiftSlider = document.getElementById('shift-slider');
            const textInput = document.getElementById('text-input');
            const plaintextAlphabetDiv = document.getElementById('plaintext-alphabet');
            const ciphertextAlphabetDiv = document.getElementById('ciphertext-alphabet');
            const encryptionKeyDisplay = document.getElementById('encryption-key-display');
            const decryptionKeyDisplay = document.getElementById('decryption-key-display');
            const plaintextOutput = document.getElementById('plaintext-output');
            const ciphertextOutput = document.getElementById('ciphertext-output');

            const PLAINTEXT_ALPHABET = 'abcdefghijklmnopqrstuvwxyz';
            const CIPHERTEXT_ALPHABET = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ';

            /**
             * Encrypts text using a Caesar cipher.
             * @param {string} text - The plaintext to encrypt.
             * @param {number} shift - The amount to shift by.
             * @returns {string} The resulting ciphertext.
             */
            function encrypt(text, shift) {
                let result = '';
                const lowerText = text.toLowerCase();

                for (let i = 0; i < lowerText.length; i++) {
                    const char = lowerText[i];
                    const index = PLAINTEXT_ALPHABET.indexOf(char);

                    if (index !== -1) {
                        const newIndex = (index + shift) % 26;
                        result += CIPHERTEXT_ALPHABET[newIndex];
                    } else {
                        // Append non-alphabetic characters unchanged
                        result += text[i];
                    }
                }
                return result;
            }

            /**
             * Updates the entire visualization based on control inputs.
             */
            function updateCipher() {
                const shiftValue = parseInt(shiftSlider.value, 10);
                const inputText = textInput.value;

                // 1. Update Alphabet Mapping Visualization
                plaintextAlphabetDiv.innerHTML = '';
                ciphertextAlphabetDiv.innerHTML = '';

                for (let i = 0; i < 26; i++) {
                    // Plaintext alphabet
                    const plainSpan = document.createElement('span');
                    plainSpan.textContent = PLAINTEXT_ALPHABET[i];
                    plaintextAlphabetDiv.appendChild(plainSpan);

                    // Ciphertext alphabet
                    const cipherSpan = document.createElement('span');
                    const shiftedIndex = (i + shiftValue) % 26;
                    cipherSpan.textContent = CIPHERTEXT_ALPHABET[shiftedIndex];
                    ciphertextAlphabetDiv.appendChild(cipherSpan);
                }
                // Fix for shifted alphabet display logic to match screenshot
                // The screenshots show the shifted alphabet starting from the shifted letter
                // e.g., shift 4: EFG...
                const shiftedAlphabet = CIPHERTEXT_ALPHABET.slice(shiftValue) + CIPHERTEXT_ALPHABET.slice(0, shiftValue);
                ciphertextAlphabetDiv.innerHTML = '';
                 for (let i = 0; i < 26; i++) {
                    const cipherSpan = document.createElement('span');
                    cipherSpan.textContent = shiftedAlphabet[i];
                    ciphertextAlphabetDiv.appendChild(cipherSpan);
                }


                // 2. Update Key Display
                const decryptionKey = (26 - shiftValue) % 26;
                encryptionKeyDisplay.textContent = `encryption key: ${shiftValue}`;
                decryptionKeyDisplay.textContent = `decryption key: ${decryptionKey}`;

                // 3. Update Text Output
                plaintextOutput.innerHTML = `<span class="label-blue">plaintext : </span>${inputText}`;

                const ciphertext = encrypt(inputText, shiftValue);
                ciphertextOutput.innerHTML = `<span class="label-green">ciphertext: </span>${ciphertext}`;
            }

            // Add event listeners
            shiftSlider.addEventListener('input', updateCipher);
            textInput.addEventListener('input', updateCipher);

            // Initial call to set up the default state
            updateCipher();
        });
    </script>

</body>
</html>
