<body>

    <div class="main-container">
        <div class="control-panel">
            <div class="control-group">
                <label>coordination</label>
                <div class="button-group">
                    <button id="btn-threefold">threefold</button>
                    <button id="btn-fourfold">fourfold</button>
                    <button id="btn-sixfold">sixfold</button>
                    <button id="btn-eightfold">eightfold</button>
                </div>
            </div>
            <div class="control-group">
                <label for="slider-anion-radius">relative anion radius</label>
                <input type="range" id="slider-anion-radius" min="0.1" max="1.0" step="0.001" value="0.1">
                <span id="value-anion-radius">0.100</span>
            </div>
            <div class="control-group">
                <label for="slider-cation-radius">relative cation radius</label>
                <input type="range" id="slider-cation-radius" min="0.1" max="1.0" step="0.001" value="0.1">
                <span id="value-cation-radius">0.100</span>
            </div>
            <div class="control-group">
                <label for="slider-opacity">opacity of anions</label>
                <input type="range" id="slider-opacity" min="0.0" max="1.0" step="0.01" value="0.8">
            </div>
        </div>
        <div class="main-display-container">
            <div id="canvas-container"></div>
            <p id="ratio-text">The ratio of the cation radius to the anion radius is 1.00.</p>
        </div>
    </div>

    <script type="importmap">
        {
            "imports": {
                "three": "https://unpkg.com/three@0.160.0/build/three.module.js",
                "three/addons/": "https://unpkg.com/three@0.160.0/examples/jsm/"
            }
        }
    </script>

    <script type="module">
        import * as THREE from 'three';
        import { OrbitControls } from 'three/addons/controls/OrbitControls.js';

        let scene, camera, renderer, controls;
        let cation, boundingBox;
        let anions = [];
        let matcapTexture;
        let currentCoordination = 'threefold';

        // --- DOM Elements ---
        const btnThreefold = document.getElementById('btn-threefold');
        const btnFourfold = document.getElementById('btn-fourfold');
        const btnSixfold = document.getElementById('btn-sixfold');
        const btnEightfold = document.getElementById('btn-eightfold');
        const coordinationButtons = [btnThreefold, btnFourfold, btnSixfold, btnEightfold];

        const sliderAnionRadius = document.getElementById('slider-anion-radius');
        const valueAnionRadius = document.getElementById('value-anion-radius');
        const sliderCationRadius = document.getElementById('slider-cation-radius');
        const valueCationRadius = document.getElementById('value-cation-radius');
        const sliderOpacity = document.getElementById('slider-opacity');
        const ratioText = document.getElementById('ratio-text');
        const canvasContainer = document.getElementById('canvas-container');

        // --- Geometry Constants ---
        const ANION_DISTANCE = 1.5; // Distance from origin to anion center

        function createMatcapTexture() {
            const canvas = document.createElement('canvas');
            canvas.width = 64;
            canvas.height = 64;
            const context = canvas.getContext('2d');
            const gradient = context.createRadialGradient(
                canvas.width / 2, canvas.height / 2, 0,
                canvas.width / 2, canvas.height / 2, canvas.width / 2
            );
            gradient.addColorStop(0, '#80a0ff');
            gradient.addColorStop(0.5, '#c0c0ff');
            gradient.addColorStop(1, '#ffc080');
            context.fillStyle = gradient;
            context.fillRect(0, 0, canvas.width, canvas.height);
            return new THREE.CanvasTexture(canvas);
        }

        function init() {
            // Scene
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xffffff);

            // Camera
            const aspect = canvasContainer.clientWidth / canvasContainer.clientHeight;
            camera = new THREE.PerspectiveCamera(50, aspect, 0.1, 100);
            camera.position.set(0, 0, 5);

            // Renderer
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(canvasContainer.clientWidth, canvasContainer.clientHeight);
            renderer.setPixelRatio(window.devicePixelRatio);
            canvasContainer.appendChild(renderer.domElement);

            // Controls
            controls = new OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.05;

            // Lights
            scene.add(new THREE.AmbientLight(0xffffff, 0.6));
            const pointLight = new THREE.PointLight(0xffffff, 0.8);
            pointLight.position.set(5, 5, 5);
            scene.add(pointLight);

            // Materials
            matcapTexture = createMatcapTexture();

            // Initial Setup
            setupEventListeners();
            updateActiveButton();
            updateScene();
            updateSliderValues();
            updateRatioText();

            animate();
        }

        function clearScene() {
            if (cation) scene.remove(cation);
            anions.forEach(anion => scene.remove(anion));
            if (boundingBox) scene.remove(boundingBox);

            cation = null;
            anions = [];
            boundingBox = null;
        }

        function updateScene() {
            clearScene();

            const cationRadius = parseFloat(sliderCationRadius.value);
            const anionRadius = parseFloat(sliderAnionRadius.value);
            const anionOpacity = parseFloat(sliderOpacity.value);

            // --- Create Cation ---
            const cationGeometry = new THREE.SphereGeometry(1, 32, 16);
            const cationMaterial = new THREE.MeshPhongMaterial({ matcap: matcapTexture });
            cation = new THREE.Mesh(cationGeometry, cationMaterial);
            cation.scale.setScalar(cationRadius);
            scene.add(cation);

            // --- Create Anions ---
            const anionPositions = getAnionPositions(currentCoordination);
            const anionGeometry = new THREE.SphereGeometry(1, 32, 16);
            const anionMaterial = new THREE.MeshPhongMaterial({
                color: 0xe0d8e0,
                transparent: true,
                opacity: anionOpacity,
                shininess: 80
            });

            anionPositions.forEach(pos => {
                const anion = new THREE.Mesh(anionGeometry, anionMaterial);
                anion.position.copy(pos);
                anion.scale.setScalar(anionRadius);
                anions.push(anion);
                scene.add(anion);
            });

            // --- Create Bounding Box ---
            if (currentCoordination !== 'threefold') {
                let boxSize;
                const d = ANION_DISTANCE / Math.sqrt(3);
                switch(currentCoordination) {
                    case 'fourfold':
                    case 'eightfold':
                        boxSize = d * 2;
                        break;
                    case 'sixfold':
                        boxSize = ANION_DISTANCE * 2;
                        break;
                }
                const boxGeom = new THREE.BoxGeometry(boxSize, boxSize, boxSize);
                const edges = new THREE.EdgesGeometry(boxGeom);
                boundingBox = new THREE.LineSegments(edges, new THREE.LineBasicMaterial({ color: 0x888888 }));
                scene.add(boundingBox);
            }

            // Reset camera
            controls.target.set(0, 0, 0);
            camera.position.set(0, 0, 5);
            controls.update();
        }

        function getAnionPositions(type) {
            const positions = [];
            const d = ANION_DISTANCE;

            switch(type) {
                case 'threefold': // Equilateral triangle in XY plane
                    positions.push(new THREE.Vector3(d, 0, 0));
                    positions.push(new THREE.Vector3(d * Math.cos(2 * Math.PI / 3), d * Math.sin(2 * Math.PI / 3), 0));
                    positions.push(new THREE.Vector3(d * Math.cos(4 * Math.PI / 3), d * Math.sin(4 * Math.PI / 3), 0));
                    break;
                case 'fourfold': // Tetrahedron
                    const tetrad = d / Math.sqrt(3);
                    positions.push(new THREE.Vector3(tetrad, tetrad, tetrad));
                    positions.push(new THREE.Vector3(tetrad, -tetrad, -tetrad));
                    positions.push(new THREE.Vector3(-tetrad, tetrad, -tetrad));
                    positions.push(new THREE.Vector3(-tetrad, -tetrad, tetrad));
                    break;
                case 'sixfold': // Octahedron
                    positions.push(new THREE.Vector3(d, 0, 0));
                    positions.push(new THREE.Vector3(-d, 0, 0));
                    positions.push(new THREE.Vector3(0, d, 0));
                    positions.push(new THREE.Vector3(0, -d, 0));
                    positions.push(new THREE.Vector3(0, 0, d));
                    positions.push(new THREE.Vector3(0, 0, -d));
                    break;
                case 'eightfold': // Cube
                    const cubed = d / Math.sqrt(3);
                    for (let i = -1; i <= 1; i += 2) {
                        for (let j = -1; j <= 1; j += 2) {
                            for (let k = -1; k <= 1; k += 2) {
                                positions.push(new THREE.Vector3(i * cubed, j * cubed, k * cubed));
                            }
                        }
                    }
                    break;
            }
            return positions;
        }

        function setupEventListeners() {
            btnThreefold.addEventListener('click', () => { currentCoordination = 'threefold'; updateActiveButton(); updateScene(); });
            btnFourfold.addEventListener('click', () => { currentCoordination = 'fourfold'; updateActiveButton(); updateScene(); });
            btnSixfold.addEventListener('click', () => { currentCoordination = 'sixfold'; updateActiveButton(); updateScene(); });
            btnEightfold.addEventListener('click', () => { currentCoordination = 'eightfold'; updateActiveButton(); updateScene(); });

            sliderAnionRadius.addEventListener('input', () => {
                const radius = parseFloat(sliderAnionRadius.value);
                valueAnionRadius.textContent = radius.toFixed(3);
                if(anions.length > 0) {
                    anions.forEach(anion => anion.scale.setScalar(radius));
                }
                updateRatioText();
            });

            sliderCationRadius.addEventListener('input', () => {
                const radius = parseFloat(sliderCationRadius.value);
                valueCationRadius.textContent = radius.toFixed(3);
                if(cation) {
                    cation.scale.setScalar(radius);
                }
                updateRatioText();
            });

            sliderOpacity.addEventListener('input', () => {
                const opacity = parseFloat(sliderOpacity.value);
                if(anions.length > 0) {
                    // All anions share the same material, so we only need to update it once
                    anions[0].material.opacity = opacity;
                }
            });

            window.addEventListener('resize', onWindowResize);
        }

        function updateActiveButton() {
            coordinationButtons.forEach(btn => btn.classList.remove('active'));
            document.getElementById(`btn-${currentCoordination}`).classList.add('active');
        }

        function updateSliderValues() {
            valueAnionRadius.textContent = parseFloat(sliderAnionRadius.value).toFixed(3);
            valueCationRadius.textContent = parseFloat(sliderCationRadius.value).toFixed(3);
        }

        function updateRatioText() {
            const cationR = parseFloat(sliderCationRadius.value);
            const anionR = parseFloat(sliderAnionRadius.value);
            const ratio = anionR > 0 ? (cationR / anionR) : 0;
            ratioText.textContent = `The ratio of the cation radius to the anion radius is ${ratio.toFixed(2)}.`;
        }

        function onWindowResize() {
            camera.aspect = canvasContainer.clientWidth / canvasContainer.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(canvasContainer.clientWidth, canvasContainer.clientHeight);
        }

        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        init();
    </script>

</body>
</html>
