                <input type="range" id="slider-angle" min="10" max="80" value="30" step="1">
            </div>
            <div id="animation-controls">
                <p>release block</p>
                <button id="btn-play">▶</button>
                <button id="btn-pause">||</button>
                <button id="btn-reset">|&lt;</button>
            </div>
        </div>
        <!-- Visualization Area -->
        <div id="visualization-area">
            <p id="info-display"></p>
            <div id="p5-canvas-container">
                <!-- p5.js canvas will be inserted here -->
            </div>
        </div>
    </div>

    <script>
        // p5.js sketch for the inclined plane simulation

        // DOM elements
        let sliderAngle, btnPlay, btnPause, btnReset, infoDisplay, canvasContainer;

        // Physics and state variables
        let theta = 30; // Initial angle in degrees
        const g = 9.81;
        const mass = 20; // Visual mass/force scaling
        let isPlaying = false;
        let blockDist = 0; // Distance traveled along the slope in pixels
        let startTime = 0;
        let accel_pixels_per_sec2 = 0;

        // Drawing constants
        const pixelMeterScale = 50; // 50 pixels = 1 meter
        const vectorScale = 8;
        const blockW = 60, blockH = 30;
        let rampOrigin;

        function setup() {
            canvasContainer = select('#p5-canvas-container');
            let canvas = createCanvas(canvasContainer.width, canvasContainer.height);
            canvas.parent('p5-canvas-container');
            canvas.id('p5-canvas');

            angleMode(DEGREES);

            // DOM Element references and listeners
            sliderAngle = select('#slider-angle');
            btnPlay = select('#btn-play');
            btnPause = select('#btn-pause');
            btnReset = select('#btn-reset');
            infoDisplay = select('#info-display');

            sliderAngle.input(handleSliderChange);
            btnPlay.mousePressed(playAnimation);
            btnPause.mousePressed(pauseAnimation);
            btnReset.mousePressed(resetAnimation);

            // Initial setup
            handleSliderChange();
        }

        function draw() {
            background(255);

            if (isPlaying) {
                let elapsedTime = (millis() - startTime) / 1000; // in seconds
                let s_pixels = 0.5 * accel_pixels_per_sec2 * elapsedTime * elapsedTime;
                blockDist = s_pixels;
            }

            drawScene();
        }

        function handleSliderChange() {
            theta = sliderAngle.value();
            resetAnimation();
            updateInfoDisplay();
        }

        function playAnimation() {
            if (isPlaying) return;
            isPlaying = true;

            // Calculate equivalent start time if resuming from a paused state
            let a = g * sin(theta);
            accel_pixels_per_sec2 = a * pixelMeterScale;
            let t_equiv_sec = sqrt(2 * blockDist / accel_pixels_per_sec2);
            startTime = millis() - (t_equiv_sec * 1000);
        }

        function pauseAnimation() {
            isPlaying = false;
        }

        function resetAnimation() {
            isPlaying = false;
            blockDist = 0;
            startTime = 0;
        }

        function updateInfoDisplay() {
            let a = g * sin(theta);
            infoDisplay.html(`slope = \\( \\theta \\) = ${theta}°; acceleration = \\( g \\sin(\\theta) \\) = ${a.toFixed(3)} m/s²`);
            if (typeof MathJax !== 'undefined' && MathJax.typesetPromise) {
                MathJax.typesetPromise([infoDisplay.elt]);
            }
        }

        function drawScene() {
            // Define ramp geometry based on canvas size and angle
            rampOrigin = createVector(150, height - 100);
            let baseLength = width - rampOrigin.x - 100;
            let rampHeight = baseLength * tan(theta);
            let hypLength = baseLength / cos(theta);

            // Stop animation if block reaches the end
            if (blockDist > hypLength - blockW) {
                blockDist = hypLength - blockW;
                pauseAnimation();
            }

            push(); // Start of scene transformations
            translate(rampOrigin.x, rampOrigin.y);

            // 1. Draw Inclined Plane
            stroke('yellow');
            strokeWeight(2);
            fill(173, 216, 230, 200); // lightblue with some transparency
            triangle(0, 0, baseLength, 0, baseLength, -rampHeight);

            // 2. Draw Angle Marker
            noFill();
            stroke(0);
            strokeWeight(1);
            arc(0, 0, 80, 80, -theta, 0);
            fill(0);
            noStroke();
            textSize(16);
            textAlign(CENTER, CENTER);
            text('θ', 45 * cos(theta/2), -45 * sin(theta/2));

            // 3. Draw Coordinate Axes
            drawAxes();

            // 4. Position and draw the block and forces
            push(); // Start of block-related transformations
            // Move origin to the top corner of the ramp
            translate(baseLength, -rampHeight); 
            // Rotate coordinate system to align with the slope
            rotate(-theta); 
            // Move origin along the slope to the block's center position
            translate(-blockW / 2 - blockDist, -blockH / 2);

            // Now (0,0) is the center of the block, and axes are aligned with the slope

            // Draw force vectors and the block
            drawForcesAndBlock();

            pop(); // End of block-related transformations
            pop(); // End of scene transformations
        }

        function drawAxes() {
            push();
            translate(-80, 50); // Position axes to the left/below the ramp
            rotate(-theta);
            stroke(0);
            fill(0);
            strokeWeight(1.5);

            // X-axis
            line(0, 0, 70, 0);
            triangle(70, 0, 62, -4, 62, 4);
            text('x', 80, 5);

            // Y-axis
            line(0, 0, 0, -70);
            triangle(0, -70, -4, -62, 4, -62);
            text('y', -5, -80);
            pop();
        }

        function drawForcesAndBlock() {
            // Define force vectors relative to the block's rotated frame
            const F_grav_mag = mass * g;
            const F_norm_mag = F_grav_mag * cos(theta);
            const F_net_mag = F_grav_mag * sin(theta);

            // --- Force Decomposition (Dashed lines) ---
            // These are drawn first to be in the background
            let F_grav_vec_rotated = createVector(F_grav_mag * sin(theta), F_grav_mag * cos(theta));
            stroke(0);
            strokeWeight(1.5);
            drawingContext.setLineDash([5, 5]);
            // Parallel component (same as F_net)
            line(0, 0, -F_net_mag / mass * vectorScale, 0);
            // Perpendicular component (same as F_norm)
            line(0, 0, 0, F_norm_mag / mass * vectorScale);
            // Lines to complete the rectangle
            line(-F_net_mag / mass * vectorScale, 0, -F_net_mag / mass * vectorScale, F_norm_mag / mass * vectorScale);
            line(0, F_norm_mag / mass * vectorScale, -F_net_mag / mass * vectorScale, F_norm_mag / mass * vectorScale);
            drawingContext.setLineDash([]);


            // --- Block ---
            stroke(0);
            strokeWeight(1);
            fill('darkgrey');
            rectMode(CENTER);
            rect(0, 0, blockW, blockH);

            // --- Force Vectors ---
            // Normal Force (perp. to slope, positive y in this frame)
            drawArrow(createVector(0,0), createVector(0, -F_norm_mag / mass * vectorScale), 'deepskyblue', '"normal" force');

            // Net Force (parallel to slope, negative x in this frame)
            drawArrow(createVector(0,0), createVector(-F_net_mag / mass * vectorScale, 0), 'purple', 'F   = m g sin(θ)', true);
            fill('purple'); noStroke(); textSize(10); text('net', -90, -1); // Subscript for F_net

            // Gravity (needs to be un-rotated to point down)
            push();
            rotate(theta); // Counter-rotate to get back to world alignment
            drawArrow(createVector(0,0), createVector(0, F_grav_mag / mass * vectorScale), 'red', 'F        =mg');
            fill('red'); noStroke(); textSize(10); text('grav', -18, 14); // Subscript for F_grav
            pop();

            // --- Force Component Labels ---
            // mg cos(theta)
            push();
            let mg_cos_label_pos = createVector(-10, (F_norm_mag / mass * vectorScale)/2);
            translate(mg_cos_label_pos.x, mg_cos_label_pos.y);
            rotate(90);
            fill('red'); noStroke(); textSize(14); textAlign(CENTER,CENTER);
            text('m g cos(θ)', 0, 0);
            pop();

            // Angle marker for decomposition
            push();
            let corner = createVector(-F_net_mag / mass * vectorScale, F_norm_mag / mass * vectorScale);
            translate(corner.x, corner.y);
            noFill(); stroke(0); strokeWeight(1);
            arc(0,0, 40, 40, 90, 90+theta);
            fill(0); noStroke(); textSize(14);
            text('θ', 25*cos(90+theta/2), 25*sin(90+theta/2));
            pop();
        }

        // Helper to draw a vector with an arrowhead and a label
        function drawArrow(base, vec, myColor, myLabel, alignLabel = false) {
            push();
            stroke(myColor);
            strokeWeight(2.5);
            fill(myColor);
            translate(base.x, base.y);

            // Line
            line(0, 0, vec.x, vec.y);

            // Arrowhead
            push();
            translate(vec.x, vec.y);
            rotate(vec.heading());
            triangle(0, 0, -8, -4, -8, 4);
            pop();

            // Label
            if (myLabel) {
                let mid = p5.Vector.mult(vec, 0.5);
                let angle = vec.heading();
                let offsetDist = 20;

                // Position label perpendicular to the vector
                let labelPos = createVector(mid.x + offsetDist*sin(angle), mid.y - offsetDist*cos(angle));

                push();
                translate(labelPos.x, labelPos.y);
                if (alignLabel) {
                    rotate(angle);
                    // Flip text if it's upside down
                    if (angle > 90 && angle < 270) {
                        rotate(180);
                    }
                }
                noStroke();
                textSize(14);
                textAlign(CENTER, CENTER);
                text(myLabel, 0, 0);
                pop();
            }
            pop();
        }
    </script>
</body>
</html>
