                [[4, 11, 2, 14, 15, 0, 8, 13, 3, 12, 9, 7, 5, 10, 6, 1], [13, 0, 11, 7, 4, 9, 1, 10, 14, 3, 5, 12, 2, 15, 8, 6], [1, 4, 11, 13, 12, 3, 7, 14, 10, 15, 6, 8, 0, 5, 9, 2], [6, 11, 13, 8, 1, 4, 10, 7, 9, 5, 0, 15, 14, 2, 3, 12]],
                [[13, 2, 8, 4, 6, 15, 11, 1, 10, 9, 3, 14, 5, 0, 12, 7], [1, 15, 13, 8, 10, 3, 7, 4, 12, 5, 6, 11, 0, 14, 9, 2], [7, 11, 4, 1, 9, 12, 14, 2, 0, 6, 10, 13, 15, 3, 5, 8], [2, 1, 14, 7, 4, 10, 8, 13, 15, 12, 9, 0, 3, 5, 6, 11]]
            ];

            // Helper Functions
            const permute = (input, table) => {
                let output = '';
                for (let i = 0; i < table.length; i++) {
                    output += input[table[i] - 1];
                }
                return output;
            };

            const xor = (a, b) => {
                let result = '';
                for (let i = 0; i < a.length; i++) {
                    result += (a[i] === b[i]) ? '0' : '1';
                }
                return result;
            };

            const leftShift = (bits, n) => bits.substring(n) + bits.substring(0, n);

            const stringToBinary = (str) => {
                return str.split('').map(char => char.charCodeAt(0).toString(2).padStart(8, '0')).join('');
            };

            const binaryToHex = (bin) => {
                let hex = '';
                for (let i = 0; i < bin.length; i += 4) {
                    const chunk = bin.substring(i, i + 4);
                    hex += parseInt(chunk, 2).toString(16);
                }
                return hex;
            };

            // Core DES Functions
            const generateSubkeys = (key64) => {
                const subkeys = [];
                let key56 = permute(key64, PC1);
                let C = key56.substring(0, 28);
                let D = key56.substring(28);

                for (let i = 0; i < 16; i++) {
                    C = leftShift(C, ROTATIONS[i]);
                    D = leftShift(D, ROTATIONS[i]);
                    const combined = C + D;
                    subkeys.push(permute(combined, PC2));
                }
                return subkeys;
            };

            const feistelFunction = (halfBlock, subkey) => {
                const expanded = permute(halfBlock, E);
                const xored = xor(expanded, subkey);
                let sboxOutput = '';
                for (let i = 0; i < 8; i++) {
                    const chunk = xored.substring(i * 6, (i + 1) * 6);
                    const row = parseInt(chunk[0] + chunk[5], 2);
                    const col = parseInt(chunk.substring(1, 5), 2);
                    const val = S_BOXES[i][row * 16 + col];
                    sboxOutput += val.toString(2).padStart(4, '0');
                }
                return permute(sboxOutput, P);
            };

            const desEncryptBlock = (block, subkeys, rounds) => {
                let permuted = permute(block, IP);
                let L = permuted.substring(0, 32);
                let R = permuted.substring(32);

                for (let i = 0; i < rounds; i++) {
                    const tempL = L;
                    L = R;
                    R = xor(tempL, feistelFunction(R, subkeys[i]));
                }

                // Final swap before final permutation
                const preFinal = R + L;
                return permute(preFinal, FP);
            };

            // --- UI LOGIC ---

            const inputMessage = document.getElementById('input-message');
            const inputKey = document.getElementById('input-key');
            const radioSteps = document.querySelectorAll('input[name="steps"]');
            const outputResults = document.getElementById('output-results');

            const updateDisplay = () => {
                const message = inputMessage.value;
                const key = inputKey.value;
                const maxSteps = parseInt(document.querySelector('input[name="steps"]:checked').value);

                // 1. Process Message
                let binaryMessage = stringToBinary(message);
                const remainder = binaryMessage.length % 64;
                if (remainder !== 0) {
                    binaryMessage += '0'.repeat(64 - remainder);
                }
                const messageBlocks = binaryMessage.match(/.{1,64}/g) || [];

                // 2. Process Key
                let binaryKey = stringToBinary(key);
                if (binaryKey.length > 64) {
                    binaryKey = binaryKey.substring(0, 64);
                } else {
                    binaryKey = binaryKey.padEnd(64, '0');
                }

                // 3. Generate Subkeys
                const subkeys = generateSubkeys(binaryKey);

                // 4. Encrypt and Build Output
                let results = '';
                for (let s = 2; s <= maxSteps; s += 2) {
                    let currentStepCiphertext = '';
                    for (const block of messageBlocks) {
                        const encryptedBlockBinary = desEncryptBlock(block, subkeys, s);
                        const encryptedBlockHex = binaryToHex(encryptedBlockBinary);
                        currentStepCiphertext += encryptedBlockHex;
                    }
                    const stepStr = s.toString().padStart(11, ' ');
                    results += `${stepStr}${currentStepCiphertext}\n`;
                }

                outputResults.textContent = results.trimEnd();
            };

            // Event Listeners
            [inputMessage, inputKey].forEach(el => el.addEventListener('input', updateDisplay));
            radioSteps.forEach(el => el.addEventListener('change', updateDisplay));

            // Initial calculation
            updateDisplay();
        });
    </script>

