            </div>
            <div class="slider-row">
                <label for="slider-v1">v₁</label>
                <input type="range" id="slider-v1" min="-15" max="15" step="0.01">
                <span id="value-v1"></span>
            </div>
            <div class="slider-row">
                <label for="slider-x2">x₂</label>
                <input type="range" id="slider-x2" min="-1000" max="1000" step="1">
                <span id="value-x2"></span>
            </div>
            <div class="slider-row">
                <label for="slider-v2">v₂</label>
                <input type="range" id="slider-v2" min="-15" max="15" step="0.01">
                <span id="value-v2"></span>
            </div>
        </div>

        <!-- Visualization Section -->
