    <script>
        document.addEventListener('DOMContentLoaded', () => {
            // Component references
            const sliderX1 = document.getElementById('slider-x1');
            const sliderV1 = document.getElementById('slider-v1');
            const sliderX2 = document.getElementById('slider-x2');
            const sliderV2 = document.getElementById('slider-v2');

            const valueX1 = document.getElementById('value-x1');
            const valueV1 = document.getElementById('value-v1');
            const valueX2 = document.getElementById('value-x2');
            const valueV2 = document.getElementById('value-v2');

            const btnReset = document.getElementById('btn-reset');
            const resultText = document.getElementById('result-text');
            const plotContainer = document.getElementById('plot-container');

            // Default state
            const defaults = {
                x1: 500,
                v1: -10,
                x2: 100,
                v2: 5
            };

            const plotLayout = {
                xaxis: { 
                    title: 't', 
                    range: [0, 1000],
                    gridcolor: 'lightgray'
                },
                yaxis: { 
                    title: 'distance', 
                    autorange: true,
                    zeroline: true,
                    zerolinecolor: 'darkgray',
                    zerolinewidth: 1,
                    gridcolor: 'lightgray'
                },
                showlegend: false,
                margin: { t: 20, r: 20, b: 40, l: 60 }
            };

            function updateUIAndPlot() {
                // 1. Read current values
                const x1 = sliderX1.valueAsNumber;
                const v1 = sliderV1.valueAsNumber;
                const x2 = sliderX2.valueAsNumber;
                const v2 = sliderV2.valueAsNumber;

                // 2. Update value displays
                valueX1.textContent = x1.toFixed(0);
                valueV1.textContent = v1.toFixed(2);
                valueX2.textContent = x2.toFixed(0);
                valueV2.textContent = v2.toFixed(2);

                // 3. Calculate intersection
                let T, X;
                let intersectionExists = false;
                const v_diff = v2 - v1;

                // Check for parallel lines (velocities are equal)
                if (Math.abs(v_diff) < 1e-9) {
                    resultText.textContent = "The runners' paths never intersect";
                } else {
                    T = (x1 - x2) / v_diff;
                    // Check if intersection is within the time domain [0, 1000]
                    if (T >= 0 && T <= 1000) {
                        X = x1 + v1 * T;
                        resultText.textContent = `T = ${T.toFixed(2)} s | X = ${X.toFixed(2)} m`;
                        intersectionExists = true;
                    } else {
                        resultText.textContent = "The runners' paths never intersect";
                    }
                }

                // 4. Update the plot
                const t_max = 1000;

                const trace1 = {
                    x: [0, t_max],
                    y: [x1, x1 + v1 * t_max],
                    type: 'scatter',
                    mode: 'lines',
                    name: 'Runner 1',
                    line: {
                        color: 'rgb(70, 130, 180)',
                        width: 2
                    }
                };

                const trace2 = {
                    x: [0, t_max],
                    y: [x2, x2 + v2 * t_max],
                    type: 'scatter',
                    mode: 'lines',
                    name: 'Runner 2',
                    line: {
                        color: 'rgb(255, 165, 0)',
                        width: 2
                    }
                };

                const intersectionTrace = {
                    x: intersectionExists ? [T] : [],
                    y: intersectionExists ? [X] : [],
                    type: 'scatter',
                    mode: 'markers',
                    name: 'Intersection',
                    marker: {
                        color: '#90ee90', // lightgreen
                        size: 10,
                        line: {
                            color: '#006400', // darkgreen
                            width: 1.5
                        }
                    }
                };

                Plotly.react(plotContainer, [trace1, trace2, intersectionTrace], plotLayout);
            }

            function resetToDefaults() {
                sliderX1.value = defaults.x1;
                sliderV1.value = defaults.v1;
                sliderX2.value = defaults.x2;
                sliderV2.value = defaults.v2;
                updateUIAndPlot();
            }

            // Event Listeners
            [sliderX1, sliderV1, sliderX2, sliderV2].forEach(slider => {
                slider.addEventListener('input', updateUIAndPlot);
            });

            btnReset.addEventListener('click', resetToDefaults);

            // Initial setup
            Plotly.newPlot(plotContainer, [], plotLayout, {responsive: true});
            resetToDefaults();
        });
    </script>

</body>
</html>
