            // 1. Draw Static Scene
            drawBackground();
            drawSun();
            drawCloudsAndRain();
            drawRocks();
            drawAnimal();
            drawPlants();
            drawWater();
            drawStaticLabels();

            // 2. Draw Dynamic Elements based on slider phase
            if (phase >= 1) drawErosion();
            if (phase >= 2) drawRunoff();
            if (phase >= 3) drawSoil();
            if (phase >= 4) drawLeaching();
            if (phase >= 5) drawPlantUptake();
            if (phase >= 6) drawAnimalConsumption();
            if (phase >= 7) drawDecomposition();
            if (phase >= 8) drawGeologicUplift();

            // 3. Handle Tooltips (drawn on top)
            handleTooltips(phase);
        }

        // --- DRAWING HELPER FUNCTIONS ---

        function drawBackground() {
            background('#CAE9FF'); // Sky
            noStroke();
            fill('#9B7653'); // Ground
            rect(0, 250, width, height - 250);
        }

        function drawSun() {
            push();
            translate(520, 80);
            // Sun body
            fill('#FFDD00');
            noStroke();
            ellipse(0, 0, 100, 100);
            // Sun rays
            stroke('#FFDD00');
            strokeWeight(5);
            for (let i = 0; i < 8; i++) {
                rotate(PI / 4);
                line(60, 0, 80, 0);
            }
            pop();
        }

        function drawCloudsAndRain() {
            // Clouds
            fill('#E0E0E0');
            noStroke();
            ellipse(150, 100, 120, 50);
            ellipse(100, 110, 100, 40);
            ellipse(200, 110, 100, 40);
            // Rain
            stroke('#0000FF');
            strokeWeight(1.5);
            for (let i = 0; i < 5; i++) {
                let x = 120 + i * 15;
                line(x, 140, x - 5, 160);
                line(x + 2, 160, x - 3, 180);
            }
        }

        function drawRocks() {
            fill('#5A5A5A');
            noStroke();
            ellipse(150, 320, 100, 80);
            ellipse(120, 330, 80, 60);
            ellipse(180, 330, 70, 50);
        }

        function drawAnimal() {
            push();
            translate(315, 340);
            // Legs
            fill(0);
            noStroke();
            rect(-45, 40, 15, 20);
            rect(25, 40, 15, 20);
            // Body
            fill(255);
            stroke(0);
            strokeWeight(2);
            beginShape();
            vertex(-65, 0);
            vertex(-65, -60);
            vertex(25, -60); // neck top
            vertex(55, -30); // nose
            vertex(25, 0); // neck bottom
            vertex(45, 0);
            vertex(45, 40);
            vertex(-55, 40);
            vertex(-55, 0);
            endShape(CLOSE);
            // Spots
            fill(0);
            noStroke();
            ellipse(0, 10, 8, 8);
            ellipse(15, 25, 6, 6);
            ellipse(-10, 20, 7, 7);
            ellipse(-25, 15, 9, 9);
            // Eye
            fill(0, 0, 255);
            ellipse(45, -45, 5, 5);
            pop();
        }

        function drawPlants() {
            fill('#90EE90');
            stroke('#006400');
            strokeWeight(1);
            triangle(440, 440, 450, 410, 460, 440);
            triangle(460, 440, 470, 415, 480, 440);
            triangle(425, 440, 435, 420, 445, 440);
        }

        function drawWater() {
            fill(0, 0, 255, 150);
            noStroke();
            ellipse(100, 450, 180, 100);
            ellipse(50, 460, 150, 80);
        }

        function drawStaticLabels() {
            textAlign(CENTER, CENTER);
            textSize(16);

            fill('#505050');
            text('Clouds', 150, 105);

            fill('#5A5A5A');
            text('Rocks', 150, 360);

            fill(0);
            text('Animals', 315, 400);

            fill('#006400');
            text('Plants', 485, 415);

            fill('#00008B');
            text('Water', 100, 470);
        }

        // --- DYNAMIC PROCESS DRAWING ---

        function drawArrow(x1, y1, x2, y2) {
            push();
            stroke(0);
            strokeWeight(1.5);
            fill(0);
            line(x1, y1, x2, y2);
            let angle = atan2(y1 - y2, x1 - x2);
            translate(x2, y2);
            rotate(angle - HALF_PI);
            triangle(-4, 0, 4, 0, 0, -8);
            pop();
        }

        function drawErosion() {
            drawArrow(160, 180, 160, 290);
            fill(0); noStroke(); textAlign(LEFT, CENTER); textSize(12);
            text('Erosion', 170, 250);
        }

        function drawRunoff() {
            drawArrow(160, 345, 120, 420);
            fill(0); noStroke(); textAlign(CENTER, CENTER); textSize(12);
            text('Runoff', 160, 385);
        }

        function drawSoil() {
            drawArrow(200, 450, 380, 450);
            fill(0); noStroke(); textAlign(CENTER, CENTER); textSize(12);
            text('Soil', 290, 465);
        }

        function drawLeaching() {
            drawArrow(260, 400, 180, 430);
            fill(0); noStroke(); textAlign(CENTER, CENTER); textSize(12);
            text('Leaching', 240, 420);
        }

        function drawPlantUptake() {
            drawArrow(380, 445, 445, 425);
            fill(0); noStroke(); textAlign(CENTER, CENTER); textSize(12);
            text('Plant uptake', 400, 455);
        }

        function drawAnimalConsumption() {
            drawArrow(450, 405, 360, 370);
            fill(0); noStroke(); textAlign(CENTER, CENTER); textSize(12);
            text('Animals eat plants', 440, 385);
        }

        function drawDecomposition() {
            drawArrow(315, 380, 315, 420);
            push();
            translate(315, 420);
            rotate(PI * 1.1);
            drawArrow(0,0, 30,0);
            pop();
            fill(0); noStroke(); textAlign(CENTER, CENTER); textSize(12);
            text('Decomposition', 270, 410);
        }

        function drawGeologicUplift() {
            // Curved arrow using bezier
            noFill();
            stroke(0);
            strokeWeight(1.5);
            bezier(80, 490, -20, 420, 80, 350, 110, 340);
            // Manually draw the arrowhead at the end of the bezier
            push();
            translate(110, 340);
            rotate(atan2(340 - 350, 110 - 80) + HALF_PI * 1.5);
            fill(0);
            triangle(-4, 0, 4, 0, 0, 8);
            pop();

            fill(0); noStroke(); textAlign(CENTER, CENTER); textSize(12);
            text('Geologic uplift', 70, 380);
        }

        // --- TOOLTIP LOGIC ---

        function initializeHoverables() {
            hoverables = [
                // Static Elements
                { x: 90, y: 85, w: 120, h: 40, phase: 0, tip: "Water vapor condensed in the atmosphere." }, // Clouds
                { x: 100, y: 345, w: 100, h: 30, phase: 0, tip: "A primary reservoir of phosphorus in the form of phosphate." }, // Rocks
                { x: 275, y: 385, w: 80, h: 30, phase: 0, tip: "Obtain phosphorus by eating plants or other animals." }, // Animals
                { x: 450, y: 400, w: 70, h: 30, phase: 0, tip: "Absorb phosphate from the soil to build organic molecules." }, // Plants
                { x: 60, y: 455, w: 80, h: 30, phase: 0, tip: "Phosphorus is carried into water bodies by runoff and leaching." }, // Water
                // Dynamic Processes
                { x: 165, y: 240, w: 60, h: 20, phase: 1, tip: "Weathering of rocks by wind and water releases phosphate." }, // Erosion
                { x: 130, y: 375, w: 60, h: 20, phase: 2, tip: "Phosphate from fertilizers and rocks is washed into rivers and oceans." }, // Runoff
                { x: 265, y: 455, w: 50, h: 20, phase: 3, tip: "Phosphorus in the soil is available for plants." }, // Soil
                { x: 200, y: 410, w: 80, h: 20, phase: 4, tip: "Phosphates in the soil are dissolved in water and carried away." }, // Leaching
                { x: 350, y: 445, w: 100, h: 20, phase: 5, tip: "Plants absorb inorganic phosphate from the soil through their roots." }, // Plant uptake
                { x: 380, y: 375, w: 120, h: 20, phase: 6, tip: "Transfers phosphorus from plants to animals along the food chain." }, // Animals eat plants
                { x: 220, y: 400, w: 100, h: 20, phase: 7, tip: "Bacteria break down dead organic matter, returning phosphate to the soil and water." }, // Decomposition
                { x: 20, y: 370, w: 100, h: 20, phase: 8, tip: "Over millions of years, ocean sediments containing phosphate are lifted up to form new land." } // Geologic uplift
            ];
        }

        function handleTooltips(currentPhase) {
            for (const item of hoverables) {
                if (currentPhase >= item.phase && mouseX > item.x && mouseX < item.x + item.w && mouseY > item.y && mouseY < item.y + item.h) {
                    // Draw tooltip
                    let tipText = item.tip;
                    let textW = textWidth(tipText) + 20;
                    let textH = 30;
                    let tipX = mouseX + 10;
                    let tipY = mouseY + 10;

                    // Prevent tooltip from going off-screen
                    if (tipX + textW > width) {
                        tipX = mouseX - textW - 10;
                    }
                    if (tipY + textH > height) {
                        tipY = mouseY - textH - 10;
                    }

                    push();
                    fill(255, 255, 255, 220); // Semi-transparent white background
                    stroke(0, 150);
                    strokeWeight(1);
                    rect(tipX, tipY, textW, textH, 5); // Rounded rectangle

                    fill(0); // Black text
                    noStroke();
                    textAlign(CENTER, CENTER);
                    textSize(12);
                    text(tipText, tipX + textW / 2, tipY + textH / 2);
                    pop();

                    // Stop after finding the first one to prevent overlapping tooltips
                    break; 
                }
            }
        }
    </script>

