        if(stepData.alternatingTree && stepData.alternatingTree.edges.length > 0){
            p.strokeWeight(3);
            p.stroke('#28a745'); // Green for tree
             stepData.alternatingTree.edges.forEach(edgeIndex => {
                 if (data.edges[edgeIndex]) {
