            p.stroke(0);
            p.strokeWeight(1);
            p.beginShape();
            blossom.vertices.forEach(vertexIndex => {
                const v = data.vertices[vertexIndex];
                p.vertex(v.x, v.y);
            });
            p.endShape(p.CLOSE);

            // Draw super-vertex
            const topVertex = data.vertices[blossom.topVertex];
            p.fill('#ffff00'); // Yellow
            p.stroke(0);
            p.strokeWeight(1);
            p.circle(topVertex.x, topVertex.y, 16);
        }

        // 5. Draw all vertices on top
        p.noStroke();
        p.fill(0);
        data.vertices.forEach(v => {
            p.circle(v.x, v.y, 8);
        });

        // 6. Highlight alternating tree vertices
        if (stepData.alternatingTree) {
             stepData.alternatingTree.vertices.forEach(vertexIndex => {
                 const v = data.vertices[vertexIndex];
                 p.fill(255, 165, 0, 150); // Orange tint
                 p.noStroke();
                 p.circle(v.x, v.y, 14);
             });
        }
    }

    // --- UI & EVENT HANDLING ---
    function updateUI() {
        // Update description text
        document.getElementById('description-text').textContent = demoData[currentExample].steps[currentStep - 1].description;

        // Update active class for example buttons
        document.querySelectorAll('#example-controls .btn').forEach(btn => {
            btn.classList.remove('active');
            if (btn.id === `btn-example-${currentExample}`) {
                btn.classList.add('active');
            }
        });

        // Update active class for step buttons
        document.querySelectorAll('#step-controls .btn').forEach(btn => {
            btn.classList.remove('active');
            const step = btn.id.split('-')[2];
            if (parseInt(step) === currentStep) {
                btn.classList.add('active');
            }
        });

        // Redraw canvas
        drawVisualization();
    }

    function handleExampleClick(e) {
        const exampleId = parseInt(e.target.id.split('-')[2]);
        if (exampleId !== currentExample) {
            currentExample = exampleId;
            currentStep = 1; // Reset to step 1
            updateUI();
        }
    }

    function handleStepClick(e) {
        const stepId = parseInt(e.target.id.split('-')[2]);
        if (stepId !== currentStep) {
            currentStep = stepId;
            updateUI();
        }
    }

    // --- INITIALIZATION ---
    document.addEventListener('DOMContentLoaded', () => {
        // Create p5 instance
        p5Instance = new p5(sketch);

        // Attach event listeners
        document.getElementById('btn-example-1').addEventListener('click', handleExampleClick);
        document.getElementById('btn-example-2').addEventListener('click', handleExampleClick);
        document.querySelectorAll('#step-controls .btn').forEach(btn => {
            btn.addEventListener('click', handleStepClick);
        });

        // Set initial state
        updateUI();
    });

</script>

</body>
</html>
