</script>

<script type="module">
    import * as THREE from 'three';
    import { OrbitControls } from 'three/addons/controls/OrbitControls.js';
    import { Brush, Evaluator, SUBTRACTION } from 'three-bvh-csg';

    // --- DOM Elements ---
    const controlsList = [
        { sliderId: 'slider-cylinder-radius', labelId: 'label-cylinder-radius' },
        { sliderId: 'slider-opacity', labelId: 'label-opacity' },
        { sliderId: 'slider-drill-radius', labelId: 'label-drill-radius' },
        { sliderId: 'slider-inclination', labelId: 'label-inclination' },
        { sliderId: 'slider-rotation', labelId: 'label-rotation' },
        { sliderId: 'slider-offset', labelId: 'label-offset' },
    ];
    const sliders = {};
    controlsList.forEach(({ sliderId }) => {
        sliders[sliderId] = document.getElementById(sliderId);
    });
    const checkboxBoundary = document.getElementById('checkbox-boundary-only');
    const vizContainer = document.getElementById('visualization-container');
    const canvas = document.getElementById('main-canvas');

    // --- 3D Scene Setup ---
    let scene, camera, renderer, controls;
    let csgEvaluator;
    let csgResult, ghostCylinder, boundaryCurve1, boundaryCurve2;

    const tanMaterial = new THREE.MeshStandardMaterial({
        color: 0xD2B48C,
        metalness: 0.3,
        roughness: 0.6,
        transparent: true,
        side: THREE.DoubleSide
    });
    const ghostMaterial = new THREE.MeshStandardMaterial({
        color: 0xcccccc,
        metalness: 0.1,
        roughness: 0.8,
        transparent: true,
        opacity: 0.15
    });
    const curveMaterial1 = new THREE.MeshBasicMaterial({ color: 0xFFa500 });
    const curveMaterial2 = new THREE.MeshBasicMaterial({ color: 0x4169E1 });

    function init() {
        // Scene
        scene = new THREE.Scene();
        scene.background = new THREE.Color(0xe0e0e0);

        // Camera
        camera = new THREE.PerspectiveCamera(50, vizContainer.clientWidth / vizContainer.clientHeight, 0.1, 100);
        camera.position.set(0, 3, 10);

        // Renderer
        renderer = new THREE.WebGLRenderer({ canvas: canvas, antialias: true });
        renderer.setSize(vizContainer.clientWidth, vizContainer.clientHeight);
        renderer.setPixelRatio(window.devicePixelRatio);

        // Controls
        controls = new OrbitControls(camera, renderer.domElement);
        controls.enableDamping = true;

        // Lighting
        const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
        scene.add(ambientLight);
        const dirLight1 = new THREE.DirectionalLight(0xffffff, 1.0);
        dirLight1.position.set(5, 10, 7.5);
        scene.add(dirLight1);
        const dirLight2 = new THREE.DirectionalLight(0xffffff, 0.5);
        dirLight2.position.set(-5, -2, 2);
        scene.add(dirLight2);

        // CSG
        csgEvaluator = new Evaluator();

        // Event Listeners
        window.addEventListener('resize', onWindowResize);
        controlsList.forEach(({ sliderId, labelId }) => {
            const slider = document.getElementById(sliderId);
            const label = document.getElementById(labelId);
            slider.addEventListener('input', () => {
                label.textContent = parseFloat(slider.value).toFixed(2);
                updateScene();
            });
            // Initial label update
            label.textContent = parseFloat(slider.value).toFixed(2);
        });
        checkboxBoundary.addEventListener('change', updateScene);

        // Initial Scene
        updateScene();
        animate();
    }

    function onWindowResize() {
        camera.aspect = vizContainer.clientWidth / vizContainer.clientHeight;
        camera.updateProjectionMatrix();
        renderer.setSize(vizContainer.clientWidth, vizContainer.clientHeight);
    }

    function updateScene() {
        // --- Cleanup old objects ---
        if (csgResult) scene.remove(csgResult);
        if (ghostCylinder) scene.remove(ghostCylinder);
        if (boundaryCurve1) scene.remove(boundaryCurve1);
        if (boundaryCurve2) scene.remove(boundaryCurve2);
        if (csgResult) csgResult.geometry.dispose();
        if (ghostCylinder) ghostCylinder.geometry.dispose();
        if (boundaryCurve1) boundaryCurve1.geometry.dispose();
        if (boundaryCurve2) boundaryCurve2.geometry.dispose();


        // --- Read current parameters ---
        const params = {
            cylRadius: parseFloat(sliders['slider-cylinder-radius'].value),
            opacity: parseFloat(sliders['slider-opacity'].value),
            drillRadius: parseFloat(sliders['slider-drill-radius'].value),
            inclination: parseFloat(sliders['slider-inclination'].value),
            rotation: parseFloat(sliders['slider-rotation'].value),
            offset: parseFloat(sliders['slider-offset'].value),
            boundaryOnly: checkboxBoundary.checked
        };

        // --- Create Geometries and Brushes ---
        const mainCylGeom = new THREE.CylinderGeometry(params.cylRadius, params.cylRadius, 8, 64);
        const drillGeom = new THREE.CylinderGeometry(params.drillRadius, params.drillRadius, 20, 64);

        const mainBrush = new Brush(mainCylGeom);
        const drillBrush = new Brush(drillGeom);

        // --- Apply transformations to the drill brush ---
        // A standard cylinder is along Y. Rotate it to be along X.
        const quatZ = new THREE.Quaternion().setFromAxisAngle(new THREE.Vector3(0, 0, 1), Math.PI / 2);
        // Apply user-defined rotations
        const quatX = new THREE.Quaternion().setFromAxisAngle(new THREE.Vector3(1, 0, 0), params.inclination);
        const quatY = new THREE.Quaternion().setFromAxisAngle(new THREE.Vector3(0, 1, 0), params.rotation);

        drillBrush.quaternion.multiplyQuaternions(quatY, quatX).multiply(quatZ);
        drillBrush.position.y = params.offset;
        drillBrush.updateMatrixWorld();

        // --- Perform CSG Operation ---
        csgResult = csgEvaluator.evaluate(mainBrush, drillBrush, SUBTRACTION);
        csgResult.material = tanMaterial.clone();
        csgResult.material.opacity = params.opacity;
        scene.add(csgResult);

        // --- Create Ghost Cylinder ---
        ghostCylinder = new THREE.Mesh(mainCylGeom, ghostMaterial.clone());
        scene.add(ghostCylinder);

        // --- Calculate Boundary Curve ---
        const points1 = [];
        const points2 = [];
        const drillMatrix = drillBrush.matrixWorld.elements;
        const numSegments = 200;

        for (let i = 0; i <= numSegments; i++) {
            const phi = (i / numSegments) * Math.PI * 2;
            const cosPhi = Math.cos(phi);
            const sinPhi = Math.sin(phi);

            // From pre-calculated quadratic equation coefficients
            const Kx = drillMatrix[4];
            const Kz = drillMatrix[6];
            const Cx = drillMatrix[0] * params.drillRadius * cosPhi + drillMatrix[8] * params.drillRadius * sinPhi + drillMatrix[12];
            const Cz = drillMatrix[2] * params.drillRadius * cosPhi + drillMatrix[10] * params.drillRadius * sinPhi + drillMatrix[14];

            const A = Kx * Kx + Kz * Kz;
            const B = 2 * (Kx * Cx + Kz * Cz);
            const C = Cx * Cx + Cz * Cz - params.cylRadius * params.cylRadius;

            const discriminant = B * B - 4 * A * C;

            if (discriminant >= 0) {
                const sqrtDiscriminant = Math.sqrt(discriminant);
                const t1 = (-B + sqrtDiscriminant) / (2 * A);
                const t2 = (-B - sqrtDiscriminant) / (2 * A);

                // Calculate world points for both solutions
                const p1 = new THREE.Vector3(params.drillRadius * cosPhi, t1, params.drillRadius * sinPhi).applyMatrix4(drillBrush.matrixWorld);
                const p2 = new THREE.Vector3(params.drillRadius * cosPhi, t2, params.drillRadius * sinPhi).applyMatrix4(drillBrush.matrixWorld);
                points1.push(p1);
                points2.push(p2);
            }
        }

        if (points1.length > 1) {
            const curve1 = new THREE.CatmullRomCurve3(points1, true);
            const tubeGeom1 = new THREE.TubeGeometry(curve1, 128, 0.05, 12, true);
            boundaryCurve1 = new THREE.Mesh(tubeGeom1, curveMaterial1);
            scene.add(boundaryCurve1);
        }
        if (points2.length > 1) {
            const curve2 = new THREE.CatmullRomCurve3(points2, true);
            const tubeGeom2 = new THREE.TubeGeometry(curve2, 128, 0.05, 12, true);
            boundaryCurve2 = new THREE.Mesh(tubeGeom2, curveMaterial2);
            scene.add(boundaryCurve2);
        }

        // --- Apply Visibility Logic ---
        if (params.boundaryOnly) {
            csgResult.visible = false;
            ghostCylinder.visible = true;
            if (boundaryCurve1) boundaryCurve1.visible = true;
            if (boundaryCurve2) boundaryCurve2.visible = true;
        } else {
            csgResult.visible = true;
            ghostCylinder.visible = false;
            if (boundaryCurve1) boundaryCurve1.visible = true;
            if (boundaryCurve2) boundaryCurve2.visible = true;
        }
    }

    function animate() {
        requestAnimationFrame(animate);
        controls.update();
        renderer.render(scene, camera);
    }

    init();

</script>

</body>
</html>
