    <script>
        document.addEventListener('DOMContentLoaded', () => {

            // --- DOM Element References ---
            const sliderHue = document.getElementById('slider-hue');
            const sliderSaturation = document.getElementById('slider-saturation');
            const sliderBrightness = document.getElementById('slider-brightness');
            const valueHue = document.getElementById('value-hue');
            const valueSaturation = document.getElementById('value-saturation');
            const valueBrightness = document.getElementById('value-brightness');
            const btnReset = document.getElementById('btn-reset');
            const arrowHue = document.getElementById('arrow-hue');
            const arrowSaturation = document.getElementById('arrow-saturation');
            const arrowBrightness = document.getElementById('arrow-brightness');

            const barWidth = 450;
            const barHeight = 40;
            const resultBarHeight = 60;
            const barRadius = 20;

            const initialValues = {
                h: 0,
                s: 0.5,
                b: 1
            };

            let hueSketch, satSketch, brightSketch, resultSketch;

            // --- P5.js Sketches ---

            // Hue Bar Sketch (Static)
            const sketchHue = (p) => {
                p.setup = () => {
                    p.createCanvas(barWidth, barHeight);
                    p.colorMode(p.HSB, 1, 1, 1, 1);
                    p.noStroke();

                    // Draw the gradient
                    for (let i = 0; i < p.width; i++) {
                        const hue = i / p.width;
                        p.fill(hue, 1, 1);
                        p.rect(i, 0, 1, p.height);
                    }

                    // Apply rounded corners using a clipping mask
                    const g = p.drawingContext;
                    g.save();
                    g.fillStyle = 'rgba(0,0,0,0)'; // transparent fill
                    g.globalCompositeOperation = 'destination-in';
                    g.beginPath();
                    g.roundRect(0, 0, p.width, p.height, barRadius);
                    g.fill();
                    g.restore();

                    p.noLoop();
                };
            };

            // Saturation Bar Sketch (Dynamic)
            const sketchSaturation = (p) => {
                p.setup = () => {
                    p.createCanvas(barWidth, barHeight);
                    p.colorMode(p.HSB, 1, 1, 1, 1);
                    p.noLoop();
                };

                p.drawBar = (h, b) => {
                    p.noStroke();
                    for (let i = 0; i < p.width; i++) {
                        const sat = i / p.width;
                        p.fill(h, sat, b);
                        p.rect(i, 0, 1, p.height);
                    }
                    // Apply rounded corners mask
                    const g = p.drawingContext;
                    g.save();
                    g.fillStyle = 'rgba(0,0,0,0)';
                    g.globalCompositeOperation = 'destination-in';
                    g.beginPath();
                    g.roundRect(0, 0, p.width, p.height, barRadius);
                    g.fill();
                    g.restore();
                };
            };

            // Brightness Bar Sketch (Dynamic)
            const sketchBrightness = (p) => {
                p.setup = () => {
                    p.createCanvas(barWidth, barHeight);
                    p.colorMode(p.HSB, 1, 1, 1, 1);
                    p.noLoop();
                };

                p.drawBar = (h, s) => {
                    p.noStroke();
                    for (let i = 0; i < p.width; i++) {
                        const bright = i / p.width;
                        p.fill(h, s, bright);
                        p.rect(i, 0, 1, p.height);
                    }
                    // Apply rounded corners mask
                    const g = p.drawingContext;
                    g.save();
                    g.fillStyle = 'rgba(0,0,0,0)';
                    g.globalCompositeOperation = 'destination-in';
                    g.beginPath();
                    g.roundRect(0, 0, p.width, p.height, barRadius);
                    g.fill();
                    g.restore();
                };
            };

            // Result Bar Sketch (Dynamic)
            const sketchResult = (p) => {
                p.setup = () => {
                    p.createCanvas(barWidth, resultBarHeight);
                    p.colorMode(p.HSB, 1, 1, 1, 1);
                    p.noLoop();
                };

                p.drawBar = (h, s, b) => {
                    p.background(h, s, b);
                    p.noStroke();
                    p.fill(h, s, b);
                    p.rect(0, 0, p.width, p.height, barRadius);
                };
            };

            // --- Main Logic ---

            function updateVisuals() {
                const h = parseFloat(sliderHue.value);
                const s = parseFloat(sliderSaturation.value);
                const b = parseFloat(sliderBrightness.value);

                // Update value displays
                valueHue.textContent = h.toFixed(3).replace(/^0\./, '.');
                valueSaturation.textContent = s.toFixed(3).replace(/^0\./, '.');
                valueBrightness.textContent = b.toFixed(3).replace(/^0\./, '.');

                // Redraw dynamic bars
                satSketch.drawBar(h, b);
                brightSketch.drawBar(h, s);
                resultSketch.drawBar(h, s, b);

                // Update arrow positions
                arrowHue.style.left = `${h * barWidth}px`;
                arrowSaturation.style.left = `${s * barWidth}px`;
                arrowBrightness.style.left = `${b * barWidth}px`;
            }

            function resetToInitial() {
                sliderHue.value = initialValues.h;
                sliderSaturation.value = initialValues.s;
                sliderBrightness.value = initialValues.b;
                updateVisuals();
            }

            // --- Initialization ---

            // Instantiate p5 sketches
            hueSketch = new p5(sketchHue, 'canvas-hue-parent');
            satSketch = new p5(sketchSaturation, 'canvas-saturation-parent');
            brightSketch = new p5(sketchBrightness, 'canvas-brightness-parent');
            resultSketch = new p5(sketchResult, 'canvas-result-parent');

            // Add event listeners
            sliderHue.addEventListener('input', updateVisuals);
            sliderSaturation.addEventListener('input', updateVisuals);
            sliderBrightness.addEventListener('input', updateVisuals);
            btnReset.addEventListener('click', resetToInitial);

            // Set initial state
            resetToInitial();
        });
    </script>
</body>
</html>
