            <div class="control-row">
                <label for="slider-gamma">γ</label>
                <input type="range" id="slider-gamma" min="100" max="2000" step="1" value="500">
                <span id="value-gamma">500</span>
            </div>
        </div>
        <div id="plot-container"></div>
    </div>

    <script>
        document.addEventListener('DOMContentLoaded', () => {
            // --- DOM Element References ---
            const sliderAlpha = document.getElementById('slider-alpha');
            const sliderBeta = document.getElementById('slider-beta');
            const sliderGamma = document.getElementById('slider-gamma');
            const valueAlpha = document.getElementById('value-alpha');
            const valueBeta = document.getElementById('value-beta');
            const valueGamma = document.getElementById('value-gamma');
            const plotContainer = document.getElementById('plot-container');

            // --- ODE Solver (Runge-Kutta 4th Order) ---

            /**
             * Defines the system of differential equations.
             * y[0] = ΔT, y[1] = C_X
             */
            function derivatives(y, t, alpha, beta, gamma) {
                const deltaT = y[0];
                const cX = y[1];
                const expTerm = Math.exp(deltaT / (1 + deltaT / alpha));

                const dDeltaT_dt = gamma * cX * expTerm - beta * deltaT;
                const dCx_dt = 1 - cX * expTerm;

                return [dDeltaT_dt, dCx_dt];
            }

            /**
             * Performs a single step of the RK4 method.
             */
            function rk4Step(y, t, h, params, derivativesFunc) {
                const k1 = derivativesFunc(y, t, ...params);
                const k2_y = y.map((val, i) => val + 0.5 * h * k1[i]);
                const k2 = derivativesFunc(k2_y, t + 0.5 * h, ...params);
                const k3_y = y.map((val, i) => val + 0.5 * h * k2[i]);
                const k3 = derivativesFunc(k3_y, t + 0.5 * h, ...params);
                const k4_y = y.map((val, i) => val + h * k3[i]);
                const k4 = derivativesFunc(k4_y, t + h, ...params);

                return y.map((val, i) => val + (h / 6) * (k1[i] + 2 * k2[i] + 2 * k3[i] + k4[i]));
            }

            /**
             * Solves the ODE system over the time interval.
             */
            function solveODE(alpha, beta, gamma) {
                const t_max = 20;
                const h = 0.05;
                const n_steps = Math.floor(t_max / h);

                let t = 0;
                let y = [0, 0]; // Initial conditions: [ΔT(0), C_X(0)]

                const t_arr = [0];
                const deltaT_arr = [y[0]];
                const cx_arr = [y[1]];

                const params = [alpha, beta, gamma];

                for (let i = 0; i < n_steps; i++) {
                    y = rk4Step(y, t, h, params, derivatives);
                    t += h;

                    t_arr.push(t);
                    deltaT_arr.push(y[0]);
                    cx_arr.push(y[1]);
                }
                return { t: t_arr, deltaT: deltaT_arr, cx: cx_arr };
            }

            // --- Plotting and Interaction Logic ---

            function updatePlot() {
                // 1. Get current values from sliders
                const alpha = parseFloat(sliderAlpha.value);
                const beta = parseFloat(sliderBeta.value);
                const gamma = parseFloat(sliderGamma.value);

                // 2. Update the numerical displays
                valueAlpha.textContent = alpha.toFixed(4);
                valueBeta.textContent = beta.toFixed(2);
                valueGamma.textContent = gamma.toFixed(0);

                // 3. Solve the ODEs with the new parameters
                const solution = solveODE(alpha, beta, gamma);

                // 4. Prepare data for plotting (scale C_X)
                const scaledCx = solution.cx.map(val => val * 1000);

                // 5. Define Plotly traces
                const traceDeltaT = {
                    x: solution.t,
                    y: solution.deltaT,
                    mode: 'lines',
                    line: { color: 'orange', width: 2 }
                };

                const traceCx = {
                    x: solution.t,
                    y: scaledCx,
                    mode: 'lines',
                    line: { color: 'steelblue', width: 2 }
                };

                // 6. Define Plotly layout
                const layout = {
                    xaxis: { title: '', range: [0, 20], zeroline: true },
                    yaxis: { title: '', range: [0, 100], zeroline: true },
                    showlegend: false,
                    margin: { l: 50, r: 20, b: 30, t: 20 },
                    plot_bgcolor: 'white',
                    paper_bgcolor: 'white',
                    annotations: [
                        {
                            text: 'ΔT',
                            x: -0.06,
                            y: 100,
                            xref: 'paper',
                            yref: 'y',
                            xanchor: 'right',
                            yanchor: 'top',
                            showarrow: false,
                            font: { color: 'purple', size: 14 }
                        },
                        {
                            text: '1000 C<sub>X</sub>',
                            x: -0.06,
                            y: 95,
                            xref: 'paper',
                            yref: 'y',
                            xanchor: 'right',
                            yanchor: 'top',
                            showarrow: false,
                            font: { color: 'purple', size: 14 }
                        }
                    ]
                };

                // 7. Render the plot
                Plotly.newPlot(plotContainer, [traceDeltaT, traceCx], layout, {responsive: true});
            }

            // --- Event Listeners ---
            sliderAlpha.addEventListener('input', updatePlot);
            sliderBeta.addEventListener('input', updatePlot);
            sliderGamma.addEventListener('input', updatePlot);

            // --- Initial Plot ---
            updatePlot();
        });
    </script>
