    };

    const values = {
        n: document.getElementById('value-n'),
        tMin: document.getElementById('value-tmin'),
        tMax: document.getElementById('value-tmax'),

        tRef: document.getElementById('value-tref'),
        c: document.getElementById('value-c'),
        b: document.getElementById('value-b'),
        a: document.getElementById('value-a'),
    };

    let state = {
        activeModel: 'arrhenius' // 'arrhenius' or 'exponential'
    };

    function linearRegression(data) {
        let sumX = 0, sumY = 0, sumXY = 0, sumX2 = 0;
        const n = data.length;

        for (const [x, y] of data) {
            sumX += x;
            sumY += y;
            sumXY += x * y;
            sumX2 += x * x;
        }

        const slope = (n * sumXY - sumX * sumY) / (n * sumX2 - sumX * sumX);
        const intercept = (sumY - slope * sumX) / n;

        const yMean = sumY / n;
        let ssTot = 0;
        let ssRes = 0;

        for (const [x, y] of data) {
            ssTot += (y - yMean) ** 2;
            ssRes += (y - (slope * x + intercept)) ** 2;
        }

        const rSquared = 1 - (ssRes / ssTot);

        return { slope, intercept, rSquared };
    }

    function updateVisualization() {
        const n = parseInt(controls.n.value);
        const tMin = parseFloat(controls.tMin.value);
        const tMax = parseFloat(controls.tMax.value);

        const tRef = parseFloat(controls.tRef.value);
        const c_gen = parseFloat(controls.c.value);
        const b_gen = parseFloat(controls.b.value);
        const a_gen = parseFloat(controls.a.value);

        // 1. Generate Temperatures
        const temperatures = Array.from({ length: n }, (_, i) => tMin + i * (tMax - tMin) / (n - 1));

        // 2. Generate Data
        let dataPoints = [];
        if (state.activeModel === 'arrhenius') {
            const k_tRef = Math.exp(-a_gen / (tRef + b_gen));
            dataPoints = temperatures.map(T => {
                const k_T = Math.exp(-a_gen / (T + b_gen));
                const y = k_T / k_tRef;
                return { T, y };
            });
        } else { // exponential
            dataPoints = temperatures.map(T => {
                const y = Math.exp(c_gen * (T - tRef));
                return { T, y };
            });
        }

        const plotDataX = dataPoints.map(p => p.T);
        const plotDataY = dataPoints.map(p => p.y);

        // 3. Perform Fit and 4. Update Plots
        if (state.activeModel === 'arrhenius') {
            // Fit with exponential model
            const fitData = dataPoints.map(p => [p.T - tRef, Math.log(p.y)]);
            const { slope: c_fit, rSquared } = linearRegression(fitData);

            const lineRange = [Math.min(...plotDataX), Math.max(...plotDataX)];
            const fitLineX = Array.from({length: 100}, (_, i) => lineRange[0] + i * (lineRange[1] - lineRange[0]) / 99);
            const fitLineY = fitLineX.map(T => Math.exp(c_fit * (T - tRef)));

            // Top Plot
            const topTraceData = { x: plotDataX, y: plotDataY, mode: 'markers', type: 'scatter', name: 'Data', marker: { color: '#d62728' } };
            const topTraceFit = { x: fitLineX, y: fitLineY, mode: 'lines', type: 'scatter', name: 'Fit', line: { color: '#d62728' } };
            const topLayout = {
                title: `exponential model fit to Arrhenius data<br>c = ${c_fit.toFixed(3)}   R² = ${rSquared.toFixed(3)}`,
                xaxis: { title: '<i>T</i> (°C)' },
                yaxis: { title: '<i>k(T)/k(T<sub>ref</sub>)</i>' },
                showlegend: false,
                margin: {t: 60, b: 40, l: 60, r: 20}
            };
            Plotly.newPlot('plot-top', [topTraceData, topTraceFit], topLayout);

            // Bottom Plot
            const bottomTraceData = { x: fitData.map(p => p[0]), y: fitData.map(p => p[1]), mode: 'markers', type: 'scatter', name: 'Transformed Data', marker: { color: '#2ca02c' } };
            const bottomTraceFit = { x: fitData.map(p => p[0]), y: fitData.map(p => c_fit * p[0]), mode: 'lines', type: 'scatter', name: 'Linear Fit', line: { color: '#2ca02c' } };
            const bottomLayout = {
                title: `linear fit of the exponential model<br>R² = ${rSquared.toFixed(3)}`,
                xaxis: { title: '<i>T</i> - <i>T<sub>ref</sub></i>' },
                yaxis: { title: 'log(<i>k(T)/k(T<sub>ref</sub>)</i>)' },
                showlegend: false,
                margin: {t: 60, b: 40, l: 60, r: 20},
                shapes: [
                    { type: 'line', x0: Math.min(...bottomTraceData.x), y0: 0, x1: Math.max(...bottomTraceData.x), y1: 0, line: { color: 'grey', width: 1, dash: 'solid' } },
                    { type: 'line', x0: 0, y0: Math.min(...bottomTraceData.y), x1: 0, y1: Math.max(...bottomTraceData.y), line: { color: 'grey', width: 1, dash: 'solid' } }
                ]
            };
            Plotly.newPlot('plot-bottom', [bottomTraceData, bottomTraceFit], bottomLayout);

        } else { // exponential model is generating data
            // Fit with Arrhenius model
            const fitData = dataPoints.map(p => [1 / (p.T + b_gen), Math.log(p.y)]);
            const { slope, intercept, rSquared } = linearRegression(fitData);
            const a_fit = -slope;

            const lineRange = [Math.min(...plotDataX), Math.max(...plotDataX)];
            const fitLineX = Array.from({length: 100}, (_, i) => lineRange[0] + i * (lineRange[1] - lineRange[0]) / 99);
            const k_tRef_fit = Math.exp(-a_fit / (tRef + b_gen));
            const fitLineY = fitLineX.map(T => Math.exp(-a_fit / (T + b_gen)) / k_tRef_fit);

            // Top Plot
            const topTraceData = { x: plotDataX, y: plotDataY, mode: 'markers', type: 'scatter', name: 'Data', marker: { color: '#d62728' } };
            const topTraceFit = { x: fitLineX, y: fitLineY, mode: 'lines', type: 'scatter', name: 'Fit', line: { color: '#d62728' } };
            const topLayout = {
                title: `Arrhenius model fit to exponential data<br>a = ${a_fit.toFixed(0)},   R² = ${rSquared.toFixed(3)}`,
                xaxis: { title: '<i>T</i> (°C)' },
                yaxis: { title: '<i>k(T)/k(T<sub>ref</sub>)</i>' },
                showlegend: false,
                margin: {t: 60, b: 40, l: 60, r: 20}
            };
            Plotly.newPlot('plot-top', [topTraceData, topTraceFit], topLayout);

            // Bottom Plot
            const bottomTraceData = { x: fitData.map(p => p[0]), y: fitData.map(p => p[1]), mode: 'markers', type: 'scatter', name: 'Transformed Data', marker: { color: '#1f77b4' } };
            const bottomFitLineX = fitData.map(p => p[0]);
            const bottomFitLineY = bottomFitLineX.map(x => slope * x + intercept);
            const bottomTraceFit = { x: bottomFitLineX, y: bottomFitLineY, mode: 'lines', type: 'scatter', name: 'Linear Fit', line: { color: '#1f77b4' } };
            const bottomLayout = {
                title: `linear fit of the Arrhenius model<br>R² = ${rSquared.toFixed(3)}`,
                xaxis: { title: '1/(<i>T</i> + <i>b</i>)', autorange: 'reversed' },
                yaxis: { title: 'log(<i>k(T)/k(T<sub>ref</sub>)</i>)' },
                showlegend: false,
                margin: {t: 60, b: 40, l: 60, r: 20},
                shapes: [
                     { type: 'line', x0: Math.min(...bottomTraceData.x), y0: 0, x1: Math.max(...bottomTraceData.x), y1: 0, line: { color: 'grey', width: 1, dash: 'solid' } }
                ]
            };
            Plotly.newPlot('plot-bottom', [bottomTraceData, bottomTraceFit], bottomLayout);
        }
    }

    // Event Listeners
    controls.btnArrhenius.addEventListener('click', () => {
        state.activeModel = 'arrhenius';
        controls.btnArrhenius.classList.add('active-button');
        controls.btnExponential.classList.remove('active-button');
        updateVisualization();
    });

    controls.btnExponential.addEventListener('click', () => {
        state.activeModel = 'exponential';
        controls.btnExponential.classList.add('active-button');
        controls.btnArrhenius.classList.remove('active-button');
        updateVisualization();
    });

    Object.keys(controls).forEach(key => {
        if (key.startsWith('btn')) return;
        const slider = controls[key];
        const display = values[key];
        slider.addEventListener('input', () => {
            let val = slider.value;
            // enforce Tmax > Tmin
            if (key === 'tMin') {
                const tMinValue = parseInt(val);
                controls.tMax.min = tMinValue + 1;
                if (parseInt(controls.tMax.value) <= tMinValue) {
                    controls.tMax.value = tMinValue + 1;
                    values.tMax.textContent = controls.tMax.value;
                }
            }
            display.textContent = val;
            updateVisualization();
        });
    });

    // Initial setup
    controls.btnArrhenius.classList.add('active-button');
    updateVisualization();
});
</script>

</body>
</html>
