    <script>
        document.addEventListener('DOMContentLoaded', function() {
            // --- DOM Elements ---
            const sliderLambda = document.getElementById('slider-lambda');
            const valueLambda = document.getElementById('value-lambda');
            const sliderOmega = document.getElementById('slider-omega');
            const valueOmega = document.getElementById('value-omega');
            const btnPressureDrop = document.getElementById('btn-pressure-drop');
            const btnDifference = document.getElementById('btn-difference');
            const plotContainer = document.getElementById('plot-container');

            // --- State ---
            let currentPlotType = 'pressure-drop';

            // --- Math Functions ---

            // Memoized factorial
            const factorial = (() => {
                const cache = [1];
                return function(n) {
                    if (n < 0) return NaN;
                    if (n >= cache.length) {
                        for (let i = cache.length; i <= n; i++) {
                            cache[i] = cache[i - 1] * i;
                        }
                    }
                    return cache[n];
                };
            })();

            // Bessel function K0 approximation (from GSL, based on Abramowitz and Stegun)
            function besselK0(x) {
                if (x <= 0) return Infinity;
                let y, ans;
                if (x <= 2.0) {
                    y = x * 0.5;
                    const i0 = 1.0 + y * y * (3.5156229 + y * y * (3.0899424 + y * y * (1.2067492 + y * y * (0.2659732 + y * y * (0.0360768 + y * y * 0.0045813)))));
                    ans = -Math.log(y) * i0 + (-0.57721566 + y * y * (0.42278420 + y * y * (0.23069756 + y * y * (0.03488590 + y * y * (0.00262698 + y * y * (0.00010750 + y * y * 0.00000740))))));
                } else {
                    y = 2.0 / x;
                    ans = (Math.exp(-x) / Math.sqrt(x)) * (1.25331414 + y * (-0.07832358 + y * (0.02189568 + y * (-0.01062446 + y * (0.00587872 + y * (-0.00251540 + y * 0.00053208))))));
                }
                return ans;
            }

            // Bessel function K1 approximation (from GSL, based on Abramowitz and Stegun)
            function besselK1(x) {
                if (x <= 0) return Infinity;
                let y, ans;
                if (x <= 2.0) {
                    y = x * 0.5;
                    const i1 = y * (0.5 + y * y * (0.87890594 + y * y * (0.51498869 + y * y * (0.15084934 + y * y * (0.02658733 + y * y * (0.00301532 + y * y * 0.00032411))))));
                    ans = Math.log(y) * i1 + (1.0 / x) * (1.0 + y * y * (0.15443144 + y * y * (-0.67278579 + y * y * (-0.18156897 + y * y * (-0.01919402 + y * y * (-0.00110404 + y * y * (-0.00004686)))))));
                } else {
                    y = 2.0 / x;
                    ans = (Math.exp(-x) / Math.sqrt(x)) * (1.25331414 + y * (0.23498619 + y * (-0.03655620 + y * (0.01504268 + y * (-0.00780353 + y * (0.00325614 + y * (-0.00068245)))))));
                }
                return ans;
            }

            // Stehfest algorithm for numerical inverse Laplace transform
            const N_STEHFEST = 12;
            const V_STEHFEST = new Array(N_STEHFEST + 1);

            function precomputeStehfestCoefficients() {
                const N2 = N_STEHFEST / 2;
                for (let i = 1; i <= N_STEHFEST; i++) {
                    let term = 0;
                    for (let k = Math.floor((i + 1) / 2); k <= Math.min(i, N2); k++) {
                        term += (Math.pow(k, N2) * factorial(2 * k)) / (factorial(N2 - k) * factorial(k) * factorial(k - 1) * factorial(i - k) * factorial(2 * k - i));
                    }
                    V_STEHFEST[i] = Math.pow(-1, i + N2) * term;
                }
            }

            function stehfest(laplaceFunc, t) {
                const ln2_t = Math.log(2) / t;
                let sum = 0;
                for (let i = 1; i <= N_STEHFEST; i++) {
                    sum += V_STEHFEST[i] * laplaceFunc(i * ln2_t);
                }
                return ln2_t * sum;
            }

            // --- Calculation Logic ---
            function calculateData(lambda, omega, plotType) {
                const tau = Array.from({ length: 200 }, (_, i) => Math.pow(10, i * 10 / 199));
                const deltaP = [];

                // The function in Laplace space, F(s) = P_bar(s) / s
                const laplaceDomainFunction = (s) => {
                    if (s === 0) return 0;
                    const f_s = (omega * (1 - omega) * s + lambda) / ((1 - omega) * s + lambda);
                    const sqrt_s_fs = Math.sqrt(s * f_s);
                    if (sqrt_s_fs === 0) return 0; // Avoid division by zero

                    const p_bar_s = besselK0(sqrt_s_fs) / (sqrt_s_fs * besselK1(sqrt_s_fs));
                    return p_bar_s / s;
                };

                for (let i = 0; i < tau.length; i++) {
                    const t = tau[i];
                    const pressure = stehfest(laplaceDomainFunction, t);
                    if (plotType === 'pressure-drop') {
                        deltaP.push(pressure);
                    } else { // 'difference'
                        const difference = pressure - 0.5 * (Math.log(t) + 0.8091);
                        deltaP.push(difference);
                    }
                }
                return { tau, deltaP };
            }

            // --- Plotting ---
            function updatePlot(isFullRedraw = false) {
                const lambdaExp = parseFloat(sliderLambda.value);
                const omegaExp = parseFloat(sliderOmega.value);
                const lambda = Math.pow(10, lambdaExp);
                const omega = Math.pow(10, omegaExp);

                // Update value displays
                valueLambda.textContent = lambda < 1e-4 ? lambda.toExponential(5) : lambda.toFixed(6);
                valueOmega.textContent = omega.toFixed(6);

                const { tau, deltaP } = calculateData(lambda, omega, currentPlotType);

                const trace = {
                    x: tau,
                    y: deltaP,
                    type: 'scatter',
                    mode: 'lines',
                    line: {
                        color: 'red',
                        width: 2.5
                    }
                };

                let layout;
                if (currentPlotType === 'pressure-drop') {
                    layout = {
                        title: 'pressure drawdown at the wellbore',
                        xaxis: { title: 'dimensionless time, τ', type: 'log', range: [0, 10], dtick: 2, gridcolor: '#ddd' },
                        yaxis: { title: 'dimensionless pressure drop, ΔP', range: [0, 12], gridcolor: '#ddd' },
                        margin: { l: 80, r: 50, b: 60, t: 60 },
                        paper_bgcolor: '#fff',
                        plot_bgcolor: '#fff',
                    };
                } else { // 'difference'
                    layout = {
                        title: 'deviation from the asymptote',
                        xaxis: { title: 'dimensionless time, τ', type: 'log', range: [0, 10], dtick: 2, gridcolor: '#ddd' },
                        yaxis: { title: 'dimensionless difference, ΔP − 1/2 (ln(τ) + 0.8091)', range: [0, 3.5], gridcolor: '#ddd' },
                        margin: { l: 80, r: 50, b: 60, t: 60 },
                        paper_bgcolor: '#fff',
                        plot_bgcolor: '#fff',
                    };
                }

                if (isFullRedraw) {
                    Plotly.newPlot(plotContainer, [trace], layout);
                } else {
                    Plotly.react(plotContainer, [trace], layout);
                }
            }

            // --- Event Listeners ---
            function handleSliderChange() {
                updatePlot(false);
            }

            sliderLambda.addEventListener('input', handleSliderChange);
            sliderOmega.addEventListener('input', handleSliderChange);

            function handleButtonClick(event) {
                const clickedButton = event.target;
                if (clickedButton.classList.contains('active')) {
                    return; // Do nothing if already active
                }

                currentPlotType = clickedButton.id === 'btn-pressure-drop' ? 'pressure-drop' : 'difference';

                btnPressureDrop.classList.remove('active');
                btnDifference.classList.remove('active');
                clickedButton.classList.add('active');

                updatePlot(true); // Full redraw needed for layout changes
            }

            btnPressureDrop.addEventListener('click', handleButtonClick);
            btnDifference.addEventListener('click', handleButtonClick);

            // --- Initialisation ---
            precomputeStehfestCoefficients();
            updatePlot(true);

        });
    </script>

</body>
</html>
