    <script>
        // --- DATA & STATE ---
        let dataPoints = [
            { "age": 30, "volume": 289 },
            { "age": 35, "volume": 445 },
            { "age": 40, "volume": 565 },
            { "age": 45, "volume": 704 },
            { "age": 50, "volume": 833 },
            { "age": 55, "volume": 924 }
        ];

        let u = 0, p = 0; // Model parameters
        let draggedPointIndex = -1; // Index of the point being dragged

        // --- P5.JS SKETCH ---
        const sketch = (s) => {
            const canvasWidth = 500;
            const canvasHeight = 500;
            const padding = 60;

            // Data coordinate range
            const minAge = 15, maxAge = 85;
            const minVolume = 0, maxVolume = 1500;
            const pointRadius = 10;

            s.setup = () => {
                const canvas = s.createCanvas(canvasWidth, canvasHeight);
                canvas.parent('canvas-container');
                s.pixelDensity(1); // For consistent pixel mapping
                recalculateAndRedraw();
            };

            s.draw = () => {
                s.background('#FFFFE0');
                drawGridAndAxes();
                drawFittedCurve();
                drawDataPoints();
            };

            // --- DRAWING HELPERS ---
            const drawGridAndAxes = () => {
                s.push();
                s.strokeWeight(1);
                s.textSize(10);
                s.textAlign(s.CENTER, s.CENTER);

                // Grid lines - Y
                s.stroke(220);
                for (let v = 200; v < maxVolume; v += 200) {
                    const y = s.map(v, minVolume, maxVolume, canvasHeight - padding, padding);
                    drawDashedLine(padding, y, canvasWidth - padding, y, 5);
                }

                // Grid lines - X
                for (let a = 20; a < maxAge; a += 10) {
                    const x = s.map(a, minAge, maxAge, padding, canvasWidth - padding);
                    drawDashedLine(x, padding, x, canvasHeight - padding, 5);
                }

                s.stroke(0);
                // Y-axis
                s.line(padding, padding, padding, canvasHeight - padding);
                for (let v = 200; v < maxVolume; v += 200) {
                    const y = s.map(v, minVolume, maxVolume, canvasHeight - padding, padding);
                    s.line(padding - 5, y, padding, y);
                    s.noStroke();
                    s.fill(0);
                    s.text(v, padding - 25, y);
                    s.stroke(0);
                }

                // X-axis
                s.line(padding, canvasHeight - padding, canvasWidth - padding, canvasHeight - padding);
                for (let a = 20; a < maxAge; a += 10) {
                    const x = s.map(a, minAge, maxAge, padding, canvasWidth - padding);
                    s.line(x, canvasHeight - padding, x, canvasHeight - padding + 5);
                    s.noStroke();
                    s.fill(0);
                    s.text(a, x, canvasHeight - padding + 15);
                    s.stroke(0);
                }

                // Labels
                s.noStroke();
                s.fill(0);
                s.textSize(14);
                s.text("Forest Growth Curve", canvasWidth / 2, padding / 2);

                s.textSize(12);
                s.text("Stand age, yr", canvasWidth / 2, canvasHeight - padding / 3);

                s.push();
                s.translate(padding / 2.5, canvasHeight / 2);
                s.rotate(-s.HALF_PI);
                s.textAlign(s.CENTER);
                s.text("Wood volume, m³ ha⁻¹", 0, 0);
                s.pop();

                s.push();
                s.translate(canvasWidth - padding + 20, canvasHeight / 2);
                s.rotate(s.HALF_PI);
                s.textAlign(s.CENTER);
                s.text("4/5 law", 0, 0);
                s.pop();

                s.pop();
            };

            const drawDashedLine = (x1, y1, x2, y2, dashLength) => {
                s.drawingContext.setLineDash([dashLength, dashLength]);
                s.line(x1, y1, x2, y2);
                s.drawingContext.setLineDash([]);
            };

            const drawFittedCurve = () => {
                if (dataPoints.length < 2 || u <= 0 || p <= 0) return;

                s.push();
                s.noFill();
                s.stroke('#0000CD'); // Blue color for curve
                s.strokeWeight(2);
                s.beginShape();
                for (let age = u + 0.1; age < maxAge; age += 0.5) {
                    if (age >= minAge) {
                        const volume = p * Math.pow(age - u, 4 / 5);
                        if (volume < maxVolume) {
                            const x = s.map(age, minAge, maxAge, padding, canvasWidth - padding);
                            const y = s.map(volume, minVolume, maxVolume, canvasHeight - padding, padding);
                            s.vertex(x, y);
                        }
                    }
                }
                s.endShape();
                s.pop();
            };

            const drawDataPoints = () => {
                s.push();
                s.stroke(0);
                s.strokeWeight(1);
                s.fill('#0000CD'); // Blue fill for points
                dataPoints.forEach(pt => {
                    const x = s.map(pt.age, minAge, maxAge, padding, canvasWidth - padding);
                    const y = s.map(pt.volume, minVolume, maxVolume, canvasHeight - padding, padding);
                    s.ellipse(x, y, pointRadius, pointRadius);
                });
                s.pop();
            };

            // --- INTERACTION ---
            s.mousePressed = () => {
                if (s.mouseX < padding || s.mouseX > canvasWidth - padding || s.mouseY < padding || s.mouseY > canvasHeight - padding) {
                    return; // Ignore clicks outside plot area
                }

                draggedPointIndex = getPointAt(s.mouseX, s.mouseY);
            };

            s.mouseDragged = () => {
                if (draggedPointIndex !== -1) {
                    let newAge = s.map(s.mouseX, padding, canvasWidth - padding, minAge, maxAge);
                    let newVolume = s.map(s.mouseY, padding, canvasHeight - padding, maxVolume, minVolume);

                    // Clamp values to be within the plot boundaries
                    newAge = s.constrain(newAge, minAge, maxAge);
                    newVolume = s.constrain(newVolume, minVolume, maxVolume);

                    dataPoints[draggedPointIndex].age = newAge;
                    dataPoints[draggedPointIndex].volume = newVolume;

                    recalculateAndRedraw();
                }
            };

            s.mouseReleased = () => {
                if (draggedPointIndex === -1 &&
                    s.mouseX > padding && s.mouseX < canvasWidth - padding &&
                    s.mouseY > padding && s.mouseY < canvasHeight - padding) {
                    // Add new point
                    let newAge = s.map(s.mouseX, padding, canvasWidth - padding, minAge, maxAge);
                    let newVolume = s.map(s.mouseY, padding, canvasHeight - padding, maxVolume, minVolume);

                    dataPoints.push({ age: newAge, volume: newVolume });
                    recalculateAndRedraw();
                }
                draggedPointIndex = -1;
            };

            s.doubleClicked = () => {
                if (s.mouseX < padding || s.mouseX > canvasWidth - padding || s.mouseY < padding || s.mouseY > canvasHeight - padding) {
                    return; // Ignore clicks outside plot area
                }

                const pointIndex = getPointAt(s.mouseX, s.mouseY);
                if (pointIndex !== -1) {
                    dataPoints.splice(pointIndex, 1);
                    recalculateAndRedraw();
                }
            };

            const getPointAt = (mx, my) => {
                for (let i = dataPoints.length - 1; i >= 0; i--) {
                    const pt = dataPoints[i];
                    const px = s.map(pt.age, minAge, maxAge, padding, canvasWidth - padding);
                    const py = s.map(pt.volume, minVolume, maxVolume, canvasHeight - padding, padding);
                    const d = s.dist(mx, my, px, py);
                    if (d < pointRadius) {
                        return i;
                    }
                }
                return -1;
            };
        };

        // --- CORE LOGIC & UI UPDATES ---
        function recalculateAndRedraw() {
            fitModel();
            updateUI();
            if (p5Instance) {
                p5Instance.redraw();
            }
        }

        function fitModel() {
            if (dataPoints.length < 2) {
                p = 0; u = 0;
                return;
            }

            // Initialize parameters
            let currentP = 50;
            let currentU = 1;

            const learningRateP = 1e-9;
            const learningRateU = 1e-6;
            const iterations = 2000;
            const epsilon = 0.1;

            for (let i = 0; i < iterations; i++) {
                let gradP = 0;
                let gradU = 0;
                let minAgeInData = Infinity;

                for (const pt of dataPoints) {
                    if (pt.age < minAgeInData) minAgeInData = pt.age;
                }
                // Constraint: u must be less than the minimum age
                currentU = Math.min(currentU, minAgeInData - epsilon);

                for (const pt of dataPoints) {
                    const { age, volume } = pt;
                    const term_age_u = age - currentU;

                    if (term_age_u <= 0) continue; // Skip points that don't fit the model's domain

                    const term_pow = Math.pow(term_age_u, 4/5);
                    const error = currentP * term_pow - volume;

                    gradP += error * term_pow;

                    const term_pow_neg = Math.pow(term_age_u, -1/5);
                    gradU += error * currentP * (-4/5) * term_pow_neg;
                }

                currentP -= learningRateP * gradP;
                currentU -= learningRateU * gradU;
            }

            p = currentP;
            u = currentU;
        }


        function updateUI() {
            const resultsU = document.getElementById('results-u');
            const resultsP = document.getElementById('results-p');
            const tableBody = document.getElementById('data-table-body');

            resultsU.textContent = u.toFixed(4);
            resultsP.textContent = p.toFixed(4);

            tableBody.innerHTML = ''; // Clear existing rows

            // Sort points by age for table display
            const sortedPoints = [...dataPoints].sort((a, b) => a.age - b.age);

            sortedPoints.forEach(pt => {
                const row = document.createElement('tr');
                const ageCell = document.createElement('td');
                const volumeCell = document.createElement('td');

                ageCell.textContent = Math.round(pt.age);
                volumeCell.textContent = Math.round(pt.volume);

                row.appendChild(ageCell);
                row.appendChild(volumeCell);
                tableBody.appendChild(row);
            });
        }

        let p5Instance = new p5(sketch);

    </script>
