                    const p1 = (numSteps > 1) ? visPoints[numSteps - 2] : visPoints[0];
                    const p2 = visPoints[numSteps - 1];
                    p.triangle(sourcePoint.x, sourcePoint.y, p1.x, p1.y, p2.x, p2.y);
                } else {
                    // Draw final, full visibility polygon
                    p.fill('#FFCC66'); // orange-yellow
                    p.stroke('rgba(170, 170, 170, 0.5)');
                    p.beginShape();
                    p.vertex(sourcePoint.x, sourcePoint.y);
                    for (const pt of visPoints) {
                        p.vertex(pt.x, pt.y);
                    }
                    p.endShape(p.CLOSE);
                }
            }

            // Draw static polygons
            drawPolygons();

            // Draw helper graphics if not complete
            if (numSteps > 0 && numSteps < parseInt(angleSlider.elt.max)) {
                const currentVertex = sortedVertices[numSteps - 1];
                const activeEdge = visibilityResult.intersectedEdges[numSteps - 1];

                // Highlight active edge
                if (activeEdge) {
                    p.stroke('#00CC00');
                    p.strokeWeight(3);
                    p.line(activeEdge.a.x, activeEdge.a.y, activeEdge.b.x, activeEdge.b.y);
                }

                // Draw ray to current vertex
                p.stroke('#0000FF');
                p.strokeWeight(1);
                p.line(sourcePoint.x, sourcePoint.y, currentVertex.x, currentVertex.y);

                // Draw sweep arc
                const startAngle = p.atan2(sortedVertices[0].y - sourcePoint.y, sortedVertices[0].x - sourcePoint.x);
                const endAngle = p.atan2(currentVertex.y - sourcePoint.y, currentVertex.x - sourcePoint.x);
                p.noFill();
                p.stroke('#0000FF');
                p.strokeWeight(1.5);
                p.arc(sourcePoint.x, sourcePoint.y, 30, 30, startAngle, endAngle);
            }

            // Draw polygon vertices
            p.noStroke();
            p.fill('#FF0000');
            for (const v of allVertices) {
                p.ellipse(v.x, v.y, 4, 4);
            }

            // Draw source point
            p.stroke('#0000FF');
            p.strokeWeight(1.5);
            p.fill('#0000FF');
            p.ellipse(sourcePoint.x, sourcePoint.y, 8, 8);
        };

        // --- Helper Functions ---

        function resetDemo() {
            sourcePoint.set(initialSourcePos.x, initialSourcePos.y);
            angleSlider.value(initialSliderVal);
            angleValue.html(initialSliderVal);
            isDragging = false;
        }

        function definePolygons() {
            polygons = [
                // Outer Polygon
                { 
                    vertices: [[50, 50], [550, 50], [550, 550], [50, 550]],
                    isHole: false 
                },
                // Hole 1
                { 
                    vertices: [[100, 100], [250, 100], [250, 150], [200, 150], [200, 200], [100, 200]],
                    isHole: true 
                },
                // Hole 2
                { 
                    vertices: [[300, 100], [450, 100], [450, 250], [400, 250], [400, 200], [300, 200]],
                    isHole: true 
                },
                // Hole 3
                { 
                    vertices: [[100, 350], [250, 350], [250, 500], [200, 500], [200, 450], [100, 450]],
                    isHole: true 
                },
                // Hole 4
                { 
                    vertices: [[300, 400], [450, 400], [450, 500], [300, 500]],
                    isHole: true 
                },
                // Hole 5
                { 
                    vertices: [[300, 300], [350, 300], [350, 350], [300, 350]],
                    isHole: true 
                },
            ];
        }

        function collectGeometry() {
            allVertices = [];
            allEdges = [];
            for (const poly of polygons) {
                for (let i = 0; i < poly.vertices.length; i++) {
                    const p1 = p.createVector(poly.vertices[i][0], poly.vertices[i][1]);
                    const p2 = p.createVector(poly.vertices[(i + 1) % poly.vertices.length][0], poly.vertices[(i + 1) % poly.vertices.length][1]);
                    allVertices.push(p1);
                    allEdges.push({ a: p1, b: p2 });
                }
            }
        }

        function drawPolygons() {
            p.strokeWeight(1);
            p.stroke('#AAAAAA');
            p.fill(255);
            p.beginShape();
            const outer = polygons[0].vertices;
            for (const v of outer) p.vertex(v[0], v[1]);

            for (let i = 1; i < polygons.length; i++) {
                p.beginContour();
                for (const v of polygons[i].vertices) p.vertex(v[0], v[1]);
                p.endContour();
            }
            p.endShape(p.CLOSE);
        }

        function getSortedVertices(center) {
            return [...allVertices].sort((a, b) => {
                const angleA = p.atan2(a.y - center.y, a.x - center.x);
                const angleB = p.atan2(b.y - center.y, b.x - center.x);
                return angleA - angleB;
            });
        }

        function calculateVisibility(source, sortedVertices, numSteps) {
            const intersections = [];
            const intersectedEdges = [];

            for (let i = 0; i < numSteps; i++) {
                const vertex = sortedVertices[i];
                const rayDir = p5.Vector.sub(vertex, source);

                let closestIntersection = null;
                let closestEdge = null;
                let min_dist = Infinity;

                for (const edge of allEdges) {
                    const intersection = lineSegmentIntersection(source, p5.Vector.add(source, rayDir.setMag(p.width * 2)), edge.a, edge.b);
                    if (intersection) {
                        const dist = p5.Vector.dist(source, intersection);
                        if (dist < min_dist) {
                            min_dist = dist;
                            closestIntersection = intersection;
                            closestEdge = edge;
                        }
                    }
                }
                if (closestIntersection) {
                    intersections.push(closestIntersection);
                    intersectedEdges.push(closestEdge);
                }
            }
            return { intersections, intersectedEdges };
        }

        function isPointInFreeSpace(pt) {
            if (!pointInPolygon(pt, polygons[0].vertices)) return false;
            for (let i = 1; i < polygons.length; i++) {
                if (pointInPolygon(pt, polygons[i].vertices)) return false;
            }
            return true;
        }

        function pointInPolygon(point, vs) {
            let x = point.x, y = point.y;
            let inside = false;
            for (let i = 0, j = vs.length - 1; i < vs.length; j = i++) {
                let xi = vs[i][0], yi = vs[i][1];
                let xj = vs[j][0], yj = vs[j][1];

                let intersect = ((yi > y) !== (yj > y)) &&
                    (x < (xj - xi) * (y - yi) / (yj - yi) + xi);
                if (intersect) inside = !inside;
            }
            return inside;
        }

        function lineSegmentIntersection(p1, p2, p3, p4) {
            const d = (p2.x - p1.x) * (p4.y - p3.y) - (p2.y - p1.y) * (p4.x - p3.x);
            if (d === 0) return null; // Parallel lines

            const t = ((p1.x - p3.x) * (p4.y - p3.y) - (p1.y - p3.y) * (p4.x - p3.x)) / d;
            const u = -((p2.x - p1.x) * (p1.y - p3.y) - (p2.y - p1.y) * (p1.x - p3.x)) / d;

            if (t >= 0 && u >= 0 && u <= 1) {
                return p.createVector(p1.x + t * (p2.x - p1.x), p1.y + t * (p2.y - p1.y));
            }
            return null;
        }
    };

    new p5(sketch);
</script>

</body>
