<body>

    <div id="main-container">
        <div id="control-panel">
            <div>
                <label for="slider-order">expansion order</label>
                <input type="range" id="slider-order" min="1" max="5" step="1" value="5">
                <span id="value-order">5</span>
            </div>
            <div>
                <label for="slider-a">f(0)</label>
                <input type="range" id="slider-a" min="0.1" max="3.0" step="0.1" value="2.0">
                <span id="value-a">2.0</span>
            </div>
            <div>
                <label for="slider-b">f'(0)</label>
                <input type="range" id="slider-b" min="-2.0" max="2.0" step="0.1" value="0.0">
                <span id="value-b">0.0</span>
            </div>
        </div>
        <div id="visualization-area">
            <div id="plot-container"></div>
        </div>
    </div>

    <script>
        document.addEventListener('DOMContentLoaded', function() {
            // --- DOM Element References ---
            const sliderOrder = document.getElementById('slider-order');
            const valueOrder = document.getElementById('value-order');
            const sliderA = document.getElementById('slider-a');
            const valueA = document.getElementById('value-a');
            const sliderB = document.getElementById('slider-b');
            const valueB = document.getElementById('value-b');
            const plotContainer = document.getElementById('plot-container');

            const factorial = [1, 1, 2, 6, 24, 120];

            // --- Core Calculation Functions ---

            function calculateCoefficients(a, b) {
                const f_derivs = new Array(6);
                f_derivs[0] = a; // f(0)
                f_derivs[1] = b; // f'(0)

                // ODE: f''(x)f(x) + f'(x) + (3/2)f(x) = 0
                // Recursively find derivatives at x=0
                if (Math.abs(f_derivs[0]) < 1e-9) { // Avoid division by zero
                    return Array(6).fill(0);
                }

                // f''(0) = -(f'(0) + 1.5*f(0)) / f(0)
                f_derivs[2] = -(f_derivs[1] + 1.5 * f_derivs[0]) / f_derivs[0];

                // f'''(0) = -[f''(0)f'(0) + f''(0) + 1.5*f'(0)] / f(0)
                f_derivs[3] = -(f_derivs[2] * f_derivs[1] + f_derivs[2] + 1.5 * f_derivs[1]) / f_derivs[0];

                // f''''(0) = -[2*f'''(0)*f'(0) + (f''(0))^2 + f'''(0) + 1.5*f''(0)] / f(0)
                f_derivs[4] = -(2 * f_derivs[3] * f_derivs[1] + f_derivs[2] ** 2 + f_derivs[3] + 1.5 * f_derivs[2]) / f_derivs[0];

                // f'''''(0) = -[3*f''''(0)*f'(0) + 4*f'''(0)*f''(0) + f''''(0) + 1.5*f'''(0)] / f(0)
                f_derivs[5] = -(3 * f_derivs[4] * f_derivs[1] + 4 * f_derivs[3] * f_derivs[2] + f_derivs[4] + 1.5 * f_derivs[3]) / f_derivs[0];

                const coeffs = f_derivs.map((d, i) => d / factorial[i]);
                return coeffs;
            }

            function calculatePowerSeries(coeffs, order, x_values) {
                return x_values.map(x => {
                    let sum = 0;
                    for (let i = 0; i <= order; i++) {
                        if(coeffs[i] !== undefined && isFinite(coeffs[i])) {
                            sum += coeffs[i] * Math.pow(x, i);
                        }
                    }
                    return sum;
                });
            }

            function rk4(odes, y0, t0, tf, n_steps) {
                const h = (tf - t0) / n_steps;
                let t = t0;
                let y = [...y0];
                const t_out = [t0];
                const y_out = [[...y0]];

                for (let i = 0; i < n_steps; i++) {
                    const k1 = odes(t, y).map(v => h * v);
                    const k2 = odes(t + h / 2, y.map((v, j) => v + k1[j] / 2)).map(v => h * v);
                    const k3 = odes(t + h / 2, y.map((v, j) => v + k2[j] / 2)).map(v => h * v);
                    const k4 = odes(t + h, y.map((v, j) => v + k3[j])).map(v => h * v);

                    y = y.map((v, j) => v + (k1[j] + 2 * k2[j] + 2 * k3[j] + k4[j]) / 6);
                    t += h;

                    t_out.push(t);
                    y_out.push([...y]);
                }
                return { t: t_out, y: y_out };
            }

            function formatPolynomial(coeffs, order) {
                let result = 'f(x) = ';
                let firstTerm = true;
                const superscripts = ['\u2070', '\u00B9', '\u00B2', '\u00B3', '\u2074', '\u2075', '\u2076', '\u2077', '\u2078', '\u2079'];

                const formatNumber = (num) => num.toFixed(5).replace(/\.?0+$/, "") || "0";

                for (let k = 0; k <= order; k++) {
                    const c = coeffs[k];
                    if (Math.abs(c) < 1e-7 || !isFinite(c)) continue;

                    const sign = c > 0 ? ' + ' : ' - ';
                    if (!firstTerm) {
                        result += sign;
                    } else if (c < 0) {
                        result += '-';
                    }

                    const abs_c = Math.abs(c);
                    const c_str = formatNumber(abs_c);

                    if (k === 0) {
                        result += c_str;
                    } else {
                        if (Math.abs(abs_c - 1) > 1e-7) {
                            result += c_str;
                            result += ' '; // space between coefficient and x
                        }
                        result += 'x';
                        if (k > 1) {
                            result += String(k).split('').map(digit => superscripts[digit]).join('');
                        }
                    }
                    firstTerm = false;
                }
                return firstTerm ? 'f(x) = 0' : result;
            }


            // --- Main Update Function ---
            function updatePlot() {
                // 1. Read Inputs
                const order = parseInt(sliderOrder.value);
                const a = parseFloat(sliderA.value);
                const b = parseFloat(sliderB.value);

                // 2. Update Value Displays
                valueOrder.textContent = order;
                valueA.textContent = a.toFixed(1);
                valueB.textContent = b.toFixed(1);

                // 3. Calculate Power Series Coefficients
                const coeffs = calculateCoefficients(a, b);

                // 4. Generate Power Series Data (Red Curve)
                const numPoints = 200;
                const xMax = 1.5;
                const x_values = Array.from({length: numPoints + 1}, (_, i) => i * xMax / numPoints);
                const y_approx = calculatePowerSeries(coeffs, order, x_values);

                // 5. Generate Numerical Solution Data (Green Curve)
                const odes = (t, y) => {
                    const y1 = y[0]; // f(x)
                    const y2 = y[1]; // f'(x)
                    if (Math.abs(y1) < 1e-9) return [y2, 0]; // Avoid singularity
                    const dy2_dt = -(y2 + 1.5 * y1) / y1;
                    return [y2, dy2_dt];
                };
                const rk4_result = rk4(odes, [a, b], 0, xMax, numPoints);
                const y_numerical = rk4_result.y.map(p => p[0]);

                // 6. Determine Agreement Region (Blue Shaded Area)
                let x_limit = xMax;
                for (let i = 0; i < x_values.length; i++) {
                    const diff = Math.abs(y_approx[i] - y_numerical[i]);
                    if (diff >= 0.01) {
                        x_limit = x_values[i];
                        break;
                    }
                }

                // 7. Update Plot Title
                const plotTitle = formatPolynomial(coeffs, order);

                // 8. Redraw Plot
                const traceRed = {
                    x: x_values,
                    y: y_approx,
                    type: 'scatter',
                    mode: 'lines',
                    name: 'Power Series Approx.',
                    line: {
                        color: 'red',
                        width: 2
                    }
                };

                const traceGreen = {
                    x: rk4_result.t,
                    y: y_numerical,
                    type: 'scatter',
                    mode: 'lines',
                    name: 'Numerical Solution',
                    line: {
                        color: 'green',
                        width: 3
                    }
                };

                const layout = {
                    title: { text: plotTitle, font: { size: 16 } },
                    xaxis: { title: 'x', range: [0, 1.5], zeroline: false },
                    yaxis: { title: 'f(x)', range: [0, 4], zeroline: false },
                    margin: { l: 50, r: 20, t: 50, b: 40 },
                    legend: { x: 0.05, y: 0.95, bgcolor: 'rgba(255,255,255,0.6)' },
                    shapes: [{
                        type: 'rect',
                        xref: 'x',
                        yref: 'paper',
                        x0: 0,
                        y0: 0,
                        x1: x_limit,
                        y1: 1,
                        fillcolor: 'lightblue',
                        opacity: 0.4,
                        layer: 'below',
                        line: { width: 0 }
                    }]
                };

                Plotly.react(plotContainer, [traceRed, traceGreen], layout, {responsive: true});
            }

            // --- Event Listeners ---
            sliderOrder.addEventListener('input', updatePlot);
            sliderA.addEventListener('input', updatePlot);
            sliderB.addEventListener('input', updatePlot);

            // --- Initial Plot ---
            updatePlot();
        });
    </script>

