        const KEY_SPACING = 1.15;
        const ROW_SPACING = 1.15;
        const VIS_MAX_HEIGHT = 10;

        // --- DOM ELEMENTS ---
        const textSelect = document.getElementById('select-text');
        const btnPlot2D = document.getElementById('btn-plot-2d');
        const btnPlot3D = document.getElementById('btn-plot-3d');
