        const canvas = document.getElementById('visualization-canvas');
        const canvasContainer = document.getElementById('canvas-container');

        // --- KEYBOARD LAYOUTS ---
        function generateLayout(rows, offsets) {
            const layout = {};
            rows.forEach((row, rowIndex) => {
                const rowChars = row.split('');
                const rowWidth = (rowChars.length - 1) * KEY_SPACING;
                const startX = -rowWidth / 2 + (offsets[rowIndex] || 0) * KEY_SPACING;
                rowChars.forEach((char, charIndex) => {
                    layout[char] = {
                        x: startX + charIndex * KEY_SPACING,
                        z: rowIndex * ROW_SPACING,
                        row: rowIndex
                    };
                });
            });
            return layout;
        }

        const QWERTY_LAYOUT = generateLayout(
            ['QWERTYUIOP', 'ASDFGHJKL', 'ZXCVBNM'],
            [0, 0.4, 1.0]
        );

        const DVORAK_LAYOUT = generateLayout(
            ['PYFGCRL', 'AOEUIDHTNS', 'QJKXBMWVZ'],
            [0.8, 0, 0.5]
        );

        // --- UTILITY FUNCTIONS ---
        function calculateFrequencies(text) {
            const frequencies = {};
            for (let i = 65; i <= 90; i++) {
                frequencies[String.fromCharCode(i)] = 0;
            }
            for (const char of text.toUpperCase()) {
                if (frequencies.hasOwnProperty(char)) {
                    frequencies[char]++;
                }
            }
            return frequencies;
        }

        function createTextSprite(text, options = {}) {
            const { fontsize = 48, fontface = 'Arial', textColor = 'rgba(0, 0, 0, 1.0)' } = options;

            const canvas = document.createElement('canvas');
            const context = canvas.getContext('2d');
            context.font = `Bold ${fontsize}px ${fontface}`;

            const metrics = context.measureText(text);
            const textWidth = metrics.width;

            canvas.width = textWidth;
            canvas.height = fontsize;

            context.font = `Bold ${fontsize}px ${fontface}`;
            context.fillStyle = textColor;
            context.fillText(text, 0, fontsize * 0.85);

            const texture = new THREE.CanvasTexture(canvas);
            const spriteMaterial = new THREE.SpriteMaterial({ map: texture });
            const sprite = new THREE.Sprite(spriteMaterial);

            sprite.scale.set(textWidth / fontsize * 0.5, 0.5, 1.0);
            return sprite;
        }

        function get2DColor(t) {
            // Gradient: blue -> purple -> orange -> red
            const c1 = new THREE.Color(0x4a90e2); // Blue
            const c2 = new THREE.Color(0x9013fe); // Purple
            const c3 = new THREE.Color(0xf5a623); // Orange
            const c4 = new THREE.Color(0xd0021b); // Red

            if (t < 0.33) return c1.lerp(c2, t / 0.33);
            if (t < 0.66) return c2.lerp(c3, (t - 0.33) / 0.33);
            return c3.lerp(c4, (t - 0.66) / 0.34);
        }

        // --- SCENE SETUP & DRAWING ---
        function init() {
            // Renderer
            renderer = new THREE.WebGLRenderer({ canvas, antialias: true });
            renderer.setClearColor(0xffffff);
            renderer.setPixelRatio(window.devicePixelRatio);

            // Scene
            scene = new THREE.Scene();

            // Lighting
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
            scene.add(ambientLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
            directionalLight.position.set(5, 10, 7.5);
            scene.add(directionalLight);

            // Groups for easy clearing
            contentGroup = new THREE.Group();
            axesGroup = new THREE.Group();
            scene.add(contentGroup, axesGroup);

            // Initial render
            updateVisualization();

            // Event Listeners
            setupEventListeners();

            // Animation loop
            animate();
        }

        function updateVisualization() {
            // 1. Clear previous objects
            while (contentGroup.children.length > 0) {
                contentGroup.remove(contentGroup.children[0]);
            }
            while (axesGroup.children.length > 0) {
                axesGroup.remove(axesGroup.children[0]);
            }

            // 2. Get data
            const text = textSelect.value;
            const frequencies = calculateFrequencies(text);
            const maxFreq = Math.max(...Object.values(frequencies));
            const yAxisMax = Math.ceil(maxFreq / 50) * 50 || 50;

            // 3. Draw components
            if (state.plotView === '2d') {
                setup2DCamera();
                draw2DPlot(frequencies, yAxisMax);
                drawAxes(yAxisMax, { width: 28, depth: 4, is2D: true });
            } else {
                setup3DCamera();
                draw3DPlot(frequencies, yAxisMax);
                drawAxes(yAxisMax, { width: 14, depth: 6, is2D: false });
            }
        }

        function setup2DCamera() {
            const aspect = canvasContainer.clientWidth / canvasContainer.clientHeight;
            const viewHeight = 15;
            const viewWidth = viewHeight * aspect;
            camera = new THREE.OrthographicCamera(-viewWidth/2, viewWidth/2, viewHeight/2, -viewHeight/2, 1, 100);
            camera.position.set(0, 0, 20);
            camera.lookAt(scene.position);
        }

        function setup3DCamera() {
            camera = new THREE.PerspectiveCamera(50, canvasContainer.clientWidth / canvasContainer.clientHeight, 0.1, 1000);
            camera.position.set(0, 15, 16);
            camera.lookAt(0, 2, 0);
        }

        function drawAxes(yMax, dims) {
            const { width, depth, is2D } = dims;
            const material = new THREE.LineBasicMaterial({ color: 0xcccccc });

            // Grid lines on floor
            const gridGeometry = new THREE.BufferGeometry();
            const points = [];
            for (let i = -width / 2; i <= width / 2; i += 2) {
                points.push(i, 0, -depth / 2, i, 0, depth / 2);
            }
            for (let i = -depth / 2; i <= depth / 2; i += 2) {
                points.push(-width / 2, 0, i, width / 2, 0, i);
            }
            gridGeometry.setAttribute('position', new THREE.Float32BufferAttribute(points, 3));
            axesGroup.add(new THREE.LineSegments(gridGeometry, material));

            // Y-Axis Ticks & Labels
            const labelMaterial = new THREE.LineBasicMaterial({ color: 0xaaaaaa });
            for (let y = 0; y <= yMax; y += 50) {
                if(y === 0 && !is2D) continue; // Skip 0 label in 3d view for clarity
                const yPos = (y / yMax) * VIS_MAX_HEIGHT;

                const tickGeom = new THREE.BufferGeometry().setFromPoints([
                    new THREE.Vector3(-width/2 - 0.1, yPos, is2D ? 0 : depth/2),
                    new THREE.Vector3(-width/2 + 0.1, yPos, is2D ? 0 : depth/2)
                ]);
                axesGroup.add(new THREE.Line(tickGeom, labelMaterial));

                const label = createTextSprite(y.toString(), { fontsize: 24, textColor: '#555555' });
                label.position.set(-width/2 - 1, yPos, is2D ? 0 : depth/2);
                axesGroup.add(label);
            }

            // Frame
            const framePoints = [
                -width/2, 0, is2D ? 0 : depth/2,
                -width/2, VIS_MAX_HEIGHT * 1.1, is2D ? 0 : depth/2,
                width/2, 0, is2D ? 0 : depth/2,
                -width/2, 0, is2D ? 0 : depth/2
            ];
            if (!is2D) {
                 framePoints.push(
                    -width/2, 0, -depth/2,
                    -width/2, VIS_MAX_HEIGHT * 1.1, -depth/2,
                    -width/2, VIS_MAX_HEIGHT * 1.1, depth/2
                 )
            }
            const frameGeom = new THREE.BufferGeometry();
            frameGeom.setAttribute('position', new THREE.Float32BufferAttribute(framePoints, 3));
            axesGroup.add(new THREE.Line(frameGeom, new THREE.LineBasicMaterial({ color: 0x888888 })));
        }


        function draw2DPlot(frequencies, yAxisMax) {
            const alphabet = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";
            const barWidth = 0.8;
            const totalWidth = alphabet.length * (barWidth + 0.2);
            const startX = -totalWidth / 2;

            alphabet.split('').forEach((char, index) => {
                const freq = frequencies[char];
                const height = freq > 0 ? (freq / yAxisMax) * VIS_MAX_HEIGHT : 0.01;
                const normalizedFreq = freq / (yAxisMax * 0.8); // Scale to use full color range

                const geometry = new THREE.BoxGeometry(barWidth, height, barWidth);
                const color = get2DColor(Math.min(normalizedFreq, 1.0));
                const material = new THREE.MeshToonMaterial({ color });

                const bar = new THREE.Mesh(geometry, material);
                bar.position.set(startX + index * (barWidth + 0.2), height / 2, 0);
                contentGroup.add(bar);

                const label = createTextSprite(char, { fontsize: 32 });
                label.position.set(bar.position.x, height + 0.7, 0);
                contentGroup.add(label);
            });
        }

        function draw3DPlot(frequencies, yAxisMax) {
            const layout = state.keyboardLayout === 'qwerty' ? QWERTY_LAYOUT : DVORAK_LAYOUT;
            const rowColors = [0x8f9a39, 0x4a90e2, 0x9013fe]; // Olive, Blue, Purple

            Object.keys(layout).forEach(char => {
                const pos = layout[char];
                const freq = frequencies[char];
                const height = freq > 0 ? (freq / yAxisMax) * VIS_MAX_HEIGHT + 0.2 : 0.2;

                // Key base
                const geometry = new THREE.CylinderGeometry(0.5, 0.5, height, 32);
                const material = new THREE.MeshStandardMaterial({
                    color: rowColors[pos.row],
                    roughness: 0.6,
                    metalness: 0.2
                });
                const key = new THREE.Mesh(geometry, material);
                key.position.set(pos.x, height / 2, pos.z);
                contentGroup.add(key);

                // Key top label
                const labelGeometry = new THREE.CircleGeometry(0.45, 32);
                const labelCanvas = document.createElement('canvas');
                labelCanvas.width = 128;
                labelCanvas.height = 128;
                const ctx = labelCanvas.getContext('2d');
                ctx.fillStyle = '#FFFFFF';
                ctx.fillRect(0,0,128,128);
                ctx.fillStyle = '#000000';
                ctx.font = 'bold 70px Arial';
                ctx.textAlign = 'center';
                ctx.textBaseline = 'middle';
                ctx.fillText(char, 64, 64);

                const labelTexture = new THREE.CanvasTexture(labelCanvas);
                const labelMaterial = new THREE.MeshBasicMaterial({ map: labelTexture });
                const labelPlane = new THREE.Mesh(labelGeometry, labelMaterial);
                labelPlane.position.set(pos.x, height + 0.01, pos.z);
                labelPlane.rotation.x = -Math.PI / 2;
                contentGroup.add(labelPlane);
            });
            contentGroup.position.z = -2; // Center keyboard in view
        }

        // --- EVENT HANDLING ---
        function setupEventListeners() {
            textSelect.addEventListener('change', updateVisualization);

            btnPlot2D.addEventListener('click', () => {
                state.plotView = '2d';
                btnPlot2D.classList.add('active');
                btnPlot3D.classList.remove('active');
                updateVisualization();
            });

            btnPlot3D.addEventListener('click', () => {
                state.plotView = '3d';
                btnPlot3D.classList.add('active');
                btnPlot2D.classList.remove('active');
                updateVisualization();
            });

            btnKeyboardQwerty.addEventListener('click', () => {
                state.keyboardLayout = 'qwerty';
                btnKeyboardQwerty.classList.add('active');
                btnKeyboardDvorak.classList.remove('active');
                if (state.plotView === '3d') {
                    updateVisualization();
                }
            });

            btnKeyboardDvorak.addEventListener('click', () => {
                state.keyboardLayout = 'dvorak';
                btnKeyboardDvorak.classList.add('active');
                btnKeyboardQwerty.classList.remove('active');
                if (state.plotView === '3d') {
                    updateVisualization();
                }
            });

            window.addEventListener('resize', onWindowResize);
        }

        // --- ANIMATION & RESIZE ---
        function onWindowResize() {
            const width = canvasContainer.clientWidth;
            const height = canvasContainer.clientHeight;

            if (camera.isOrthographicCamera) {
                const aspect = width / height;
                const viewHeight = 15;
                const viewWidth = viewHeight * aspect;
                camera.left = -viewWidth / 2;
                camera.right = viewWidth / 2;
                camera.top = viewHeight / 2;
                camera.bottom = -viewHeight / 2;
            } else {
                camera.aspect = width / height;
            }

            camera.updateProjectionMatrix();
            renderer.setSize(width, height);
        }

        function animate() {
            requestAnimationFrame(animate);
            if (camera && scene) {
                renderer.render(scene, camera);
            }
        }

        // --- START ---
        init();
    </script>
</body>
</html>
