                <svg width="40" height="20" viewBox="0 0 40 20" xmlns="http://www.w3.org/2000/svg">
                    <path d="M0 10 C 5 0, 15 20, 20 10 S 25 0, 30 10 S 35 20, 40 10" stroke="black" fill="none" stroke-width="1.5"/>
                </svg>
                <svg width="40" height="20" viewBox="0 0 40 20" xmlns="http://www.w3.org/2000/svg">
                    <path d="M0 10 H 10 C 15 10, 15 0, 20 0 S 25 10, 30 10 H 40" stroke="black" fill="none" stroke-width="1.5"/>
                </svg>
            </div>
        </div>
        <div class="control-group">
            <label for="slider-s-amp">amplitude</label>
            <input type="range" id="slider-s-amp" min="0" max="0.4" step="0.001" value="0.1">
            <span id="label-s-amp">0.1</span>
        </div>
