<script>
    // --- p5.js Sketch ---

    // DOM element references
    let sliderTime, sliderTrail, sliderPoints;
    let sliderPAmp, sliderPWl;
    let sliderSAmp, sliderSWl;
    let labelTime, labelTrail, labelPoints;
    let labelPAmp, labelPWl;
    let labelSAmp, labelSWl;
    let btnPlayPause, btnReset;

    // Simulation state
    let particles = [];
    let isPlaying = false;

    const defaults = {
        time: 0,
        trail: 5,
        points: 50,
        p_amp: 0.2,
        p_wl: 3.0,
        s_amp: 0.1,
        s_wl: 1.0
    };

    // Logical coordinate system
    const LOGICAL_WIDTH = 5;
    const LOGICAL_Y_RANGE = 0.5; // from -0.5 to 0.5
    const MARGIN = { top: 30, right: 30, bottom: 30, left: 40 };

    function setup() {
        const canvasContainer = document.getElementById('canvas-container');
        const canvas = createCanvas(canvasContainer.offsetWidth, 350);
        canvas.parent('canvas-container');

        // --- Get DOM elements ---
        sliderTime = select('#slider-time');
        sliderTrail = select('#slider-trail');
        sliderPoints = select('#slider-points');
        sliderPAmp = select('#slider-p-amp');
        sliderPWl = select('#slider-p-wl');
        sliderSAmp = select('#slider-s-amp');
        sliderSWl = select('#slider-s-wl');

        labelTime = select('#label-time');
        labelTrail = select('#label-trail');
        labelPoints = select('#label-points');
        labelPAmp = select('#label-p-amp');
        labelPWl = select('#label-p-wl');
        labelSAmp = select('#label-s-amp');
        labelSWl = select('#label-s-wl');

        btnPlayPause = select('#btn-play-pause');
        btnReset = select('#btn-reset');

        // --- Set initial values from defaults ---
        setControlsToDefaults();

        // --- Initialize simulation ---
        initializeParticles();
        updateAllLabels();

        // --- Setup event listeners ---
        btnPlayPause.mousePressed(togglePlayPause);
        btnReset.mousePressed(resetSimulation);

        sliderTime.input(() => labelTime.html(nf(sliderTime.value(), 1, 2)));
        sliderTrail.input(() => labelTrail.html(sliderTrail.value()));
        sliderPoints.input(() => {
            labelPoints.html(sliderPoints.value());
            initializeParticles();
        });
        sliderPAmp.input(() => labelPAmp.html(nf(sliderPAmp.value(), 1, 3)));
        sliderPWl.input(() => labelPWl.html(nf(sliderPWl.value(), 1, 2)));
        sliderSAmp.input(() => labelSAmp.html(nf(sliderSAmp.value(), 1, 3)));
        sliderSWl.input(() => labelSWl.html(nf(sliderSWl.value(), 1, 2)));
    }

    function draw() {
        background(255);

        // --- Handle animation ---
        if (isPlaying) {
            let currentTime = parseFloat(sliderTime.value());
            currentTime += 0.01;
            if (currentTime > sliderTime.elt.max) {
                currentTime = 0;
            }
            sliderTime.value(currentTime);
            labelTime.html(nf(currentTime, 1, 2));
        }

        // --- Get current control values ---
        const time = sliderTime.value();
        const trailLength = sliderTrail.value();
        const p_amp = sliderPAmp.value();
        const p_wl = sliderPWl.value();
        const s_amp = sliderSAmp.value();
        const s_wl = sliderSWl.value();

        // --- Draw Axes and Grid ---
        drawAxes();

        // --- Set up coordinate system for drawing data ---
        push();
        translate(MARGIN.left, MARGIN.top);
        const plotWidth = width - MARGIN.left - MARGIN.right;
        const plotHeight = height - MARGIN.top - MARGIN.bottom;

        // --- Iterate over particles to calculate and draw ---
        for (const p of particles) {
            // Calculate wave displacement
            const dx = p_amp * sin(TWO_PI * (p.x0 / p_wl - time));
            const dy = s_amp * sin(TWO_PI * (p.x0 / s_wl - time));

            const x_final = p.x0 + dx;
            const y_final = dy;

            // Update history for trail
            p.history.push({ x: x_final, y: y_final });
            if (p.history.length > trailLength) {
                p.history.shift();
            }

            // Map logical coordinates to pixel coordinates
            const x_px = map(x_final, 0, LOGICAL_WIDTH, 0, plotWidth);
            const y_px = map(y_final, -LOGICAL_Y_RANGE, LOGICAL_Y_RANGE, plotHeight, 0);

            // Draw thin vertical line
            stroke(211, 211, 211); // lightsteelblue
            strokeWeight(1);
            line(x_px, 0, x_px, plotHeight);

            // Draw trail
            if (trailLength > 0) {
                for (let i = 0; i < p.history.length; i++) {
                    const hist_x_px = map(p.history[i].x, 0, LOGICAL_WIDTH, 0, plotWidth);
                    const hist_y_px = map(p.history[i].y, -LOGICAL_Y_RANGE, LOGICAL_Y_RANGE, plotHeight, 0);
                    const alpha = map(i, 0, p.history.length, 50, 150);
                    noStroke();
                    fill(106, 90, 205, alpha); // slateblue with alpha
                    ellipse(hist_x_px, hist_y_px, 3, 3);
                }
            }

            // Draw the particle
            noStroke();
            fill(106, 90, 205); // slateblue
            ellipse(x_px, y_px, 6, 6);
        }
        pop();
    }

    function drawAxes() {
        const plotWidth = width - MARGIN.left - MARGIN.right;
        const plotHeight = height - MARGIN.top - MARGIN.bottom;

        push();
        translate(MARGIN.left, MARGIN.top);

        // Draw main axes lines
        stroke(150);
        strokeWeight(1);
        line(0, plotHeight / 2, plotWidth, plotHeight / 2); // X-axis (at y=0)
        line(0, 0, 0, plotHeight); // Y-axis (at x=0)

        // Draw X-axis ticks and labels
        for (let i = 0; i <= LOGICAL_WIDTH; i++) {
            const x = map(i, 0, LOGICAL_WIDTH, 0, plotWidth);
            line(x, plotHeight / 2 - 4, x, plotHeight / 2 + 4);
            noStroke();
            fill(0);
            textAlign(CENTER, TOP);
            text(i, x, plotHeight / 2 + 8);
        }

        // Draw Y-axis ticks and labels
        const yTicks = [-0.4, -0.2, 0.0, 0.2, 0.4];
        for (const tick of yTicks) {
            const y = map(tick, -LOGICAL_Y_RANGE, LOGICAL_Y_RANGE, plotHeight, 0);
            stroke(150);
            strokeWeight(1);
            line(-4, y, 4, y);
            noStroke();
            fill(0);
            textAlign(RIGHT, CENTER);
            text(tick.toFixed(1), -8, y);
        }

        pop();
    }

    function initializeParticles() {
        particles = [];
        const numPoints = sliderPoints.value();
        for (let i = 0; i < numPoints; i++) {
            // Evenly space particles based on their index
            const x0 = map(i, 0, numPoints > 1 ? numPoints - 1 : 1, 0, LOGICAL_WIDTH);
            particles.push({
                x0: x0,
                history: []
            });
        }
    }

    function togglePlayPause() {
        isPlaying = !isPlaying;
        btnPlayPause.html(isPlaying ? 'Pause' : 'Play');
    }

    function resetSimulation() {
        isPlaying = false;
        btnPlayPause.html('Play');

        setControlsToDefaults();
        updateAllLabels();
        initializeParticles();
    }

    function setControlsToDefaults() {
        sliderTime.value(defaults.time);
        sliderTrail.value(defaults.trail);
        sliderPoints.value(defaults.points);
        sliderPAmp.value(defaults.p_amp);
        sliderPWl.value(defaults.p_wl);
        sliderSAmp.value(defaults.s_amp);
        sliderSWl.value(defaults.s_wl);
    }

    function updateAllLabels() {
        labelTime.html(nf(sliderTime.value(), 1, 2));
        labelTrail.html(sliderTrail.value());
        labelPoints.html(sliderPoints.value());
