<body>
    <div id="app-container">
        <div id="control-panel">
            <div class="control-group">
                <label for="slider-t">t</label>
                <input type="range" id="slider-t" min="0" max="4" step="0.01" value="0.1">
                <span id="t-value-display">0.10</span>
            </div>
            <div class="control-group">
                <label for="slider-k">k</label>
                <input type="range" id="slider-k" min="1" max="10" step="1" value="2">
                <span id="k-value-display">2</span>
            </div>
            <div class="checkbox-group">
                <input type="checkbox" id="checkbox-axes" checked>
                <label for="checkbox-axes">axes</label>
            </div>
        </div>
        <div id="visualization-area">
            <div id="formula-display"></div>
            <div id="canvas-container"></div>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        // --- DOM Elements & State ---
        let sliderT, sliderK, checkboxAxes;
        let tValueDisplay, kValueDisplay, formulaDisplay;
        let canvasContainer;

        // --- Constants ---
        const ZEROS_J0 = [2.4048, 5.5201, 8.6537, 11.7915, 14.9309, 18.0711, 21.2116, 24.3525, 27.4935, 30.6346];
        const AMPLITUDE = 0.8; // Controls the visual width of the vibration

        /**
         * Bessel function of the first kind, order 0.
         * Uses polynomial approximations from Abramowitz and Stegun.
         * @param {number} x The input value.
         * @returns {number} J0(x).
         */
        function besselJ0(x) {
            let ax = Math.abs(x);
            if (ax < 8.0) {
                let y = x * x;
                let ans1 = 57568490574.0 + y * (-13362590354.0 + y * (651619640.7 + y * (-11214424.18 + y * (77392.33017 + y * (-184.9052456)))));
                let ans2 = 57568490411.0 + y * (1029532985.0 + y * (9494680.718 + y * (59272.64853 + y * (267.8532712 + y * 1.0))));
                return ans1 / ans2;
            } else {
                let z = 8.0 / ax;
                let y = z * z;
                let xx = ax - 0.785398164;
                let ans1 = 1.0 + y * (-0.1098628627e-2 + y * (0.2734510407e-4 + y * (-0.2073370639e-5 + y * 0.2093887211e-6)));
                let ans2 = -0.1562499995e-1 + y * (0.1430488765e-3 + y * (-0.6911147651e-5 + y * (0.7621095161e-6 - y * 0.934945152e-7)));
                return Math.sqrt(0.636619772 / ax) * (Math.cos(xx) * ans1 - z * Math.sin(xx) * ans2);
            }
        }

        function setup() {
            canvasContainer = document.getElementById('canvas-container');
            const canvas = createCanvas(canvasContainer.offsetWidth, canvasContainer.offsetHeight);
            canvas.parent('canvas-container');

            // --- Initialize Controls ---
            sliderT = select('#slider-t');
            sliderK = select('#slider-k');
            checkboxAxes = select('#checkbox-axes');

            tValueDisplay = document.getElementById('t-value-display');
            kValueDisplay = document.getElementById('k-value-display');
            formulaDisplay = document.getElementById('formula-display');

            // --- Add Event Listeners ---
            sliderT.input(updateT);
            sliderK.input(updateK);
            checkboxAxes.changed(() => redraw());

            // --- Initial Setup ---
            updateDisplays();
            updateFormula();

            // Redraw only when controls change
            noLoop();
        }

        function draw() {
            const t = sliderT.value();
            const k = sliderK.value();
            const showAxes = checkboxAxes.checked();
            const z_k = ZEROS_J0[k - 1];
            const omega_k = z_k / 2.0;

            background('#f0f0f0');

            // --- Coordinate System Setup ---
            // We map a logical coordinate system to the canvas.
            // Logical X: -2 to 2
            // Logical Y: 0 to -1.2 (negative downwards)
            push(); // Isolate transformations

            const x_margin = 60;
            const y_top_margin = 40;
            const y_bottom_margin = 40;

            const plotWidth = width - 2 * x_margin;
            const plotHeight = height - y_top_margin - y_bottom_margin;

            // Move origin to the top-center of the plotting area
            translate(width / 2, y_top_margin);

            const scaleX = plotWidth / 4.0; // Maps [-2, 2] to plotWidth
            const scaleY = plotHeight / 1.2; // Maps [0, 1.2] to plotHeight

            if (showAxes) {
                drawAxes(scaleX, scaleY);
            }

            // --- Draw the Vibrating String ---
            const timeFactor = Math.cos(omega_k * t);

            stroke(65, 105, 225); // RoyalBlue
            strokeWeight(2.5);
            noFill();

            beginShape();
            const steps = 200;
            for (let i = 0; i <= steps; i++) {
                const y_norm = i / steps; // Normalized y from 0 to 1
                const bessel_arg = z_k * Math.sqrt(y_norm);

                const x_pos = AMPLITUDE * timeFactor * besselJ0(bessel_arg);
                const y_pos = -y_norm; // String hangs downwards

                // Map logical coordinates to scaled canvas coordinates
                vertex(x_pos * scaleX, y_pos * scaleY * 1.2); // Multiply by 1.2 because y_norm is [0,1] but axis is [0, 1.2]
            }
            endShape();

            pop(); // Restore original transformation state
        }

        function drawAxes(scaleX, scaleY) {
            push();

            // Style for axes lines
            stroke(180);
            strokeWeight(1);

            // Use native canvas dashing for performance
            drawingContext.setLineDash([4, 4]);

            // Vertical axis (y-axis)
            line(0, 0, 0, -1.1 * scaleY);
            // Horizontal axis (x-axis)
            line(-2 * scaleX, 0, 2 * scaleX, 0);

            // Style for ticks and labels
            drawingContext.setLineDash([]); // Solid lines
            noStroke();
            fill(100);
            textSize(12);
            textAlign(CENTER, CENTER);

            // X-axis ticks and labels
            for (let i = -2; i <= 2; i++) {
                if (i !== 0) {
                    text(i, i * scaleX, 15);
                    stroke(180);
                    line(i * scaleX, -5, i * scaleX, 5);
                    noStroke();
                }
            }

            // Y-axis ticks and labels
            textAlign(RIGHT, CENTER);
            for (let y = -0.2; y >= -1.0; y -= 0.2) {
                const y_val = y.toFixed(1);
                text(y_val, -10, y * scaleY * 1.2);
                stroke(180);
                line(-5, y * scaleY * 1.2, 5, y * scaleY * 1.2);
                noStroke();
            }
            pop();
        }

        function updateDisplays() {
            tValueDisplay.textContent = parseFloat(sliderT.value()).toFixed(2);
            kValueDisplay.textContent = sliderK.value();
        }

        function updateT() {
            tValueDisplay.textContent = parseFloat(sliderT.value()).toFixed(2);
            redraw();
        }

        function updateK() {
            kValueDisplay.textContent = sliderK.value();
            updateFormula();
            redraw();
        }

        function updateFormula() {
            const k = sliderK.value();
            const z_k = ZEROS_J0[k - 1];
            const omega_k = z_k / 2.0;

            const latexString = `\\(\\omega_{${k}} = ${omega_k.toFixed(2)} \\sqrt{g/l}\\)`;
            formulaDisplay.innerHTML = latexString;

            // Ask MathJax to typeset the new formula
            if (window.MathJax) {
                MathJax.typesetPromise([formulaDisplay]).catch((err) => console.log('MathJax error:', err));
            }
        }

        function windowResized() {
            resizeCanvas(canvasContainer.offsetWidth, canvasContainer.offsetHeight);
            redraw();
        }

    </script>
</body>
</html>
