    <script>
        document.addEventListener('DOMContentLoaded', () => {
            // --- STATE & DEFAULTS ---
            const DEFAULTS = {
                opacities: {
                    '1': 0.7,
                    '2': 0.7,
                    '3': 0.7,
                    '4': 0.7,
                },
                colors: {
                    '1': '#00ff00',
                    '2': '#0000ff',
                    '3': '#ff0000',
                    '4': '#000000',
                },
                layeringIndex: 0,
            };

            const layeringPermutations = [
                [1, 2, 3, 4],
                [4, 3, 2, 1],
                [1, 3, 2, 4],
                [2, 4, 1, 3],
                [3, 1, 4, 2],
            ];
            let layeringOrderIndex = DEFAULTS.layeringIndex;
            let p5Instance;

            // --- DOM ELEMENT REFERENCES ---
            const controls = {
                opacity1: document.getElementById('slider-opacity1'),
                opacity2: document.getElementById('slider-opacity2'),
                opacity3: document.getElementById('slider-opacity3'),
                opacity4: document.getElementById('slider-opacity4'),
                color1: document.getElementById('color-picker1'),
                color2: document.getElementById('color-picker2'),
                color3: document.getElementById('color-picker3'),
                color4: document.getElementById('color-picker4'),
                layeringDisplay: document.getElementById('layering-display'),
                btnLayerNext: document.getElementById('btn-layer-next'),
                btnLayerPrev: document.getElementById('btn-layer-prev'),
                btnReset: document.getElementById('btn-reset'),
            };

            // --- P5.JS SKETCH ---
            const sketch = (p) => {
                let circleData = {};

                p.setup = () => {
                    const container = document.getElementById('canvas-container');
                    const canvas = p.createCanvas(container.clientWidth, container.clientHeight);
                    canvas.parent(container);
                    p.noLoop();

                    p.windowResized = () => {
                      p.resizeCanvas(container.clientWidth, container.clientHeight);
                      p.redraw();
                    }
                };

                p.draw = () => {
                    p.background('#f0f0f0');
                    const w = p.width;
                    const h = p.height;

                    // Define circle properties relative to canvas size
                    circleData = {
                        1: { x: w * 0.35, y: h * 0.40, d: w * 0.4, label: '1' },
                        2: { x: w * 0.65, y: h * 0.40, d: w * 0.4, label: '2' },
                        3: { x: w * 0.50, y: h * 0.65, d: w * 0.4, label: '3' },
                        4: { x: w * 0.50, y: h * 0.45, d: w * 0.3, label: '4' },
                    };

                    const currentOrder = layeringPermutations[layeringOrderIndex];

                    // Draw circles based on layering order
                    p.noStroke();
                    for (const id of currentOrder) {
                        const circle = circleData[id];
                        const colorValue = document.getElementById(`color-picker${id}`).value;
                        const opacityValue = document.getElementById(`slider-opacity${id}`).value;

                        let c = p.color(colorValue);
                        p.fill(p.red(c), p.green(c), p.blue(c), opacityValue * 255);
                        p.ellipse(circle.x, circle.y, circle.d, circle.d);
                    }

                    // Draw labels on top
                    p.fill(0);
                    p.textAlign(p.CENTER, p.CENTER);
                    p.textSize(32);
                    p.textStyle(p.BOLD);

                    for (let i = 1; i <= 4; i++) {
                        const circle = circleData[i];
                        p.text(circle.label, circle.x, circle.y);
                    }
                };
            };

            // --- CONTROL LOGIC ---
            function updateLayeringDisplay() {
                controls.layeringDisplay.textContent = layeringPermutations[layeringOrderIndex].join(', ');
            }

            function resetAll() {
                // Reset opacity sliders
                controls.opacity1.value = DEFAULTS.opacities['1'];
                controls.opacity2.value = DEFAULTS.opacities['2'];
                controls.opacity3.value = DEFAULTS.opacities['3'];
                controls.opacity4.value = DEFAULTS.opacities['4'];

                // Reset color pickers
                controls.color1.value = DEFAULTS.colors['1'];
                controls.color2.value = DEFAULTS.colors['2'];
                controls.color3.value = DEFAULTS.colors['3'];
                controls.color4.value = DEFAULTS.colors['4'];

                // Reset layering
                layeringOrderIndex = DEFAULTS.layeringIndex;
                updateLayeringDisplay();

                // Redraw canvas
                if (p5Instance) {
                    p5Instance.redraw();
                }
            }

            // --- EVENT LISTENERS ---
            function setupEventListeners() {
                // Sliders and color pickers trigger redraw
                const redrawControls = Object.values(controls).slice(0, 8); // opacities and colors
                redrawControls.forEach(control => {
                    control.addEventListener('input', () => p5Instance.redraw());
                });

                // Layering buttons
                controls.btnLayerNext.addEventListener('click', () => {
                    layeringOrderIndex = (layeringOrderIndex + 1) % layeringPermutations.length;
                    updateLayeringDisplay();
                    p5Instance.redraw();
                });
                controls.btnLayerPrev.addEventListener('click', () => {
                    layeringOrderIndex = (layeringOrderIndex - 1 + layeringPermutations.length) % layeringPermutations.length;
                    updateLayeringDisplay();
                    p5Instance.redraw();
                });

                // Reset button
                controls.btnReset.addEventListener('click', resetAll);
            }

            // --- INITIALIZATION ---
            setupEventListeners();
            p5Instance = new p5(sketch, 'canvas-container');
            // Timeout to ensure p5 has initialized before the first reset/draw
            setTimeout(resetAll, 50); 
        });
    </script>
</body>
</html>
