            width: 100%;
        }

        #display-pressure {
            font-weight: bold;
            min-width: 2ch;
            text-align: right;
        }

        #plot-container {
            width: 100%;
            height: 500px;
        }
    </style>
</head>
<body>

<div id="app-container">
    <div id="control-panel">
        <div class="eos-buttons">
            <button id="btn-srk">SRK</button>
            <button id="btn-pr">PR</button>
        </div>
        <div class="aspen-control">
            <input type="checkbox" id="checkbox-aspen" checked>
            <label for="checkbox-aspen">comparison with Aspen Plus</label>
        </div>
        <div class="pressure-control">
            <label for="slider-pressure">pressure P</label>
            <input type="range" id="slider-pressure" min="1" max="20" step="1" value="10">
            <span id="display-pressure">10</span>
        </div>
    </div>
    <div id="plot-container"></div>
</div>

<script>
document.addEventListener('DOMContentLoaded', () => {
    // 1. Component and State setup
    const btnSrk = document.getElementById('btn-srk');
    const btnPr = document.getElementById('btn-pr');
    const checkboxAspen = document.getElementById('checkbox-aspen');
    const sliderPressure = document.getElementById('slider-pressure');
    const displayPressure = document.getElementById('display-pressure');
    const plotContainer = document.getElementById('plot-container');

    let activeEos = 'srk'; // 'srk' or 'pr'

    // 2. Constants for Propylene
    const Tc = 365.57; // K
    const Pc = 46.65; // bar
    const omega = 0.144; // Acentric Factor
    const R = 0.0831446; // L·bar/(mol·K)

    // Aspen Plus comparison data
    const aspenData = {
        x: [300, 310, 320, 330, 340, 350, 360, 370, 380, 390, 400],
        y: [0.868, 0.881, 0.891, 0.900, 0.909, 0.917, 0.924, 0.930, 0.937, 0.942, 0.946]
    };

    // 3. Calculation Logic

    /**
     * Solves the cubic equation x^3 + ax^2 + bx + c = 0
     * @returns {number[]} An array of real roots.
     */
    function solveCubic(a, b, c) {
        const p = (3 * b - a * a) / 3;
        const q = (2 * a * a * a - 9 * a * b + 27 * c) / 27;
        const roots = [];

        if (Math.abs(p) < 1e-8) { // Linear equation
            roots.push(Math.cbrt(-q) - a / 3);
        } else if (Math.abs(q) < 1e-8) { // All roots real
            roots.push(0 - a / 3, Math.sqrt(-p) - a / 3, -Math.sqrt(-p) - a / 3);
        } else {
            const D = (q / 2) * (q / 2) + (p / 3) * (p / 3) * (p / 3);
            if (Math.abs(D) < 1e-8) { // All roots real, at least two are equal
                const r = Math.cbrt(-q / 2);
                roots.push(2 * r - a / 3, -r - a / 3);
            } else if (D > 0) { // One real root
                const u = Math.cbrt(-q / 2 + Math.sqrt(D));
                roots.push(u - p / (3 * u) - a / 3);
            } else { // All three roots real
                const r = Math.sqrt(-(p * p * p) / 27);
                const phi = Math.atan2(Math.sqrt(-D), -q / 2) / 3;
                roots.push(
                    2 * Math.cbrt(r) * Math.cos(phi) - a / 3,
                    2 * Math.cbrt(r) * Math.cos(phi + 2 * Math.PI / 3) - a / 3,
                    2 * Math.cbrt(r) * Math.cos(phi + 4 * Math.PI / 3) - a / 3
                );
            }
        }
        return roots.filter(r => !isNaN(r));
    }


    /**
     * Calculates the fugacity coefficient for a given EoS, pressure, and temperature.
     */
    function calculateFugacity(eos, P, T) {
        const Tr = T / Tc;

        let a, b, m, alpha, A, B, Z, lnPhi;

        if (eos === 'srk') {
            a = 0.42748 * R * R * Tc * Tc / Pc;
            b = 0.08664 * R * Tc / Pc;
            m = 0.480 + 1.574 * omega - 0.176 * omega * omega;
            alpha = Math.pow(1 + m * (1 - Math.sqrt(Tr)), 2);

            const a_eff = a * alpha;
            A = a_eff * P / (R * R * T * T);
            B = b * P / (R * T);

            const roots = solveCubic(-1, A - B - B * B, -A * B);
            Z = Math.max(...roots);
            if (Z <= B) return NaN;

            lnPhi = Z - 1 - Math.log(Z - B) - (A / B) * Math.log(1 + B / Z);

        } else if (eos === 'pr') {
            a = 0.45724 * R * R * Tc * Tc / Pc;
            b = 0.07780 * R * Tc / Pc;
            m = 0.37464 + 1.54226 * omega - 0.26992 * omega * omega;
            alpha = Math.pow(1 + m * (1 - Math.sqrt(Tr)), 2);

            const a_eff = a * alpha;
            A = a_eff * P / (R * R * T * T);
            B = b * P / (R * T);

            const roots = solveCubic(-(1 - B), A - 3 * B * B - 2 * B, -(A * B - B * B - B * B * B));
            Z = Math.max(...roots);
            if (Z <= B) return NaN;

            lnPhi = Z - 1 - Math.log(Z - B) - (A / (2 * Math.sqrt(2) * B)) * Math.log((Z + (1 + Math.sqrt(2)) * B) / (Z + (1 - Math.sqrt(2)) * B));
        }

        return Math.exp(lnPhi);
    }

    /**
     * Generates the data for the EoS curve.
     */
    function generateEosData(eos, pressure) {
        const temperatures = [];
        const fugacityCoeffs = [];
        for (let T = 300; T <= 500; T += 2) {
            const phi = calculateFugacity(eos, pressure, T);
            if (!isNaN(phi)) {
                temperatures.push(T);
                fugacityCoeffs.push(phi);
            }
        }
        return { x: temperatures, y: fugacityCoeffs };
    }

    // 4. Plotting
    const plotLayout = {
        xaxis: {
            title: 'temperature (K)',
            range: [295, 505],
            gridcolor: '#ffffff',
            zerolinecolor: '#ffffff',
            showgrid: true,
            showline: true,
            minor: {
                showgrid: true,
                gridcolor: '#fafafa'
            }
        },
        yaxis: {
            title: 'gas-phase fugacity coefficient',
            range: [0.68, 1.02],
            gridcolor: '#ffffff',
            zerolinecolor: '#ffffff',
            showgrid: true,
            showline: true,
             minor: {
                showgrid: true,
                gridcolor: '#fafafa'
            }
        },
        margin: { l: 80, r: 20, b: 50, t: 20 },
        plot_bgcolor: '#eeeeee',
        paper_bgcolor: '#fff',
        showlegend: false
    };

    function drawPlot() {
        const pressure = parseFloat(sliderPressure.value);
        const showAspen = checkboxAspen.checked;

        const srkData = generateEosData('srk', pressure);
        const prData = generateEosData('pr', pressure);

        const srkTrace = {
            x: srkData.x,
            y: srkData.y,
            type: 'scatter',
            mode: 'lines',
            line: { color: 'red', width: 2 },
            visible: activeEos === 'srk'
        };

        const prTrace = {
            x: prData.x,
            y: prData.y,
            type: 'scatter',
            mode: 'lines',
            line: { color: 'blue', width: 2 },
            visible: activeEos === 'pr'
        };

        const aspenTrace = {
            x: aspenData.x,
            y: aspenData.y,
            type: 'scatter',
            mode: 'markers',
            marker: { color: 'green', symbol: 'square', size: 8 },
            visible: showAspen && pressure === 10
        };

        const data = [srkTrace, prTrace, aspenTrace];

        Plotly.react(plotContainer, data, plotLayout, {responsive: true});
    }

    // 5. Event Listeners
    function handleEosChange(newEos) {
        activeEos = newEos;
        if (newEos === 'srk') {
            btnSrk.classList.add('selected');
            btnPr.classList.remove('selected');
        } else {
            btnPr.classList.add('selected');
            btnSrk.classList.remove('selected');
        }
        drawPlot();
    }

    btnSrk.addEventListener('click', () => handleEosChange('srk'));
    btnPr.addEventListener('click', () => handleEosChange('pr'));

    sliderPressure.addEventListener('input', () => {
        displayPressure.textContent = sliderPressure.value;
        drawPlot();
    });

    checkboxAspen.addEventListener('change', drawPlot);

    // 6. Initial Render
    function initialize() {
        btnSrk.classList.add('selected');
        sliderPressure.value = 10;
        displayPressure.textContent = '10';
        checkboxAspen.checked = true;
        drawPlot();
    }

    initialize();
});
</script>

</body>
</html>
