            <div id="reaction-slider-container" class="control-group">
                <label for="slider-reaction">reaction evolution</label>
                <button id="btn-reaction-minus">-</button>
                <input type="range" id="slider-reaction" min="0" max="100" step="1" value="0">
                <button id="btn-reaction-plus">+</button>
            </div>
            <div id="erg-ewg-slider-container" class="control-group">
                <label for="slider-erg-ewg">ERG/EWG effect intensity</label>
                <button id="btn-erg-ewg-minus">-</button>
                <input type="range" id="slider-erg-ewg" min="0" max="100" step="1" value="0">
                <button id="btn-erg-ewg-plus">+</button>
            </div>
        </div>
        <div id="canvas-container">
            <canvas id="viz-canvas"></canvas>
        </div>
    </div>

    <script>
        const sketch = (p) => {
            // --- STATE MANAGEMENT ---
            const state = {
                activeMode: 'da',
                inverse: false,
                focus: false,
                reaction: 0,
                ergEwg: 0,
            };

            // --- DOM ELEMENTS ---
            let modeButtons, checkInverse, checkFocus, sliderReaction, sliderErgEwg;
            let btnReactionMinus, btnReactionPlus, btnErgEwgMinus, btnErgEwgPlus;
            let controlGroups;

            const lerp = (a, b, t) => a * (1 - t) + b * t;
            const lerpPoint = (p1, p2, t) => ({ x: lerp(p1.x, p2.x, t), y: lerp(p1.y, p2.y, t) });

            // --- P5.JS SETUP & DRAW ---
            p.setup = function() {
                const canvasContainer = document.getElementById('canvas-container');
                const canvas = p.createCanvas(canvasContainer.clientWidth, canvasContainer.clientHeight);
                canvas.parent('viz-canvas');
                p.pixelDensity(2);
                p.noLoop();

                setupDOMListeners();
                updateControlVisibility();
            };

            p.windowResized = function() {
                const canvasContainer = document.getElementById('canvas-container');
                p.resizeCanvas(canvasContainer.clientWidth, canvasContainer.clientHeight);
                p.redraw();
            }

            p.draw = function() {
                p.background(255);
                p.translate(p.width / 2, p.height / 2);
                p.stroke(0);
                p.strokeWeight(2);

                switch (state.activeMode) {
                    case 'da':
                        drawDielsAlder();
                        break;
                    case 'regio':
                        drawRegioselectivity();
                        break;
                    case 'stereo-phile':
                        drawStereoPhile();
                        break;
                    case 'stereo-diene':
                        drawStereoDiene();
                        break;
                    case 'homo':
                        drawHomoLumo();
                        break;
                }
            };

            // --- DOM & STATE LOGIC ---
            function setupDOMListeners() {
                modeButtons = document.querySelectorAll('.mode-button');
                checkInverse = document.getElementById('check-inverse');
                checkFocus = document.getElementById('check-focus');
                sliderReaction = document.getElementById('slider-reaction');
                sliderErgEwg = document.getElementById('slider-erg-ewg');

                btnReactionMinus = document.getElementById('btn-reaction-minus');
                btnReactionPlus = document.getElementById('btn-reaction-plus');
                btnErgEwgMinus = document.getElementById('btn-erg-ewg-minus');
                btnErgEwgPlus = document.getElementById('btn-erg-ewg-plus');

                controlGroups = {
                    inverse: document.getElementById('inverse-container'),
                    focus: document.getElementById('focus-container'),
                    reaction: document.getElementById('reaction-slider-container'),
                    ergEwg: document.getElementById('erg-ewg-slider-container'),
                };

                // Mode buttons
                modeButtons.forEach(button => {
                    button.addEventListener('click', () => {
                        modeButtons.forEach(btn => btn.classList.remove('active'));
                        button.classList.add('active');
                        state.activeMode = button.id.replace('btn-mode-', '');
                        updateControlVisibility();
                        p.redraw();
                    });
                });

                // Controls
                checkInverse.addEventListener('input', (e) => { state.inverse = e.target.checked; p.redraw(); });
                checkFocus.addEventListener('input', (e) => { state.focus = e.target.checked; p.redraw(); });
                sliderReaction.addEventListener('input', (e) => { state.reaction = parseInt(e.target.value); p.redraw(); });
                sliderErgEwg.addEventListener('input', (e) => { state.ergEwg = parseInt(e.target.value); p.redraw(); });

                const setupSliderButtons = (slider, minusBtn, plusBtn) => {
                    minusBtn.addEventListener('click', () => {
                        slider.value = Math.max(parseInt(slider.min), parseInt(slider.value) - parseInt(slider.step));
                        slider.dispatchEvent(new Event('input'));
                    });
                    plusBtn.addEventListener('click', () => {
                        slider.value = Math.min(parseInt(slider.max), parseInt(slider.value) + parseInt(slider.step));
                        slider.dispatchEvent(new Event('input'));
                    });
                };

                setupSliderButtons(sliderReaction, btnReactionMinus, btnReactionPlus);
                setupSliderButtons(sliderErgEwg, btnErgEwgMinus, btnErgEwgPlus);
            }

            function updateControlVisibility() {
                // Hide all first
                Object.values(controlGroups).forEach(group => group.classList.add('hidden'));

                // Show based on mode
                switch (state.activeMode) {
                    case 'da':
                        controlGroups.focus.classList.remove('hidden');
                        controlGroups.reaction.classList.remove('hidden');
                        break;
                    case 'regio':
                        Object.values(controlGroups).forEach(group => group.classList.remove('hidden'));
                        break;
                    case 'stereo-phile':
                    case 'stereo-diene':
                        controlGroups.focus.classList.remove('hidden');
                        break;
                    case 'homo':
                        controlGroups.inverse.classList.remove('hidden');
                        controlGroups.ergEwg.classList.remove('hidden');
                        break;
                }
            }

            // --- DRAWING FUNCTIONS ---

            function drawDoubleBond(x1, y1, x2, y2, offset = 4) {
                const angle = p.atan2(y2 - y1, x2 - x1);
                const dx = offset * p.sin(angle);
                const dy = offset * p.cos(angle);
                p.line(x1 - dx, y1 + dy, x2 - dx, y2 + dy);
                p.line(x1 + dx, y1 - dy, x2 + dx, y2 - dy);
            }

            function drawDielsAlder() {
                const t = state.reaction / 100;

                // Define coordinates
                const s = 80; // scale
                const dieneReactant = [
                    {x: -2.5*s, y: -0.5*s}, {x: -1.7*s, y: 0.5*s}, {x: -0.7*s, y: -0.5*s}, {x: 0.1*s, y: 0.5*s}
                ];
                const phileReactant = [{x: 0.5*s, y: -0.7*s}, {x: 0.5*s, y: 0.7*s}];

                const product = [
                    {x: -1.5*s, y: -0.866*s}, {x: -1.5*s, y: 0.866*s}, {x: 0, y: 1.5*s},
                    {x: 1.5*s, y: 0.866*s}, {x: 1.5*s, y: -0.866*s}, {x: 0, y: -1.5*s}
                ];

                // Interpolation logic
                let t_approach = p.min(1, t / 0.5);
                let t_react = p.max(0, (t - 0.5) / 0.5);

                const dieneNow = [
                    lerpPoint(dieneReactant[0], product[0], t_approach),
                    lerpPoint(dieneReactant[1], product[1], t_approach),
                    lerpPoint(dieneReactant[2], product[2], t_approach),
                    lerpPoint(dieneReactant[3], product[3], t_approach)
                ];
                const phileNow = [
                    lerpPoint(phileReactant[0], product[5], t_approach),
                    lerpPoint(phileReactant[1], product[4], t_approach)
                ];

                // --- Draw Bonds ---
                p.strokeWeight(2);
                p.stroke(0);

                // Diene backbone (always present)
                p.line(dieneNow[0].x, dieneNow[0].y, dieneNow[1].x, dieneNow[1].y);
                p.line(dieneNow[1].x, dieneNow[1].y, dieneNow[2].x, dieneNow[2].y);
                p.line(dieneNow[2].x, dieneNow[2].y, dieneNow[3].x, dieneNow[3].y);

                // --- Fading/Forming Bonds ---
                const breakAlpha = lerp(255, 0, t_react);
                const formAlpha = lerp(0, 255, t_react);

                // Bonds being broken
                p.stroke(0, breakAlpha);
                drawDoubleBond(dieneNow[0].x, dieneNow[0].y, dieneNow[1].x, dieneNow[1].y);
                drawDoubleBond(dieneNow[2].x, dieneNow[2].y, dieneNow[3].x, dieneNow[3].y);
                drawDoubleBond(phileNow[0].x, phileNow[0].y, phileNow[1].x, phileNow[1].y);

                // Bonds being formed
                // New C-C sigma bonds
                p.stroke(0, formAlpha);
                p.line(dieneNow[0].x, dieneNow[0].y, phileNow[0].x, phileNow[0].y);
                p.line(dieneNow[3].x, dieneNow[3].y, phileNow[1].x, phileNow[1].y);
                // New pi bond
                drawDoubleBond(dieneNow[1].x, dieneNow[1].y, dieneNow[2].x, dieneNow[2].y);

                // --- Focus Highlight ---
                if (state.focus) {
                    p.strokeWeight(6);
                    // Old bonds fading highlight
                    p.stroke(255, 0, 0, breakAlpha * 0.7);
                    drawDoubleBond(dieneNow[0].x, dieneNow[0].y, dieneNow[1].x, dieneNow[1].y, 5);
                    drawDoubleBond(dieneNow[2].x, dieneNow[2].y, dieneNow[3].x, dieneNow[3].y, 5);
                    drawDoubleBond(phileNow[0].x, phileNow[0].y, phileNow[1].x, phileNow[1].y, 5);

                    // New bonds forming highlight
                    p.stroke(255, 0, 0, formAlpha * 0.7);
                    p.line(dieneNow[0].x, dieneNow[0].y, phileNow[0].x, phileNow[0].y);
                    p.line(dieneNow[3].x, dieneNow[3].y, phileNow[1].x, phileNow[1].y);
                    drawDoubleBond(dieneNow[1].x, dieneNow[1].y, dieneNow[2].x, dieneNow[2].y, 5);
                }
            }

            // Placeholder/Simplified version
            function drawRegioselectivity() {
                p.textAlign(p.CENTER, p.CENTER);
                p.noStroke();
                p.fill(150);
                p.textSize(16);
                p.text("Regioselectivity visualization is a complex feature.", 0, -20);
                p.text("This mock-up demonstrates control visibility.", 0, 20);
                p.text(`Inverse: ${state.inverse}, Reaction: ${state.reaction}, ERG/EWG: ${state.ergEwg}`, 0, 60);

            }

            function drawStereoMolecule(offsetX, offsetY, scale, isRacemic, isTop) {
                const s = scale;
                // Base cyclohexene structure
                const product = [
                    {x: -0.75*s, y: -0.433*s}, {x: -0.75*s, y: 0.433*s}, {x: 0, y: 0.866*s},
                    {x: 0.75*s, y: 0.433*s}, {x: 0.75*s, y: -0.433*s}, {x: 0, y: -0.866*s}
                ];

                p.push();
                p.translate(offsetX, offsetY);

                // Draw bonds
                p.stroke(0);
                p.strokeWeight(2);
                p.noFill();
                p.beginShape();
                for (const pt of product) p.vertex(pt.x, pt.y);
                p.endShape(p.CLOSE);
                drawDoubleBond(product[1].x, product[1].y, product[2].x, product[2].y);

                // Substituents
                const sub1Attach = product[0];
                const sub2Attach = product[5];

                const sub1End = { x: sub1Attach.x + 0.6*s, y: sub1Attach.y - 0.7*s };
                const sub2End = { x: sub2Attach.x - 0.6*s, y: sub2Attach.y - 0.7*s * (isTop ? 1 : -1) };

                // Focus highlight
                if (state.focus) {
                    p.strokeWeight(3);
                    p.stroke(255, 0, 0);
                    p.line(product[0].x, product[0].y, product[1].x, product[1].y);
                    p.line(product[2].x, product[2].y, product[3].x, product[3].y);
                }

                // Stereochemistry lines
                p.stroke(180);
                p.strokeWeight(1.5);
                p.drawingContext.setLineDash([5, 5]);
                p.line(product[0].x, product[0].y, product[3].x, product[3].y);
                p.line(product[5].x, product[5].y, product[4].x, product[4].y);
                p.drawingContext.setLineDash([]);

                // Substituent bonds
                p.stroke(0);
                p.strokeWeight(2);
                p.line(sub1Attach.x, sub1Attach.y, sub1End.x, sub1End.y);
                p.line(sub2Attach.x, sub2Attach.y, sub2End.x, sub2End.y);

                // Text labels
                p.noStroke();
                p.fill(80);
                p.textSize(16);
                p.textAlign(p.LEFT, p.CENTER);
                p.text('COOH', sub1End.x + 5, sub1End.y);
                p.textAlign(isTop ? p.RIGHT : p.LEFT, p.CENTER);
                p.text(isTop ? 'COOH' : 'HOOC', sub2End.x + (isTop ? -5 : 5), sub2End.y);

                p.pop();
            }

            function drawStereoPhile() {
                const w = p.width;
                const h = p.height;
                const s = Math.min(w, h) / 7;

                // Top part: meso
                p.stroke(200);
                p.line(-w/2, 0, w/2, 0);
                p.noStroke();
                p.fill(100);
                p.textAlign(p.CENTER, p.BOTTOM);
                p.textSize(18);
                p.text('cis', 0, -h/2 + 25);
                drawStereoMolecule(0, -h/4, s, false, true);
                p.fill(150);
                p.textAlign(p.CENTER, p.TOP);
                p.textSize(14);
                p.text('meso compound', 0, -h/4 + 1.2*s);

                // Symmetry plane
                p.stroke(255, 100, 100);
                p.strokeWeight(1.5);
                p.drawingContext.setLineDash([8, 6]);
                p.line(0, -h/2 + 30, 0, -10);
                p.drawingContext.setLineDash([]);


                // Bottom part: racemic
                p.noStroke();
                p.fill(100);
                p.textAlign(p.CENTER, p.BOTTOM);
                p.textSize(18);
                p.text('trans', 0, 25);
                drawStereoMolecule(-w/4, h/4, s, true, false);
                drawStereoMolecule(w/4, h/4, s, true, true);
                p.fill(150);
                p.textAlign(p.CENTER, p.TOP);
                p.textSize(14);
                p.text('racemic mixture', 0, h/4 + 1.2*s);

                 // Symmetry planes (visual guides for enantiomers)
                p.stroke(255, 100, 100);
                p.strokeWeight(1.5);
                p.drawingContext.setLineDash([8, 6]);
                p.line(-w/4, 30, -w/4, h/2 - 10);
                p.line(w/4, 30, w/4, h/2 - 10);
                p.drawingContext.setLineDash([]);
            }

            function drawStereoDiene() {
                 p.textAlign(p.CENTER, p.CENTER);
                p.noStroke();
                p.fill(150);
                p.textSize(16);
                p.text("Stereospecificity (diene) follows a similar principle.", 0, -20);
                p.text("This view is a placeholder to complete the UI.", 0, 20);
            }

            function drawHomoLumo() {
                const intensity = state.ergEwg / 100;
                const maxShift = 50;
                const shift = intensity * maxShift;

                let dieneShift = state.inverse ? shift : -shift;
                let phileShift = state.inverse ? -shift : shift;

                // Positions
                const dieneX = -150;
                const phileX = 150;
                const baseLumoY = -100;
                const baseHomoY = 0;

                const dieneHomoY = baseHomoY + dieneShift;
                const dieneLumoY = baseLumoY + dieneShift;
                const phileHomoY = baseHomoY + phileShift;
                const phileLumoY = baseLumoY + phileShift;

                // Labels
                p.noStroke();
                p.fill(0);
                p.textSize(16);
                p.textAlign(p.CENTER, p.BOTTOM);
                p.text('diene', dieneX, baseLumoY - 50);
                p.text('dienophile', phileX, baseLumoY - 50);

                if (state.inverse) {
                    p.textSize(14);
                    p.fill(100);
                    p.text('EWG', dieneX, baseLumoY - 30);
                    p.text('ERG', phileX, baseLumoY - 30);
                }

                // Draw energy levels and orbitals
                drawEnergyLevel(dieneX, dieneLumoY, 'LUMO', false);
                drawEnergyLevel(dieneX, dieneHomoY, 'HOMO', true);
                drawEnergyLevel(phileX, phileLumoY, 'LUMO', false);
                drawEnergyLevel(phileX, phileHomoY, 'HOMO', true);

                // Draw interaction arrows
                const gap1 = p.abs(dieneHomoY - phileLumoY); // Normal
                const gap2 = p.abs(phileHomoY - dieneLumoY); // Inverse

                if (gap1 < gap2) {
                    // Normal is dominant
                    drawInteractionArrow(dieneX, dieneHomoY, phileX, phileLumoY, true);
                    drawInteractionArrow(phileX, phileHomoY, dieneX, dieneLumoY, false);
                } else {
                    // Inverse is dominant
                    drawInteractionArrow(dieneX, dieneHomoY, phileX, phileLumoY, false);
                    drawInteractionArrow(phileX, phileHomoY, dieneX, dieneLumoY, true);
                }
            }

            function drawEnergyLevel(x, y, label, filled) {
                p.stroke(0);
                p.strokeWeight(1.5);
                p.line(x - 40, y, x + 40, y);

                // Orbital representation
                p.line(x - 15, y - 10, x - 5, y - 10);
                p.line(x + 5, y - 10, x + 15, y - 10);

                if (filled) {
                    p.push();
                    p.noStroke();
                    p.fill(0);
                    // Up arrow
                    p.triangle(x-10, y-12, x-14, y-20, x-6, y-20);
                    // Down arrow
                    p.triangle(x+10, y-8, x+6, y-16, x+14, y-16);
                    p.pop();
                }

                p.noStroke();
                p.fill(0);
                p.textSize(14);
                p.textAlign(p.LEFT, p.CENTER);
                p.text(label, x - 80, y);
            }

            function drawInteractionArrow(x1, y1, x2, y2, isDominant) {
                p.push();
                p.strokeWeight(isDominant ? 2.5 : 1.5);
                p.stroke(isDominant ? p.color(255, 0, 0) : 0);
                p.drawingContext.setLineDash([5, 5]);

                const angle = p.atan2(y2 - y1, x2 - x1);
                p.line(x1 + 45, y1, x2 - 45, y2);

                p.translate(x2 - 45, y2);
                p.rotate(angle);
                p.noStroke();
                p.fill(isDominant ? p.color(255, 0, 0) : 0);
                p.triangle(0, 0, -10, -5, -10, 5);

                p.pop();
            }

        };

        new p5(sketch);
    </script>
</body>
</html>
