<body>
    <div id="demo-container">
        <!-- Section 1: Control Panel -->
        <div id="control-panel">
            <label>step</label>
            <input type="radio" id="step1" name="step-selector" value="1" checked>
            <label for="step1">1</label>
            <input type="radio" id="step2" name="step-selector" value="2">
            <label for="step2">2</label>
            <input type="radio" id="step3" name="step-selector" value="3">
            <label for="step3">3</label>
        </div>
        <!-- Section 2: Visualization Area -->
        <div id="canvas-container">
            <!-- p5.js canvas will be created here -->
        </div>
    </div>
    <script>
        // Global state variables
        let currentStep = 1;
        let P;
        let A, B, C;
        let isDragging = false;

        const canvasWidth = 600;
        const canvasHeight = 520;
        const triangleSide = 480;
        const sqrt3 = Math.sqrt(3);

        function setup() {
            let canvas = createCanvas(canvasWidth, canvasHeight);
            canvas.parent('canvas-container');

            const h = triangleSide * sqrt3 / 2;
            const marginTop = (canvasHeight - h) / 2 + 20;
            const marginX = (canvasWidth - triangleSide) / 2;

            A = createVector(marginX, marginTop + h);
            B = createVector(marginX + triangleSide, marginTop + h);
            C = createVector(canvasWidth / 2, marginTop);

            // Initial position for point P
            P = createVector(width * 0.4, height * 0.6);

            // Add event listeners to radio buttons to update the current step
            const radios = document.querySelectorAll('input[name="step-selector"]');
            radios.forEach(radio => {
                radio.addEventListener('change', (event) => {
                    currentStep = parseInt(event.target.value);
                });
            });
        }

        function draw() {
            background(255);

            drawMainTriangle();

            const [d_ab, d_bc, d_ac] = calculatePerpendiculars(P);

            if (currentStep === 1) {
                drawStep1(P);
            } else if (currentStep === 2) {
                drawStep2(P, d_ab, d_bc, d_ac);
            } else if (currentStep === 3) {
                drawStep3(P, d_ab, d_bc, d_ac);
            }
        }

        // --- Drawing Functions for Each Step ---

        function drawMainTriangle() {
            stroke(0);
            noFill();
            strokeWeight(1.5);
            triangle(A.x, A.y, B.x, B.y, C.x, C.y);

            // Main Altitude
            drawingContext.setLineDash([5, 5]);
            stroke(180, 200, 255);
            strokeWeight(1);
            line(C.x, C.y, C.x, A.y);
            drawingContext.setLineDash([]);

            // Vertex Labels
            noStroke();
            fill(0);
            textSize(16);
            textAlign(RIGHT, TOP);
            text('A', A.x - 5, A.y);
            textAlign(LEFT, TOP);
            text('B', B.x + 5, B.y);
            textAlign(CENTER, BOTTOM);
            text('C', C.x, C.y - 5);
        }

        function drawStep1(point) {
            const foot_ab = createVector(point.x, A.y);
            const foot_ac = getClosestPointOnLine(point, A, C);
            const foot_bc = getClosestPointOnLine(point, B, C);

            stroke(0);
            strokeWeight(1);
            line(point.x, point.y, foot_ab.x, foot_ab.y);
            line(point.x, point.y, foot_ac.x, foot_ac.y);
            line(point.x, point.y, foot_bc.x, foot_bc.y);

            fill(0, 0, 255);
            noStroke();
            circle(foot_ab.x, foot_ab.y, 8);
            circle(foot_ac.x, foot_ac.y, 8);
            circle(foot_bc.x, foot_bc.y, 8);

            drawP_Marker(point, 255);
        }

        function drawStep2(point, d_ab, d_bc, d_ac) {
            // --- Red Triangle (altitude d_ab) ---
            const s_ab = d_ab * 2 / sqrt3;
            fill(255, 0, 0, 128);
            noStroke();
            triangle(point.x, point.y, point.x - s_ab / 2, point.y + d_ab, point.x + s_ab / 2, point.y + d_ab);
            strokeWeight(1);
            stroke(255, 100, 100);
            line(point.x, point.y, point.x, point.y + d_ab);

            // --- Green Triangle (altitude d_ac) ---
            const s_ac = d_ac * 2 / sqrt3;
            const angle_ac = p5.Vector.sub(C, A).heading();
            const green_v2 = createVector(s_ac / 2, -d_ac).rotate(angle_ac).add(point);
            const green_v3 = createVector(-s_ac / 2, -d_ac).rotate(angle_ac).add(point);
            fill(0, 255, 0, 128);
            noStroke();
            triangle(point.x, point.y, green_v2.x, green_v2.y, green_v3.x, green_v3.y);
            const green_mid_base = createVector(0, -d_ac).rotate(angle_ac).add(point);
            strokeWeight(1);
            stroke(100, 255, 100);
            line(point.x, point.y, green_mid_base.x, green_mid_base.y);

            // --- Blue Triangle (altitude d_bc) ---
            const s_bc = d_bc * 2 / sqrt3;
            const angle_bc = p5.Vector.sub(C, B).heading();
            const blue_v2 = createVector(s_bc / 2, -d_bc).rotate(angle_bc).add(point);
            const blue_v3 = createVector(-s_bc / 2, -d_bc).rotate(angle_bc).add(point);
            fill(0, 0, 255, 128);
            noStroke();
            triangle(point.x, point.y, blue_v2.x, blue_v2.y, blue_v3.x, blue_v3.y);
            const blue_mid_base = createVector(0, -d_bc).rotate(angle_bc).add(point);
            strokeWeight(1);
            stroke(100, 100, 255);
            line(point.x, point.y, blue_mid_base.x, blue_mid_base.y);

            // Feet markers on main triangle
            const foot_ab = createVector(point.x, A.y);
            const foot_ac = getClosestPointOnLine(point, A, C);
            const foot_bc = getClosestPointOnLine(point, B, C);
            fill(0, 0, 255); noStroke();
            circle(foot_ab.x, foot_ab.y, 8);
            circle(foot_ac.x, foot_ac.y, 8);
            circle(foot_bc.x, foot_bc.y, 8);

            // Labels for x and y
            const x_point = (green_v2.x < green_v3.x) ? green_v2 : green_v3;
            const y_point = (blue_v2.x > blue_v3.x) ? blue_v2 : blue_v3;
            noStroke(); fill(0); textAlign(CENTER, BOTTOM); textSize(14);
            text('x', x_point.x, x_point.y - 2);
            text('y', y_point.x, y_point.y - 2);

            drawP_Marker(point, 255);
        }

        function drawStep3(point, d_ab, d_bc, d_ac) {
            const main_altitude_x = C.x;

            drawP_Marker(point, 80); // Draw ghost of P

            // --- Red Triangle (stacked) ---
            const s_ab = d_ab * 2 / sqrt3;
            const y_bottom_red = A.y;
            const y_top_red = y_bottom_red - d_ab;
            fill(255, 0, 0, 128);
            noStroke();
            triangle(main_altitude_x, y_top_red, main_altitude_x - s_ab / 2, y_bottom_red, main_altitude_x + s_ab / 2, y_bottom_red);

            // --- Blue Triangle (stacked) ---
            const s_bc = d_bc * 2 / sqrt3;
            const y_bottom_blue = y_top_red;
            const y_top_blue = y_bottom_blue - d_bc;
            fill(0, 0, 255, 128);
            noStroke();
            triangle(main_altitude_x, y_top_blue, main_altitude_x - s_bc / 2, y_bottom_blue, main_altitude_x + s_bc / 2, y_bottom_blue);

            // --- Green Triangle (stacked) ---
            const s_ac = d_ac * 2 / sqrt3;
            const y_bottom_green = y_top_blue;
            const y_top_green = y_bottom_green - d_ac;
            fill(0, 255, 0, 128);
            noStroke();
            triangle(main_altitude_x, y_top_green, main_altitude_x - s_ac / 2, y_bottom_green, main_altitude_x + s_ac / 2, y_bottom_green);

            // Label 'y' at the top of the blue triangle
            noStroke(); fill(0); textAlign(CENTER, BOTTOM); textSize(14);
            text('y', main_altitude_x, y_top_blue - 2);
        }

        function drawP_Marker(point, alpha) {
            stroke(0);
            strokeWeight(1.5);
            fill(220, 220, 220, alpha);
            circle(point.x, point.y, 16);
            line(point.x - 5, point.y, point.x + 5, point.y);
            line(point.x, point.y - 5, point.x, point.y + 5);

            noStroke();
            fill(255, 0, 0, alpha);
            textSize(16);
            textAlign(RIGHT, TOP);
            text('P', point.x - 10, point.y + 5);
        }

        // --- Interaction Handlers ---

        function mousePressed() {
            if (mouseX > 0 && mouseX < width && mouseY > 0 && mouseY < height) {
                if (dist(mouseX, mouseY, P.x, P.y) < 15) {
                    isDragging = true;
                }
            }
        }

        function mouseReleased() {
            isDragging = false;
        }

        function mouseDragged() {
            if (isDragging) {
                const mousePos = createVector(mouseX, mouseY);
                if (isPointInTriangle(mousePos, A, B, C)) {
                    P.set(mousePos.x, mousePos.y);
                } else {
                    const p1 = getClosestPointOnSegment(mousePos, A, B);
                    const p2 = getClosestPointOnSegment(mousePos, B, C);
                    const p3 = getClosestPointOnSegment(mousePos, C, A);

                    const d1 = p5.Vector.dist(mousePos, p1);
                    const d2 = p5.Vector.dist(mousePos, p2);
                    const d3 = p5.Vector.dist(mousePos, p3);

                    let closestPoint = p1;
                    if (d2 < d1 && d2 < d3) {
                        closestPoint = p2;
                    } else if (d3 < d1) {
                        closestPoint = p3;
                    }
                    P.set(closestPoint.x, closestPoint.y);
                }
            }
        }

        // --- Geometry Helper Functions ---

        function calculatePerpendiculars(point) {
            const d_ab = A.y - point.y;
            const d_ac = abs((C.y - A.y) * point.x - (C.x - A.x) * point.y + C.x * A.y - C.y * A.x) / dist(A.x, A.y, C.x, C.y);
            const d_bc = abs((C.y - B.y) * point.x - (C.x - B.x) * point.y + C.x * B.y - C.y * B.x) / dist(B.x, B.y, C.x, C.y);
            return [d_ab, d_bc, d_ac];
        }

        function getClosestPointOnLine(p, a, b) {
            const ab = p5.Vector.sub(b, a);
            const ap = p5.Vector.sub(p, a);
            const t = ap.dot(ab) / ab.magSq();
            return p5.Vector.add(a, ab.mult(t));
        }

        function getClosestPointOnSegment(p, a, b) {
            const ab = p5.Vector.sub(b, a);
            const ap = p5.Vector.sub(p, a);
            let t = ap.dot(ab) / ab.magSq();
            t = constrain(t, 0, 1);
            return p5.Vector.add(a, ab.mult(t));
        }

        function sign(p1, p2, p3) {
            return (p1.x - p3.x) * (p2.y - p3.y) - (p2.x - p3.x) * (p1.y - p3.y);
        }

        function isPointInTriangle(pt, v1, v2, v3) {
            const d1 = sign(pt, v1, v2);
            const d2 = sign(pt, v2, v3);
            const d3 = sign(pt, v3, v1);
            const has_neg = (d1 < 0) || (d2 < 0) || (d3 < 0);
            const has_pos = (d1 > 0) || (d2 > 0) || (d3 > 0);
            return !(has_neg && has_pos);
        }

    </script>
</body>
</html>
