    <script>
        // --- DATA AND CONSTANTS ---
        const ORBITAL_DATA = {
            'Mercury':  { a: 0.3871,   e: 0.2056, i: 7.005,  long: 48.331,  arg: 29.124,  M: 174.796, color: 0x808080, size: 0.1 },
            'Venus':    { a: 0.7233,   e: 0.0068, i: 3.395,  long: 76.680,  arg: 54.884,  M: 50.416,  color: 0xD2B48C, size: 0.15 },
            'Earth':    { a: 1.0000,   e: 0.0167, i: 0.000,  long: -11.260, arg: 114.208, M: 358.617, color: 0x4682B4, size: 0.16 },
            'Mars':     { a: 1.5237,   e: 0.0934, i: 1.850,  long: 49.562,  arg: 286.502, M: 19.412,  color: 0xFF4500, size: 0.12 },
            'Ceres':    { a: 2.7691,   e: 0.0760, i: 10.593, long: 80.393,  arg: 73.598,  M: 77.372,  color: 0x4B0082, size: 0.3 },
            'Pluto':    { a: 39.482,   e: 0.2488, i: 17.160, long: 110.299, arg: 113.834, M: 14.882,  color: 0x4B0082, size: 0.4 },
            'Haumea':   { a: 43.132,   e: 0.1912, i: 28.220, long: 122.155, arg: 239.544, M: 204.03,  color: 0x4B0082, size: 0.4 },
            'Makemake': { a: 45.791,   e: 0.1559, i: 29.006, long: 79.623,  arg: 294.821, M: 95.895,  color: 0x4B0082, size: 0.4 },
            'Eris':     { a: 67.668,   e: 0.4418, i: 44.187, long: 35.951,  arg: 151.291, M: 206.51,  color: 0x4B0082, size: 0.4 }
        };
        const INNER_PLANETS = ['Mercury', 'Venus', 'Earth', 'Mars'];
        const DWARF_PLANETS = ['Ceres', 'Pluto', 'Haumea', 'Makemake', 'Eris'];
        const BASE_DATE = new Date('2000-01-01T00:00:00Z');
        const J2000_EPOCH_OFFSET_DAYS = 0.5; // J2000 is at 12:00 UT, slider starts at 00:00

        // --- STATE VARIABLES ---
        let scene, camera, renderer, controls;
        let isAnimating = false;
        let selectedDwarfPlanet = 'Pluto';
        const celestialObjects = {}; // To hold meshes and orbits: { name: { mesh, orbit } }
        let currentDwarfPlanetObjects = { mesh: null, orbit: null };

        // --- DOM ELEMENTS ---
        const timeSlider = document.getElementById('time-slider');
        const playPauseButton = document.getElementById('play-pause-button');
        const dateDisplay = document.getElementById('date-display');
        const dwarfPlanetSelector = document.getElementById('dwarf-planet-selector');
        const canvas = document.getElementById('3d-canvas');

        // --- HELPER FUNCTIONS ---
        const degToRad = (degrees) => degrees * (Math.PI / 180);

        function calculatePosition(daysSinceEpoch, elements) {
            const d = daysSinceEpoch;
            const a = elements.a;
            const e = elements.e;
            const i_rad = degToRad(elements.i);
            const long_rad = degToRad(elements.long);
            const arg_rad = degToRad(elements.arg);
            const M_epoch_rad = degToRad(elements.M);

            const P = 365.25 * Math.pow(a, 1.5); // Orbital period in days
            const M_rad = (M_epoch_rad + (2 * Math.PI / P) * d) % (2 * Math.PI);

            // Solve Kepler's Equation: M = E - e*sin(E) using Newton-Raphson
            let E = M_rad; // Initial guess
            for (let i = 0; i < 7; i++) { // 7 iterations is more than enough
                E = E - (E - e * Math.sin(E) - M_rad) / (1 - e * Math.cos(E));
            }

            // Calculate true anomaly (nu)
            const nu = 2 * Math.atan2(Math.sqrt(1 + e) * Math.sin(E / 2), Math.sqrt(1 - e) * Math.cos(E / 2));

            // Calculate heliocentric distance (r)
            const r = a * (1 - e * Math.cos(E));

            // Position in orbital plane
            const x_orb = r * Math.cos(nu);
            const y_orb = r * Math.sin(nu);

            // Transform to ecliptic coordinates (Y-up for three.js)
            const w_v = arg_rad + nu;
            const cos_w_v = Math.cos(w_v);
            const sin_w_v = Math.sin(w_v);
            const cos_long = Math.cos(long_rad);
            const sin_long = Math.sin(long_rad);
            const cos_i = Math.cos(i_rad);
            const sin_i = Math.sin(i_rad);

            const x = r * (cos_long * cos_w_v - sin_long * sin_w_v * cos_i);
            const z = -r * (sin_long * cos_w_v + cos_long * sin_w_v * cos_i);
            const y = r * (sin_i * sin_w_v);

            return new THREE.Vector3(x, y, z);
        }

        // --- SCENE SETUP ---
        function init() {
            // Scene
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xf8f9fa);

            // Camera
            const container = document.getElementById('visualization-container');
            const aspect = container.clientWidth / container.clientHeight;
            camera = new THREE.PerspectiveCamera(45, aspect, 0.1, 1000);
            camera.position.set(0, 40, 70);
            camera.lookAt(0, 0, 0);

            // Renderer
            renderer = new THREE.WebGLRenderer({ canvas: canvas, antialias: true });
            renderer.setSize(container.clientWidth, container.clientHeight);
            renderer.setPixelRatio(window.devicePixelRatio);

            // Lights
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
            scene.add(ambientLight);
            const pointLight = new THREE.PointLight(0xfff5e1, 1, 0, 2);
            pointLight.position.set(0, 0, 0);
            scene.add(pointLight);

            // Controls
            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.05;

            // Sun
            const sunGeo = new THREE.SphereGeometry(0.5, 32, 32);
            const sunMat = new THREE.MeshBasicMaterial({ color: 0xffcc00 });
            const sun = new THREE.Mesh(sunGeo, sunMat);
            scene.add(sun);

            // Inner Planets
            INNER_PLANETS.forEach(name => {
                const data = ORBITAL_DATA[name];
                const mesh = createCelestialBody(data.size, data.color);
                const orbit = createOrbitPath(name, 0x0000ff, 0.4);
                celestialObjects[name] = { mesh, orbit };
                scene.add(mesh);
                scene.add(orbit);
            });

            // Initial Dwarf Planet
            updateDwarfPlanet();

            // Event Listeners
            timeSlider.addEventListener('input', () => updateScene());
            playPauseButton.addEventListener('click', toggleAnimation);
            dwarfPlanetSelector.addEventListener('click', handleDwarfPlanetSelect);
            window.addEventListener('resize', onWindowResize);

            // Initial render
            updateScene();
            animate();
        }

        function createCelestialBody(size, color) {
            const geometry = new THREE.SphereGeometry(size, 16, 16);
            const material = new THREE.MeshLambertMaterial({ color: color });
            return new THREE.Mesh(geometry, material);
        }

        function createOrbitPath(planetName, color, opacity = 1.0) {
            const elements = ORBITAL_DATA[planetName];
            const period = 365.25 * Math.pow(elements.a, 1.5);
            const points = [];
            const numPoints = 200;
            for (let i = 0; i <= numPoints; i++) {
                const day = (i / numPoints) * period;
                points.push(calculatePosition(day - J2000_EPOCH_OFFSET_DAYS, elements));
            }
            const geometry = new THREE.BufferGeometry().setFromPoints(points);
            const material = new THREE.LineBasicMaterial({ color: color, transparent: true, opacity: opacity });
            return new THREE.Line(geometry, material);
        }

        // --- UPDATE & ANIMATION LOGIC ---
        function updateScene() {
            const days = parseFloat(timeSlider.value);
            const daysSinceJ2000 = days - J2000_EPOCH_OFFSET_DAYS;

            // Update Date Display
            const currentDate = new Date(BASE_DATE.getTime() + days * 24 * 60 * 60 * 1000);
            const options = { weekday: 'short', year: 'numeric', month: 'short', day: 'numeric', hour: '2-digit', minute: '2-digit', second: '2-digit', timeZone: 'UTC', hour12: false };
            const formattedDate = new Intl.DateTimeFormat('en-US', options).format(currentDate);
            dateDisplay.textContent = formattedDate.replace(/, /g, ' ').replace(' 24:', ' 00:'); // a bit of formatting hack

            // Update Planet Positions
            for (const name in celestialObjects) {
                const pos = calculatePosition(daysSinceJ2000, ORBITAL_DATA[name]);
                celestialObjects[name].mesh.position.copy(pos);
            }
        }

        function updateDwarfPlanet() {
            // Remove old dwarf planet if it exists
            if (currentDwarfPlanetObjects.mesh) scene.remove(currentDwarfPlanetObjects.mesh);
            if (currentDwarfPlanetObjects.orbit) scene.remove(currentDwarfPlanetObjects.orbit);

            // Create new dwarf planet
            const data = ORBITAL_DATA[selectedDwarfPlanet];
            const mesh = createCelestialBody(data.size, data.color);
            const orbit = createOrbitPath(selectedDwarfPlanet, 0xff0000, 1.0);

            // Store and add to scene
            currentDwarfPlanetObjects = { mesh, orbit };
            celestialObjects[selectedDwarfPlanet] = currentDwarfPlanetObjects; // Add to main object list for position updates
            scene.add(mesh);
            scene.add(orbit);

            // Immediately update position
            updateScene();
        }

        function animate() {
            requestAnimationFrame(animate);

            if (isAnimating) {
                let currentValue = parseFloat(timeSlider.value);
                currentValue += 2; // Animation speed
                if (currentValue >= timeSlider.max) {
                    currentValue = timeSlider.min;
                }
                timeSlider.value = currentValue;
                // Manually trigger the input event to update the scene
                timeSlider.dispatchEvent(new Event('input'));
            }

            controls.update();
            renderer.render(scene, camera);
        }

        // --- EVENT HANDLERS ---
        function toggleAnimation() {
            isAnimating = !isAnimating;
            playPauseButton.textContent = isAnimating ? '⏸' : '▶';
        }

        function handleDwarfPlanetSelect(e) {
            if (e.target.tagName === 'BUTTON') {
                const newPlanet = e.target.dataset.planet;
                if (newPlanet === selectedDwarfPlanet) return;

                // Update active button
                const currentActive = dwarfPlanetSelector.querySelector('.active');
                if (currentActive) currentActive.classList.remove('active');
                e.target.classList.add('active');

                // Remove old dwarf planet from main update list
                delete celestialObjects[selectedDwarfPlanet];

                // Update state and scene
                selectedDwarfPlanet = newPlanet;
                updateDwarfPlanet();
            }
        }

        function onWindowResize() {
            const container = document.getElementById('visualization-container');
            const width = container.clientWidth;
            const height = container.clientHeight;

            camera.aspect = width / height;
            camera.updateProjectionMatrix();
            renderer.setSize(width, height);
        }

        // --- INITIALIZATION CALL ---
        init();
    </script>
</body>
