    </style>
</head>
<body>

    <div id="main-container">
        <!-- Left Panel: Controls -->
        <div id="control-panel">
            <label for="select-cfc">cfc plot</label>
            <select id="select-cfc"></select>

            <div class="checkbox-container">
                <label for="checkbox-show-all">show all</label>
                <input type="checkbox" id="checkbox-show-all">
            </div>

            <label for="slider-year">year</label>
            <div class="slider-container">
                <input type="range" id="slider-year" min="1940" max="2020" step="1" value="1950">
                <span id="label-year">1950</span>
            </div>

            <label for="slider-time">time (s)</label>
            <div class="slider-container">
                <input type="range" id="slider-time" min="0" max="30" step="1" value="1">
                <span id="label-time">1</span>
            </div>
        </div>

        <!-- Right Panel: Visualizations -->
        <div id="visualization-panel">
            <div id="plots-container">
                <div id="plot-ozone" class="plot"></div>
                <div id="plot-cfc" class="plot"></div>
            </div>
            <div id="scene-container"></div>
        </div>
    </div>

<script>
document.addEventListener('DOMContentLoaded', () => {
    // --- DATA ---
    const cfcData = {
      'CFC-12': { color: 'purple', data: [[1940, 5], [1950, 20], [1960, 50], [1970, 150], [1980, 320], [1990, 480], [2000, 540], [2010, 520], [2020, 500]] },
      'CFC-11': { color: 'orange', data: [[1940, 2], [1950, 10], [1960, 30], [1970, 90], [1980, 180], [1990, 260], [2000, 250], [2010, 230], [2020, 220]] },
      'CFC-113': { color: 'black', data: [[1940, 0], [1950, 1], [1960, 5], [1970, 15], [1980, 40], [1990, 80], [2000, 85], [2010, 75], [2020, 70]] },
      'CCl4': { color: 'green', data: [[1940, 50], [1950, 60], [1960, 75], [1970, 90], [1980, 100], [1990, 105], [2000, 95], [2010, 85], [2020, 80]] }
    };

    // --- DOM ELEMENT REFERENCES ---
    const selectCfc = document.getElementById('select-cfc');
    const showAllCheckbox = document.getElementById('checkbox-show-all');
    const yearSlider = document.getElementById('slider-year');
    const yearLabel = document.getElementById('label-year');
    const timeSlider = document.getElementById('slider-time');
    const timeLabel = document.getElementById('label-time');

    const plotOzoneDiv = document.getElementById('plot-ozone');
    const plotCfcDiv = document.getElementById('plot-cfc');
    const sceneContainer = document.getElementById('scene-container');

    // --- THREE.JS SETUP ---
    let scene, camera, renderer, earth, ozoneLayer, atmosphere;

    function initThree() {
        scene = new THREE.Scene();

        const width = sceneContainer.clientWidth;
        const height = sceneContainer.clientHeight;

        camera = new THREE.PerspectiveCamera(75, width / height, 0.1, 1000);
        camera.position.set(0, 5, 20);
        camera.lookAt(scene.position);

        renderer = new THREE.WebGLRenderer({ antialias: true, alpha: true });
        renderer.setSize(width, height);
        renderer.setClearColor(0x000000, 0); 
        sceneContainer.appendChild(renderer.domElement);

        // Earth
        const earthGeometry = new THREE.SphereGeometry(5, 32, 32);
        const earthMaterial = new THREE.MeshBasicMaterial({ color: 0x0000ff });
        earth = new THREE.Mesh(earthGeometry, earthMaterial);
        scene.add(earth);

        // Ozone Layer
        const ozoneGeometry = new THREE.SphereGeometry(6, 32, 32);
        const ozoneMaterial = new THREE.MeshBasicMaterial({
            color: 0x00ff00,
            transparent: true,
            opacity: 0.5
        });
        ozoneLayer = new THREE.Mesh(ozoneGeometry, ozoneMaterial);
        scene.add(ozoneLayer);

        // Atmosphere
        const atmosphereGeometry = new THREE.SphereGeometry(6.5, 32, 32);
        const atmosphereMaterial = new THREE.MeshBasicMaterial({
            color: 0xffaa88,
            transparent: true,
            opacity: 0.1
        });
        atmosphere = new THREE.Mesh(atmosphereGeometry, atmosphereMaterial);
        scene.add(atmosphere);

        // Sun
        const sunGeometry = new THREE.SphereGeometry(1.5, 32, 32);
        const sunMaterial = new THREE.MeshBasicMaterial({ color: 0xffff00 });
        const sun = new THREE.Mesh(sunGeometry, sunMaterial);
        sun.position.x = -15;
        scene.add(sun);

        // Radiation Arrows
        const radiationGroup = new THREE.Group();
        const arrowOrigin = new THREE.Vector3(-12, 0, 0);
        for (let i = 0; i < 10; i++) {
            const angle = (Math.random() - 0.5) * Math.PI / 4;
            const dir = new THREE.Vector3(Math.cos(angle), Math.sin(angle), 0).normalize();
            const arrow = new THREE.ArrowHelper(dir, arrowOrigin, 8, 0xffa500, 1, 0.5);
            radiationGroup.add(arrow);
        }
        scene.add(radiationGroup);

        // Lighting
        const ambientLight = new THREE.AmbientLight(0xffffff, 0.8);
        scene.add(ambientLight);
        const pointLight = new THREE.PointLight(0xffffff, 0.5);
        pointLight.position.set(-20, 10, 10);
        scene.add(pointLight);

        // Render loop
        function animate() {
            requestAnimationFrame(animate);
            renderer.render(scene, camera);
        }
        animate();

        window.addEventListener('resize', onWindowResize, false);
    }

    function onWindowResize() {
        const width = sceneContainer.clientWidth;
        const height = sceneContainer.clientHeight;
        camera.aspect = width / height;
        camera.updateProjectionMatrix();
        renderer.setSize(width, height);
    }

    // --- PLOTLY SETUP ---
    function initPlots() {
        const commonLayout = {
            margin: { t: 40, b: 40, l: 40, r: 20 },
            paper_bgcolor: 'rgba(0,0,0,0)',
            plot_bgcolor: 'rgba(0,0,0,0)',
            showlegend: false
        };

        Plotly.newPlot(plotCfcDiv, [], {
            ...commonLayout,
            title: 'CFC concentration',
            xaxis: { title: 'year', range: [1935, 2025] },
            yaxis: { range: [0, 600] }
        });

        Plotly.newPlot(plotOzoneDiv, [], {
            ...commonLayout,
            title: { text: 'concentration of ozone' },
            xaxis: { title: 'time (s)', range: [0, 30] },
            yaxis: { title: '$$1/[O_3]$$', range: [0, 110] }
        });
    }

    // --- HELPER FUNCTIONS ---
    function interpolateCfc(cfcName, year) {
        const data = cfcData[cfcName].data;
        if (year <= data[0][0]) return data[0][1];
        if (year >= data[data.length - 1][0]) return data[data.length - 1][1];

        for (let i = 0; i < data.length - 1; i++) {
            const [x1, y1] = data[i];
            const [x2, y2] = data[i + 1];
            if (year >= x1 && year <= x2) {
                return y1 + (year - x1) * (y2 - y1) / (x2 - x1);
            }
        }
        return 0; // Fallback
    }

    // --- MAIN UPDATE FUNCTION ---
    function update() {
        // Read current state from controls
        const selectedCfc = selectCfc.value;
        const showAll = showAllCheckbox.checked;
        const currentYear = parseInt(yearSlider.value, 10);
        const currentTime = parseInt(timeSlider.value, 10);

        // Update labels
        yearLabel.textContent = currentYear;
        timeLabel.textContent = currentTime;

        // --- 1. Update CFC Concentration Plot ---
        const cfcTraces = [];
        if (showAll) {
            for (const cfcName in cfcData) {
                const cfc = cfcData[cfcName];
                cfcTraces.push({
                    x: cfc.data.map(p => p[0]),
                    y: cfc.data.map(p => p[1]),
                    mode: 'lines',
                    name: cfcName,
                    line: {
                        color: cfc.color,
                        width: cfcName === selectedCfc ? 4 : 2
                    }
                });
            }
        } else {
            const cfc = cfcData[selectedCfc];
            cfcTraces.push({
                x: cfc.data.map(p => p[0]),
                y: cfc.data.map(p => p[1]),
                mode: 'lines',
                name: selectedCfc,
                line: { color: cfc.color, width: 2 }
            });
        }

        // Add marker for the current year
        const currentCfcConcentration = interpolateCfc(selectedCfc, currentYear);
        cfcTraces.push({
            x: [currentYear],
            y: [currentCfcConcentration],
            mode: 'markers',
            marker: { color: 'red', size: 8 }
        });

        Plotly.react(plotCfcDiv, cfcTraces);

        // --- 2. Update Ozone Concentration Plot ---
        const k = 0.0003; // Rate constant scaling factor
        const initialOzoneInv = 1;
        const pseudoRateConstant = k * currentCfcConcentration;

        const ozoneX = [];
        const ozoneY = [];
        for (let t = 0; t <= currentTime; t++) {
            ozoneX.push(t);
            ozoneY.push(initialOzoneInv + pseudoRateConstant * t);
        }

        const ozoneTrace = {
            x: ozoneX,
            y: ozoneY,
            mode: 'lines',
            line: { color: 'blue' }
        };

        Plotly.react(plotOzoneDiv, [ozoneTrace]);

        // --- 3. Update 3D Scene ---
        const maxCfc = 600; // A value slightly above the max in data
        const opacity = Math.max(0.1, 1 - (currentCfcConcentration / maxCfc));
        if (ozoneLayer) {
            ozoneLayer.material.opacity = opacity;
        }
    }

    // --- INITIALIZATION & EVENT LISTENERS ---
    function init() {
        // Populate dropdown
        Object.keys(cfcData).forEach(cfcName => {
            const option = document.createElement('option');
            option.value = cfcName;
            option.textContent = cfcName;
            selectCfc.appendChild(option);
        });
        selectCfc.value = 'CFC-12'; // Default

        // Set up event listeners
        selectCfc.addEventListener('change', update);
        showAllCheckbox.addEventListener('change', update);
        yearSlider.addEventListener('input', update);
        timeSlider.addEventListener('input', update);

        // Initial render
        initPlots();
        initThree();
        update();
    }

    init();
});
</script>

</body>
</html>
