</head>
<body>

    <h1>Eratosthenes's Measure of the Earth's Circumference</h1>
    <p>
        This visualization demonstrates the method used by Eratosthenes to estimate the Earth's circumference. By measuring the angle of the sun's shadow at Alexandria at the same time the sun was directly overhead at Syene, he could calculate the angle between the two cities relative to the Earth's center.
    </p>

    <div id="control-panel">
        <label for="slider-post-angle">post angle</label>
        <input type="range" id="slider-post-angle" min="1" max="60" value="7" step="0.1">
        <span id="display-post-angle"></span>
        <label for="checkbox-zoom">zoom</label>
        <input type="checkbox" id="checkbox-zoom">
    </div>

    <div id="visualization-container">
        <!-- p5.js canvas will be inserted here -->
    </div>

    <script>
        let angle;
        let isZoomed;

        let slider, checkbox, angleDisplay;

        function setup() {
            let canvas = createCanvas(600, 450);
            canvas.parent('visualization-container');
            canvas.id('demo-canvas');
            angleMode(DEGREES);

            slider = select('#slider-post-angle');
            checkbox = select('#checkbox-zoom');
            angleDisplay = select('#display-post-angle');

            angle = parseFloat(slider.value());
            isZoomed = checkbox.checked();
            angleDisplay.html(angle.toFixed(1));

            slider.input(() => {
                angle = parseFloat(slider.value());
                angleDisplay.html(angle.toFixed(1));
            });

            checkbox.changed(() => {
                isZoomed = checkbox.checked();
            });
        }

        function draw() {
            background('#F0F0F0');
            drawSunRays();

            if (isZoomed) {
                drawZoomedView();
            } else {
                drawFullView();
            }
        }

        function setLineDash(list) {
            drawingContext.setLineDash(list);
        }

        function drawSunRays() {
            stroke('#DAA520');
            strokeWeight(2);
            for (let i = 0; i < 8; i++) {
                let y = 50 + i * 50;
                line(width, y, width - 150, y);
                // Arrowhead
                line(width - 150, y, width - 150 + 10, y - 5);
                line(width - 150, y, width - 150 + 10, y + 5);
            }
        }

        function drawFullView() {
            let earthCenter = createVector(250, 225);
            let earthRadius = 150;

            // Earth body
            fill('#468296');
            noStroke();
            ellipse(earthCenter.x, earthCenter.y, earthRadius * 2);

            // "Earth" text
            fill(255);
            textSize(24);
            textAlign(CENTER, CENTER);
            text('Earth', earthCenter.x, earthCenter.y);

            // --- Syene ---
            let syenePoint = createVector(earthCenter.x + earthRadius, earthCenter.y);
            stroke(0);
            strokeWeight(1.5);
            setLineDash([5, 5]);
            line(earthCenter.x, earthCenter.y, syenePoint.x, syenePoint.y);

            fill(0);
            noStroke();
            textAlign(LEFT, CENTER);
            textSize(12);
            text('Syene', syenePoint.x + 10, syenePoint.y);

            // --- Alexandria ---
            let alexandriaAngle = -angle;
            let alexandriaPoint = createVector(
                earthCenter.x + earthRadius * cos(alexandriaAngle),
                earthCenter.y + earthRadius * sin(alexandriaAngle)
            );

            // Alexandria radial line
            stroke(0);
            strokeWeight(1.5);
            line(earthCenter.x, earthCenter.y, alexandriaPoint.x, alexandriaPoint.y);

            // Horizontal line at Alexandria
            line(alexandriaPoint.x - 50, alexandriaPoint.y, alexandriaPoint.x + 80, alexandriaPoint.y);
            setLineDash([]);

            fill(0);
            noStroke();
            text('Alexandria', alexandriaPoint.x + 10, alexandriaPoint.y - 15);

            // Angle visualization at Alexandria
            push();
            translate(alexandriaPoint.x, alexandriaPoint.y);

            // Arrowhead on surface representing the post
            fill(0);
            stroke(0);
            strokeWeight(1);
            rotate(alexandriaAngle);
            beginShape();
            vertex(0, 0);
            vertex(15, -5);
            vertex(15, 5);
            endShape(CLOSE);

            // Angle arc
            noFill();
            stroke(0);
            arc(0, 0, 40, 40, 180, 180 + angle);

            // Angle text
            let textAngle = 180 + angle / 2;
            noStroke();
            fill(0);
            textAlign(CENTER, CENTER);
            textSize(11);
            text(`${angle.toFixed(0)}°`, 28 * cos(textAngle), 28 * sin(textAngle));
            pop();
        }

        function drawZoomedView() {
            let sectorCenter = createVector(150, 450);
            let sectorRadius = 450;

            // Earth Sector
            fill('#468296');
            noStroke();
            arc(sectorCenter.x, sectorCenter.y, sectorRadius * 2, sectorRadius * 2, -angle, 0, PIE);

            // "Earth" text
            fill(255);
            textSize(24);
            textAlign(CENTER, CENTER);
            text('Earth', sectorCenter.x + 50, sectorCenter.y - 80);

            let alexandriaPointOnArc = createVector(
                sectorCenter.x + sectorRadius * cos(-angle),
                sectorCenter.y + sectorRadius * sin(-angle)
            );

            // Radii
            stroke(0);
            strokeWeight(1.5);
            setLineDash([5, 5]);
            line(sectorCenter.x, sectorCenter.y, sectorCenter.x + sectorRadius, sectorCenter.y); // Syene radius
            line(sectorCenter.x, sectorCenter.y, alexandriaPointOnArc.x, alexandriaPointOnArc.y); // Alexandria radius
            setLineDash([]);

            // Labels as seen in snapshot
            fill(0);
            noStroke();
            textSize(12);
            textAlign(LEFT, CENTER);
            let labelX = 450;
            let syeneY = sectorCenter.y;
            ellipse(labelX, syeneY, 4, 4);
            text('Syene', labelX + 10, syeneY);
            let alexandriaY = syeneY - 30;
            ellipse(labelX, alexandriaY, 4, 4);
            text('Alexandria', labelX + 10, alexandriaY);

            // Central Angle Visualization
            noFill();
            stroke(0);
            strokeWeight(1);
            arc(sectorCenter.x, sectorCenter.y, 150, 150, -angle, 0);

            // Central Angle Text
            let textAngle = -angle / 2;
            let textR = 95;
            fill(0);
            noStroke();
            textAlign(CENTER, CENTER);
            textSize(14);
            text(`${angle.toFixed(0)}°`, sectorCenter.x + textR * cos(textAngle), sectorCenter.y + textR * sin(textAngle));

            // Post/Shadow triangle at Alexandria
            let v1 = alexandriaPointOnArc; // Vertex at arc
            let v2 = p5.Vector.fromAngle(radians(180 - angle), 40).add(v1); // Vertex along radius
            let v3 = p5.Vector.fromAngle(radians(180), 40).add(v1); // Vertex along horizontal

            fill('#D2B48C'); // Tan color
            noStroke();
            triangle(v1.x, v1.y, v2.x, v2.y, v3.x, v3.y);
        }
    </script>
</body>
</html>
