        const EARTH_RADIUS = 180;
        const BASE_WATER_RADIUS = EARTH_RADIUS + 5;
        const PULL_SCALING_FACTOR = 0.25; // Scales pull strength to pixel displacement

        function setup() {
            let canvas = createCanvas(CANVAS_SIZE, CANVAS_SIZE);
            canvas.parent('viz-container');
            angleMode(DEGREES); // Use degrees for cos/sin to match sliders
            noLoop(); // Drawing is triggered by user input only

            // Select DOM elements using p5.js's select function
            sliderMoonPos = select('#slider-moon-pos');
            sliderMoonPull = select('#slider-moon-pull');
            sliderSunPos = select('#slider-sun-pos');
            sliderSunPull = select('#slider-sun-pull');
            btnReset = select('#btn-reset');

            // Set initial slider values from HTML (redundant but good practice)
            // Note: HTML 'value' attribute already sets the initial state.

            // Add event listeners to trigger redraw
            sliderMoonPos.input(redraw);
            sliderMoonPull.input(redraw);
            sliderSunPos.input(redraw);
            sliderSunPull.input(redraw);
            btnReset.mousePressed(resetControlsAndRedraw);

            redraw(); // Draw the initial state
        }

        function draw() {
            background(255); // Clear canvas with white
            translate(width / 2, height / 2); // Move origin to the center

            // Get current values from the sliders
            let moonAngle = sliderMoonPos.value();
            let moonPull = sliderMoonPull.value();
            let sunAngle = sliderSunPos.value();
            let sunPull = sliderSunPull.value();

            // --- 1. Draw the water layer ---
            fill('#aed6f1'); // Light blue for water
            stroke(0);        // Black outline
            strokeWeight(1);

            beginShape();
            // Iterate 360 degrees to draw the water's perimeter
            for (let a = 0; a <= 360; a += 2) { 
                // The cos^2 function creates two bulges (high tides) on opposite sides
                let moonEffect = moonPull * pow(cos(a - moonAngle), 2);
                let sunEffect = sunPull * pow(cos(a - sunAngle), 2);

                // Calculate the final radius of the water at this angle
                let totalRadius = BASE_WATER_RADIUS + (moonEffect + sunEffect) * PULL_SCALING_FACTOR;

                // Convert polar coordinates (radius, angle) to Cartesian (x, y)
                let x = totalRadius * cos(a);
                let y = totalRadius * sin(a);
                vertex(x, y);
            }
            endShape(CLOSE);

            // --- 2. Draw the Earth on top ---
            noStroke();
            fill('#966F33'); // Brown for Earth
            ellipse(0, 0, EARTH_RADIUS * 2); // Draw a circle for the Earth
        }

        // Resets sliders to their default values
        function resetControls() {
            sliderMoonPos.value(DEFAULTS.moonPos);
            sliderMoonPull.value(DEFAULTS.moonPull);
            sliderSunPos.value(DEFAULTS.sunPos);
            sliderSunPull.value(DEFAULTS.sunPull);
        }

        // Resets sliders and then triggers a redraw
        function resetControlsAndRedraw() {
            resetControls();
            redraw();
        }

    </script>
</body>
</html>
