<body>

    <div id="app-container">
        <div id="controls-panel">
            <div class="control-group">
                <label for="slider-earth-position">Earth position</label>
                <input type="range" id="slider-earth-position" min="0" max="360" step="1" value="180">
            </div>
            <div class="control-group">
                <label for="slider-comet-position">comet position</label>
                <input type="range" id="slider-comet-position" min="0" max="360" step="1" value="180">
            </div>
            <div class="control-group">
                <label for="slider-coma">coma</label>
                <input type="range" id="slider-coma" min="0" max="100" step="1" value="20">
            </div>
            <div class="control-group">
                <label for="slider-tail">tail</label>
                <input type="range" id="slider-tail" min="0" max="100" step="1" value="50">
            </div>
            <div class="control-group">
                <label for="slider-tail-spread">tail spread</label>
                <input type="range" id="slider-tail-spread" min="0" max="100" step="1" value="20">
            </div>
        </div>
        <div id="visualization-panel">
            <div id="telescopic-view-wrapper">
                <canvas id="telescopic-canvas"></canvas>
                <p>telescopic view</p>
            </div>
            <div id="orbit-view-wrapper">
                <canvas id="orbit-canvas"></canvas>
                <p>orbit diagram</p>
            </div>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>

    <script>
        // --- Global Variables ---
        let sceneOrbit, cameraOrbit, rendererOrbit;
        let sceneTelescopic, cameraTelescopic, rendererTelescopic;

        let sun, earth, cometModel;
        let nucleus, coma, tail;

        const orbitParams = {
            earth: { a: 4, e: 0.0167, color: 0x0000ff },
            mars: { a: 6.1, e: 0.0934, color: 0xff4500 },
            jupiter: { a: 20.8, e: 0.0489, color: 0xffa500 },
            holmes: { a: 14.4, e: 0.432, color: 0xffff00 }
        };

        let earthCurve, holmesCurve;

        const sliders = {
            earthPosition: document.getElementById('slider-earth-position'),
            cometPosition: document.getElementById('slider-comet-position'),
            coma: document.getElementById('slider-coma'),
            tail: document.getElementById('slider-tail'),
            tailSpread: document.getElementById('slider-tail-spread')
        };

        // --- Initialization ---
        function init() {
            // Setup Orbit Diagram
            const orbitCanvas = document.getElementById('orbit-canvas');
            const orbitWrapper = document.getElementById('orbit-view-wrapper');
            sceneOrbit = new THREE.Scene();
            sceneOrbit.background = new THREE.Color(0xffffff);
            cameraOrbit = new THREE.PerspectiveCamera(45, orbitWrapper.clientWidth / orbitWrapper.clientHeight, 0.1, 1000);
            cameraOrbit.position.set(0, 35, 30);
            cameraOrbit.lookAt(0, 0, 0);
            rendererOrbit = new THREE.WebGLRenderer({ canvas: orbitCanvas, antialias: true });
            rendererOrbit.setSize(orbitWrapper.clientWidth, orbitWrapper.clientHeight);

            // Setup Telescopic View
            const telescopicCanvas = document.getElementById('telescopic-canvas');
            const telescopicWrapper = document.getElementById('telescopic-view-wrapper');
            sceneTelescopic = new THREE.Scene();
            sceneTelescopic.background = new THREE.Color(0x000000);
            cameraTelescopic = new THREE.PerspectiveCamera(15, telescopicWrapper.clientWidth / telescopicWrapper.clientHeight, 0.1, 1000);
            rendererTelescopic = new THREE.WebGLRenderer({ canvas: telescopicCanvas, antialias: true });
            rendererTelescopic.setSize(telescopicWrapper.clientWidth, telescopicWrapper.clientHeight);

            // --- Create Scene Contents ---
            // Lights
            const ambientLightOrbit = new THREE.AmbientLight(0xffffff, 0.5);
            sceneOrbit.add(ambientLightOrbit);
            const ambientLightTelescopic = new THREE.AmbientLight(0xffffff, 1.0);
            sceneTelescopic.add(ambientLightTelescopic);

            // Sun (in orbit scene)
            sun = new THREE.Mesh(
                new THREE.SphereGeometry(0.5, 32, 32),
                new THREE.MeshBasicMaterial({ color: 0xffff00, emissive: 0xffff00 })
            );
            const pointLight = new THREE.PointLight(0xffffff, 1.5, 200);
            sun.add(pointLight);
            sceneOrbit.add(sun);

            // Orbits
            earthCurve = createOrbit(orbitParams.earth);
            holmesCurve = createOrbit(orbitParams.holmes);
            sceneOrbit.add(earthCurve.line);
            sceneOrbit.add(holmesCurve.line);
            sceneOrbit.add(createOrbit(orbitParams.mars).line);
            sceneOrbit.add(createOrbit(orbitParams.jupiter).line);

            // Celestial Bodies
            earth = new THREE.Mesh(
                new THREE.SphereGeometry(0.2, 16, 16),
                new THREE.MeshBasicMaterial({ color: orbitParams.earth.color })
            );
            sceneOrbit.add(earth);

            // Comet Model (a group containing nucleus, coma, and tail)
            cometModel = new THREE.Group();

            nucleus = new THREE.Mesh(
                new THREE.SphereGeometry(0.1, 16, 16),
                new THREE.MeshBasicMaterial({ color: 0xffffff })
            );
            cometModel.add(nucleus);

            coma = new THREE.Mesh(
                new THREE.SphereGeometry(0.5, 32, 32),
                new THREE.MeshBasicMaterial({ color: 0xb3ffb3, transparent: true, opacity: 0.5, blending: THREE.AdditiveBlending })
            );
            cometModel.add(coma);

            const tailGeom = new THREE.ConeGeometry(1, 1, 32);
            tailGeom.translate(0, 0.5, 0); // Move base to origin
            tailGeom.rotateX(Math.PI / 2); // Align with Z-axis initially
            tail = new THREE.Mesh(
                tailGeom,
                new THREE.MeshBasicMaterial({ color: 0xb3ffb3, transparent: true, opacity: 0.4, blending: THREE.AdditiveBlending, side: THREE.DoubleSide })
            );
            cometModel.add(tail);

            sceneOrbit.add(cometModel);
            sceneTelescopic.add(cometModel); // Add same model to telescopic scene

            // --- Event Listeners ---
            for (const key in sliders) {
                sliders[key].addEventListener('input', update);
            }
            window.addEventListener('resize', onWindowResize, false);

            // Initial state
            update();
            animate();
        }

        // --- Helper to Create Orbit Lines ---
        function createOrbit(params) {
            const focusOffset = params.a * params.e;
            const semiMinorAxis = params.a * Math.sqrt(1 - params.e * params.e);

            const curve = new THREE.EllipseCurve(
                -focusOffset, 0, // ax, aY
                params.a, semiMinorAxis, // xRadius, yRadius
                0, 2 * Math.PI,  // aStartAngle, aEndAngle
                false, 0 // aClockwise, aRotation
            );

            const points = curve.getPoints(100);
            const geometry = new THREE.BufferGeometry().setFromPoints(points);
            const material = new THREE.LineBasicMaterial({ color: params.color });
            const ellipseLine = new THREE.Line(geometry, material);
            ellipseLine.rotation.x = Math.PI / 2;
            return { curve, line: ellipseLine };
        }

        // --- Update Function (called by sliders) ---
        function update() {
            // Earth Position
            const earthAngle = sliders.earthPosition.value / 360;
            const earthPos2D = earthCurve.curve.getPointAt(earthAngle);
            earth.position.set(earthPos2D.x, 0, earthPos2D.y);

            // Comet Position
            const cometAngle = sliders.cometPosition.value / 360;
            const cometPos2D = holmesCurve.curve.getPointAt(cometAngle);
            cometModel.position.set(cometPos2D.x, 0, cometPos2D.y);

            // Coma Size
            const comaValue = parseFloat(sliders.coma.value);
            const comaScale = 0.5 + (comaValue / 100) * 8;
            coma.scale.set(comaScale, comaScale, comaScale);
            coma.material.opacity = 0.2 + (comaValue/100)*0.4;


            // Tail Length and Spread
            const tailLengthValue = parseFloat(sliders.tail.value);
            const tailSpreadValue = parseFloat(sliders.tailSpread.value);
            const tailLength = (tailLengthValue / 100) * 15;
            const tailSpread = 0.5 + (tailSpreadValue / 100) * 4;
            tail.scale.set(tailSpread, tailSpread, tailLength);
            tail.visible = tailLengthValue > 0;

            // Tail Orientation (always points away from the sun)
            if (cometModel.position.length() > 0.01) {
                // We create a temporary point in the direction *away* from the comet towards the sun
                // and make the tail look at it.
                const awayFromSun = cometModel.position.clone().multiplyScalar(2);
                tail.lookAt(awayFromSun);
            }

            // Telescopic Camera Update
            cameraTelescopic.position.copy(earth.position);
            cameraTelescopic.lookAt(cometModel.position);
        }

        // --- Animation Loop ---
        function animate() {
            requestAnimationFrame(animate);
            rendererOrbit.render(sceneOrbit, cameraOrbit);
            rendererTelescopic.render(sceneTelescopic, cameraTelescopic);
        }

        // --- Handle Window Resize ---
        function onWindowResize() {
            const orbitWrapper = document.getElementById('orbit-view-wrapper');
            cameraOrbit.aspect = orbitWrapper.clientWidth / orbitWrapper.clientHeight;
            cameraOrbit.updateProjectionMatrix();
            rendererOrbit.setSize(orbitWrapper.clientWidth, orbitWrapper.clientHeight);

            const telescopicWrapper = document.getElementById('telescopic-view-wrapper');
            cameraTelescopic.aspect = telescopicWrapper.clientWidth / telescopicWrapper.clientHeight;
            cameraTelescopic.updateProjectionMatrix();
            rendererTelescopic.setSize(telescopicWrapper.clientWidth, telescopicWrapper.clientHeight);
        }

        // --- Start the application ---
        init();

    </script>
</body>
</html>
