        }

        .slider-group label {
            width: 20px;
            font-weight: bold;
            text-align: center;
        }

        .slider-group input[type="range"] {
            flex-grow: 1;
            margin: 0;
        }

        .slider-group span {
            width: 50px;
            text-align: right;
            font-family: "Courier New", Courier, monospace;
        }

        #visualization-area {
            border: 1px solid #ccc;
            width: 800px;
            height: 500px;
            background-color: white;
        }

