            display: block; /* remove any default bottom margin */
        }
    </style>
</head>
<body>
    <div id="app-container">
        <div id="control-panel">
            <!-- Mass Slider -->
            <div class="slider-group">
                <label for="slider-m">m</label>
                <input type="range" id="slider-m" min="0" max="10" value="2.845" step="0.001">
                <span id="label-m">2.845</span>
            </div>
            <!-- Horizontal Position Slider -->
            <div class="slider-group">
                <label for="slider-x">x</label>
                <input type="range" id="slider-x" min="0" max="10" value="4.1" step="0.1">
                <span id="label-x">4.1</span>
            </div>
            <!-- Vertical Position Slider -->
            <div class="slider-group">
                <label for="slider-y">y</label>
                <input type="range" id="slider-y" min="0.1" max="5" value="1.498" step="0.001">
                <span id="label-y">1.498</span>
            </div>
        </div>
        <div id="visualization-area">
            <!-- p5.js canvas will be inserted here -->
        </div>
    </div>

    <script>
        let sliderM, sliderX, sliderY;
        let labelM, labelX, labelY;

        function setup() {
            const canvas = createCanvas(800, 500);
            canvas.parent('visualization-area');

            // Get references to HTML elements
            sliderM = select('#slider-m');
            sliderX = select('#slider-x');
            sliderY = select('#slider-y');

            labelM = select('#label-m');
            labelX = select('#label-x');
            labelY = select('#label-y');

            // Attach input event listeners
            sliderM.input(() => labelM.html(parseFloat(sliderM.value()).toFixed(3)));
            sliderX.input(() => labelX.html(parseFloat(sliderX.value()).toFixed(1)));
            sliderY.input(() => labelY.html(parseFloat(sliderY.value()).toFixed(3)));

            // Set initial label values from HTML attributes
            labelM.html(parseFloat(sliderM.value()).toFixed(3));
            labelX.html(parseFloat(sliderX.value()).toFixed(1));
            labelY.html(parseFloat(sliderY.value()).toFixed(3));

            angleMode(RADIANS);
        }

        function draw() {
            background(255);

            // --- 1. Get Input Values ---
            const m = float(sliderM.value());
            const x = float(sliderX.value());
            const y = float(sliderY.value());

            // --- 2. Define Constants and Geometry ---
            const L = 10.0; // Logical distance between posts
            const g = 9.81; // Acceleration due to gravity

            // Canvas coordinates for anchor points
            const anchor1 = createVector(100, 150);
            const anchor2 = createVector(700, 150);
            const canvasWidth = anchor2.x - anchor1.x;

            // Scaling factor for y-coordinate
            const yScale = 50;

            // Calculate mass position in pixel coordinates
            const massPos = createVector(
                anchor1.x + (x / L) * canvasWidth,
                anchor1.y + y * yScale
            );

            // --- 3. Physics Calculations ---
            let theta1, theta2;

            // Handle edge cases for x to avoid division by zero in atan
            if (x > 1e-6) {
                theta1 = atan(y / x);
            } else {
                theta1 = HALF_PI; // 90 degrees if directly under left anchor
            }

            if (L - x > 1e-6) {
                theta2 = atan(y / (L - x));
            } else {
                theta2 = HALF_PI; // 90 degrees if directly under right anchor
            }

            const Fg = m * g;
            let T1 = 0, T2 = 0;

            const sinSum = sin(theta1 + theta2);
            if (abs(sinSum) > 1e-9) { // Avoid division by zero
                T1 = (Fg * cos(theta2)) / sinSum;
                T2 = (Fg * cos(theta1)) / sinSum;
            } else {
                // This unstable state is avoided by slider constraints (y > 0)
                T1 = Infinity;
                T2 = Infinity;
            }

            // --- 4. Drawing ---

            // Draw posts
            strokeWeight(15);
            stroke(0);
            line(anchor1.x, anchor1.y, anchor1.x, height - 50);
            line(anchor2.x, anchor2.y, anchor2.x, height - 50);

            // Draw horizontal dashed line
            drawDashedLine(anchor1, anchor2, 10);

            // Draw ropes
            strokeWeight(2);
            stroke(0);
            line(anchor1.x, anchor1.y, massPos.x, massPos.y);
            line(anchor2.x, anchor2.y, massPos.x, massPos.y);

            // Draw mass
            noStroke();
            fill(0);
            ellipse(massPos.x, massPos.y, 40, 40);

            // Draw angles
            noFill();
            stroke(0);
            strokeWeight(1);
            if (theta1 > 0) arc(anchor1.x, anchor1.y, 60, 60, 0, theta1);
            if (theta2 > 0) arc(anchor2.x, anchor2.y, 60, 60, PI - theta2, PI);

            // --- 5. Labels and Values ---
            fill(0);
            noStroke();
            textAlign(CENTER, CENTER);

            // Tension values display
            textSize(16);
            text(`T\u2081 = ${T1.toFixed(4)} N  |  T\u2082 = ${T2.toFixed(4)} N`, width / 2, 40);

            // Angle labels
            textSize(14);
            // Using polar coordinates to place labels correctly along the arc
            text('\u03B8\u2081', anchor1.x + 40 * cos(theta1 / 2), anchor1.y + 40 * sin(theta1 / 2));
            text('\u03B8\u2082', anchor2.x - 40 * cos(theta2 / 2), anchor2.y + 40 * sin(theta2 / 2));

            // Tension labels
            const mid1 = p5.Vector.lerp(anchor1, massPos, 0.5);
            const mid2 = p5.Vector.lerp(anchor2, massPos, 0.5);
            // Add a small offset perpendicular to the rope for better placement
            const perp1 = createVector(-(massPos.y - anchor1.y), massPos.x - anchor1.x).normalize().mult(15);
            const perp2 = createVector(-(anchor2.y - massPos.y), anchor2.x - massPos.x).normalize().mult(15);
            text('T\u2081', mid1.x + perp1.x, mid1.y + perp1.y);
            text('T\u2082', mid2.x + perp2.x, mid2.y + perp2.y);
        }

        // Helper function to draw a dashed line
        function drawDashedLine(p1, p2, dashLength) {
            push();
            strokeWeight(1.5);
            stroke(100);
            drawingContext.setLineDash([dashLength, dashLength / 2]);
            line(p1.x, p1.y, p2.x, p2.y);
            pop();
        }
    </script>
</body>
</html>
