<script>
window.addEventListener('DOMContentLoaded', () => {

    // --- STATE VARIABLES ---
    let time = 0;
    let animationFrameId = null;
    let isRunning = false;
    let lastFrameTime = performance.now();
    const timeEnd = 10;

    let getDisplacement, getVelocity;

    // --- DOM ELEMENT REFERENCES ---
    const sliders = {
        displacement: document.getElementById('slider-displacement'),
        velocity: document.getElementById('slider-velocity'),
        stiffness: document.getElementById('slider-stiffness'),
        mass: document.getElementById('slider-mass'),
        damping: document.getElementById('slider-damping'),
        proportionalGain: document.getElementById('slider-proportional-gain'),
        derivativeGain: document.getElementById('slider-derivative-gain'),
    };

    const values = {
        displacement: document.getElementById('value-displacement'),
        velocity: document.getElementById('value-velocity'),
        stiffness: document.getElementById('value-stiffness'),
        mass: document.getElementById('value-mass'),
        damping: document.getElementById('value-damping'),
        proportionalGain: document.getElementById('value-proportional-gain'),
        derivativeGain: document.getElementById('value-derivative-gain'),
    };

    const buttons = {
        play: document.getElementById('btn-play'),
        pause: document.getElementById('btn-pause'),
        reset: document.getElementById('btn-reset'),
    };

    // --- THREE.JS SETUP ---
    const sceneContainer = document.getElementById('scene-container');
    let scene, camera, renderer, massGroup, spring, damperPiston, wall;
    const springRestLength = 1.5;
    const massSize = 1;

    function initThreeScene() {
        scene = new THREE.Scene();
        scene.background = new THREE.Color(0xffffff);

        const aspect = sceneContainer.clientWidth / sceneContainer.clientHeight;
        camera = new THREE.PerspectiveCamera(50, aspect, 0.1, 1000);
        camera.position.set(4, 3, 6);
        camera.lookAt(0, 0.5, 0);

        renderer = new THREE.WebGLRenderer({ antialias: true });
        renderer.setSize(sceneContainer.clientWidth, sceneContainer.clientHeight);
        renderer.setPixelRatio(window.devicePixelRatio);
        sceneContainer.appendChild(renderer.domElement);

        // Lighting
        const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
        scene.add(ambientLight);
        const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
        directionalLight.position.set(5, 10, 7.5);
        scene.add(directionalLight);

        // Geometries & Materials
        const baseMat = new THREE.MeshStandardMaterial({ color: 0x228B22 });
        const massMat = new THREE.MeshStandardMaterial({ color: 0xFFFF00, metalness: 0.3, roughness: 0.4 });
        const springMat = new THREE.MeshStandardMaterial({ color: 0xFF0000, metalness: 0.5, roughness: 0.5 });
        const damperMat = new THREE.MeshStandardMaterial({ color: 0x0000FF, metalness: 0.5, roughness: 0.5 });
        const wireframeMat = new THREE.MeshBasicMaterial({ color: 0xaaaaaa, wireframe: true, transparent: true, opacity: 0.5 });

        // Base
        const baseGeo = new THREE.BoxGeometry(7, 0.2, 3);
        const base = new THREE.Mesh(baseGeo, baseMat);
        base.position.y = -0.1;
        scene.add(base);

        // Wall (for spring/damper attachment)
        const wallGeo = new THREE.BoxGeometry(0.2, 2, 2);
        const wallMat = new THREE.MeshStandardMaterial({ color: 0x888888 });
        wall = new THREE.Mesh(wallGeo, wallMat);
        wall.position.set(-springRestLength - massSize / 2, 1, 0);
        scene.add(wall);

        // Group for moving parts
        massGroup = new THREE.Group();
        scene.add(massGroup);
        massGroup.position.y = massSize/2;

        // Mass
        const massGeo = new THREE.BoxGeometry(massSize, massSize, massSize);
        const mass = new THREE.Mesh(massGeo, massMat);
        massGroup.add(mass);

        // Spring
        class HelixCurve extends THREE.Curve {
            constructor(radius, length, coils) {
                super();
                this.radius = radius;
                this.length = length;
                this.coils = coils;
            }
            getPoint(t) {
                const theta = 2 * Math.PI * this.coils * t;
                const x = this.length * t;
                const y = this.radius * Math.cos(theta);
                const z = this.radius * Math.sin(theta);
                return new THREE.Vector3(x, y, z);
            }
        }
        const springPath = new HelixCurve(0.25, springRestLength, 10);
        const springGeo = new THREE.TubeGeometry(springPath, 64, 0.05, 8, false);
        spring = new THREE.Mesh(springGeo, springMat);
        spring.position.x = -springRestLength;
        mass.add(spring); // Attach spring to the mass

        // Damper
        const damperCylinderGeo = new THREE.CylinderGeometry(0.15, 0.15, 1, 16);
        const damperCylinder = new THREE.Mesh(damperCylinderGeo, damperMat);
        damperCylinder.rotation.z = Math.PI / 2;
        damperCylinder.position.set(-0.5, 0.6, 0); // Relative to wall
        wall.add(damperCylinder);

        const damperPistonGeo = new THREE.CylinderGeometry(0.05, 0.05, 1.5, 16);
        damperPiston = new THREE.Mesh(damperPistonGeo, damperMat);
        damperPiston.rotation.z = Math.PI / 2;
        damperPiston.position.set(0, 0.6, 0); // Moves with mass
        massGroup.add(damperPiston);

        // Wireframe Enclosure
        const enclosureGeo = new THREE.BoxGeometry(7, 3, 3.2);
        const enclosure = new THREE.Mesh(enclosureGeo, wireframeMat);
        enclosure.position.y = 1.4;
