        // Handle Resize
        window.addEventListener('resize', onWindowResize, false);
    }

    function onWindowResize() {
        const aspect = sceneContainer.clientWidth / sceneContainer.clientHeight;
        camera.aspect = aspect;
        camera.updateProjectionMatrix();
        renderer.setSize(sceneContainer.clientWidth, sceneContainer.clientHeight);
    }

    // --- PLOTLY.JS SETUP ---
    function initPlots() {
        const layout = {
            xaxis: { title: 'Time (s)', range: [0, timeEnd] },
            yaxis: { range: [-3.5, 3.5] },
            margin: { l: 50, r: 20, b: 40, t: 20 },
            showlegend: false
        };

        const displacementLayout = { ...layout, yaxis: { ...layout.yaxis, title: 'Displacement (m)' } };
        Plotly.newPlot('plot-displacement', [{ x: [], y: [], mode: 'lines' }, { x: [], y: [], mode: 'markers', marker: { color: 'blue', size: 8 } }], displacementLayout, {staticPlot: true});

        const velocityLayout = { ...layout, yaxis: { ...layout.yaxis, title: 'Velocity (m/s)' } };
        Plotly.newPlot('plot-velocity', [{ x: [], y: [], mode: 'lines' }, { x: [], y: [], mode: 'markers', marker: { color: 'green', size: 8 } }], velocityLayout, {staticPlot: true});
    }

    // --- PHYSICS CALCULATION ---
    function calculateSolution(params) {
        const { x0, v0, m, k, c, Kp, Kd } = params;
        const k_eff = k + Kp;
        const c_eff = c + Kd;

        if (m <= 0) return { getX: t => x0, getV: t => 0 };
        if (k_eff === 0 && c_eff === 0) {
            return { getX: t => v0 * t + x0, getV: t => v0 };
        }

        const omega_n = Math.sqrt(k_eff / m);
        const zeta = c_eff / (2 * Math.sqrt(k_eff * m));

        let xFunc, vFunc;

        if (zeta < 1) { // Underdamped
            const omega_d = omega_n * Math.sqrt(1 - zeta * zeta);
            const A = x0;
            const B = (v0 + zeta * omega_n * x0) / omega_d;
            xFunc = t => Math.exp(-zeta * omega_n * t) * (A * Math.cos(omega_d * t) + B * Math.sin(omega_d * t));
            vFunc = t => -zeta * omega_n * xFunc(t) + Math.exp(-zeta * omega_n * t) * (-A * omega_d * Math.sin(omega_d * t) + B * omega_d * Math.cos(omega_d * t));
        } else if (zeta === 1) { // Critically damped
            const A = x0;
            const B = v0 + omega_n * x0;
            xFunc = t => (A + B * t) * Math.exp(-omega_n * t);
            vFunc = t => (B - omega_n * (A + B * t)) * Math.exp(-omega_n * t);
        } else { // Overdamped
            const r1 = -zeta * omega_n + omega_n * Math.sqrt(zeta * zeta - 1);
            const r2 = -zeta * omega_n - omega_n * Math.sqrt(zeta * zeta - 1);
            const A = (v0 - x0 * r2) / (r1 - r2);
            const B = x0 - A;
            xFunc = t => A * Math.exp(r1 * t) + B * Math.exp(r2 * t);
            vFunc = t => A * r1 * Math.exp(r1 * t) + B * r2 * Math.exp(r2 * t);
        }

        return { getX: xFunc, getV: vFunc };
    }

    // --- MAIN UPDATE & ANIMATION LOGIC ---
    function updateSystem() {
        if (animationFrameId) {
            cancelAnimationFrame(animationFrameId);
            animationFrameId = null;
        }
        isRunning = false;
        time = 0;

        const params = {
            x0: parseFloat(sliders.displacement.value),
            v0: parseFloat(sliders.velocity.value),
            m: parseFloat(sliders.mass.value),
            k: parseFloat(sliders.stiffness.value),
            c: parseFloat(sliders.damping.value),
            Kp: parseFloat(sliders.proportionalGain.value),
            Kd: parseFloat(sliders.derivativeGain.value)
        };

        const solution = calculateSolution(params);
        getDisplacement = solution.getX;
        getVelocity = solution.getV;

        // Generate plot data
        const t_data = [];
        const x_data = [];
        const v_data = [];
        for (let i = 0; i <= 200; i++) {
            const t = (i / 200) * timeEnd;
            t_data.push(t);
            x_data.push(getDisplacement(t));
            v_data.push(getVelocity(t));
        }

        // Update plots
        Plotly.react('plot-displacement', [
            { x: t_data, y: x_data, mode: 'lines', line: { color: '#1f77b4' } },
            { x: [0], y: [params.x0], mode: 'markers', marker: { color: 'blue', size: 8 } }
        ]);
        Plotly.react('plot-velocity', [
            { x: t_data, y: v_data, mode: 'lines', line: { color: '#1f77b4' } },
            { x: [0], y: [params.v0], mode: 'markers', marker: { color: 'green', size: 8 } }
        ]);

        // Update 3D scene to initial state
        updateSceneForTime(0);
        renderer.render(scene, camera);
    }

    function updateSceneForTime(t) {
        const x_t = getDisplacement(t);

        // Update mass group position
        massGroup.position.x = x_t;

        // Update spring scale
        const currentSpringLength = springRestLength + x_t;
        spring.scale.x = currentSpringLength / springRestLength;
        spring.position.x = -currentSpringLength;

        // Update damper piston position
        damperPiston.position.x = -0.75 + x_t/2; 
        damperPiston.scale.x = (1.5 + x_t) / 1.5;
    }

    function animate() {
        animationFrameId = requestAnimationFrame(animate);

        const now = performance.now();
        const delta = (now - lastFrameTime) / 1000;
        lastFrameTime = now;

        if (isRunning) {
            time += delta;
            if (time > timeEnd) {
                time = timeEnd;
                isRunning = false;
            }

            const x_t = getDisplacement(time);
            const v_t = getVelocity(time);

            // Update 3D scene
            updateSceneForTime(time);

            // Update plot markers
            Plotly.restyle('plot-displacement', { x: [[time]], y: [[x_t]] }, [1]);
            Plotly.restyle('plot-velocity', { x: [[time]], y: [[v_t]] }, [1]);
        }

        renderer.render(scene, camera);
    }

    // --- EVENT LISTENERS ---
    function setupEventListeners() {
        Object.keys(sliders).forEach(key => {
            sliders[key].addEventListener('input', (e) => {
                const value = parseFloat(e.target.value);
                const decimals = e.target.step.includes('.') ? e.target.step.split('.')[1].length : 0;
                values[key].textContent = value.toFixed(decimals);
            });
            sliders[key].addEventListener('change', updateSystem); // 'change' is better for performance than 'input'
        });

        buttons.play.addEventListener('click', () => {
            if (!isRunning) {
                isRunning = true;
                lastFrameTime = performance.now(); // Reset timer to avoid jump
            }
        });
        buttons.pause.addEventListener('click', () => {
            isRunning = false;
        });
        buttons.reset.addEventListener('click', () => {
            isRunning = false;
            time = 0;
            const x0 = getDisplacement(0);
            const v0 = getVelocity(0);

            updateSceneForTime(0);
            Plotly.restyle('plot-displacement', { x: [[0]], y: [[x0]] }, [1]);
            Plotly.restyle('plot-velocity', { x: [[0]], y: [[v0]] }, [1]);
        });
    }

    // --- INITIALIZATION ---
    function init() {
        // Set initial slider value displays
        Object.keys(sliders).forEach(key => {
            const slider = sliders[key];
            const value = parseFloat(slider.value);
            const decimals = slider.step.includes('.') ? slider.step.split('.')[1].length : 0;
            values[key].textContent = value.toFixed(decimals);
        });

        initThreeScene();
        initPlots();
        setupEventListeners();
        updateSystem(); // Initial calculation and draw
        animate(); // Start the render loop
    }

    init();
});
</script>

</body>
</html>
