    <script>
        // --- GLOBAL STATE ---
        let isAnimating = false;
        let simTime = 0;
        let p5sketch;

        let params = {};
        let eigenSystem = {
            omega: [],
            modes: [],
            coeffs: []
        };
        const timeDomain = { start: 0, end: 5, steps: 501 };

        // --- DOM ELEMENTS ---
        const sliderTension = document.getElementById('slider-tension');
        const sliderX1 = document.getElementById('slider-x1');
        const sliderX2 = document.getElementById('slider-x2');
        const sliderX3 = document.getElementById('slider-x3');
        const sliderM1 = document.getElementById('slider-m1');
        const sliderM2 = document.getElementById('slider-m2');
        const sliderM3 = document.getElementById('slider-m3');
        const btnPlayPause = document.getElementById('btn-play-pause');
        const btnReset = document.getElementById('btn-reset');

        const allSliders = [sliderTension, sliderX1, sliderX2, sliderX3, sliderM1, sliderM2, sliderM3];

        // --- CORE LOGIC ---

        function readParameters() {
            params.T = parseFloat(sliderTension.value);
            params.x1 = parseFloat(sliderX1.value);
            params.x2 = parseFloat(sliderX2.value);
            params.x3 = parseFloat(sliderX3.value);
            params.m1 = parseFloat(sliderM1.value);
            params.m2 = parseFloat(sliderM2.value);
            params.m3 = parseFloat(sliderM3.value);
        }

        function calculatePhysics() {
            const { T, m1, m2, m3 } = params;
            const y0 = [params.x1, params.x2, params.x3];

            const M = [[m1, 0, 0], [0, m2, 0], [0, 0, m3]];
            const K = numeric.mul(T, [[2, -1, 0], [-1, 2, -1], [0, -1, 2]]);

            const M_inv_sqrt = [[1/Math.sqrt(m1), 0, 0], [0, 1/Math.sqrt(m2), 0], [0, 0, 1/Math.sqrt(m3)]];

            const C = numeric.dot(numeric.dot(M_inv_sqrt, K), M_inv_sqrt);

            const eigs = numeric.eig(C);
            const eigenvalues = eigs.lambda.x;
            const eigenvectors_z = eigs.E.x; // columns are eigenvectors

            // Eigenfrequencies
            eigenSystem.omega = eigenvalues.map(lambda => Math.sqrt(Math.max(0, lambda)));

            // Normal modes (M-orthonormal)
            eigenSystem.modes = numeric.dot(M_inv_sqrt, eigenvectors_z);

            // Modal participation factors c_i = A_i^T * M * y(0)
            const modes_T = numeric.transpose(eigenSystem.modes);
            eigenSystem.coeffs = numeric.dot(numeric.dot(modes_T, M), y0);
        }

        function updatePlots() {
            const t = numeric.linspace(timeDomain.start, timeDomain.end, timeDomain.steps);
            const y = [[], [], []];
            const v = [[], [], []];

            const { omega, modes, coeffs } = eigenSystem;

            for (let i = 0; i < t.length; i++) {
                const time = t[i];
                let current_y = [0, 0, 0];
                let current_v = [0, 0, 0];

                for (let j = 0; j < 3; j++) { // Sum over modes
                    const cos_t = Math.cos(omega[j] * time);
                    const sin_t = Math.sin(omega[j] * time);
                    for (let k = 0; k < 3; k++) { // For each mass
                        current_y[k] += coeffs[j] * modes[k][j] * cos_t;
                        current_v[k] -= coeffs[j] * modes[k][j] * omega[j] * sin_t;
                    }
                }
                y[0].push(current_y[0]);
                y[1].push(current_y[1]);
                y[2].push(current_y[2]);
                v[0].push(current_v[0]);
                v[1].push(current_v[1]);
                v[2].push(current_v[2]);
            }

            const plotColors = ['#1f77b4', '#ff7f0e', '#2ca02c'];

            const dispTraces = [
                { x: t, y: y[0], mode: 'lines', name: 'm₁', line: {color: plotColors[0]} },
                { x: t, y: y[1], mode: 'lines', name: 'm₂', line: {color: plotColors[1]} },
                { x: t, y: y[2], mode: 'lines', name: 'm₃', line: {color: plotColors[2]} }
            ];

            const velTraces = [
                { x: t, y: v[0], mode: 'lines', name: 'm₁', line: {color: plotColors[0]} },
                { x: t, y: v[1], mode: 'lines', name: 'm₂', line: {color: plotColors[1]} },
                { x: t, y: v[2], mode: 'lines', name: 'm₃', line: {color: plotColors[2]} }
            ];

            const layout = {
                margin: { l: 40, r: 20, b: 40, t: 40, pad: 4 },
                xaxis: { title: 'time t' },
                yaxis: { autorange: true, zeroline: true },
                shapes: [{
                    type: 'line',
                    x0: simTime, x1: simTime,
                    y0: -1e6, y1: 1e6, // large range to span the plot
                    yref: 'paper',
                    line: { color: 'red', width: 1.5, dash: 'dot' }
                }],
                showlegend: false,
            };

            Plotly.newPlot('displacement-plot-container', dispTraces, { ...layout, title: 'displacement' });
            Plotly.newPlot('velocity-plot-container', velTraces, { ...layout, title: 'velocity' });
        }

        function resetSimulation() {
            isAnimating = false;
            simTime = 0;
            btnPlayPause.textContent = '▶';
            if (p5sketch) p5sketch.noLoop();

            readParameters();
            calculatePhysics();
            updatePlots();

            if (p5sketch) p5sketch.redraw();
        }

        // --- P5.JS SKETCH ---
        const sketch = (p) => {
            p.setup = () => {
                const container = document.getElementById('main-animation-container');
                const canvas = p.createCanvas(container.offsetWidth, container.offsetHeight);
                canvas.parent('main-animation-container');

                p5sketch = p; // Make p5 instance globally available

                // --- EVENT LISTENERS ---
                allSliders.forEach(slider => slider.addEventListener('input', resetSimulation));
                btnPlayPause.addEventListener('click', () => {
                    isAnimating = !isAnimating;
                    if (isAnimating) {
                        p.loop();
                        btnPlayPause.textContent = '❚❚';
                    } else {
                        p.noLoop();
                        btnPlayPause.textContent = '▶';
                    }
                });
                btnReset.addEventListener('click', resetSimulation);

                // Initial state
                resetSimulation();
            };

            p.draw = () => {
                if (isAnimating) {
                    simTime += p.deltaTime / 1000;
                    if (simTime > timeDomain.end) {
                        simTime = timeDomain.start;
                    }
                }

                // --- Drawing calculations ---
                const { omega, modes, coeffs } = eigenSystem;
                let y = [0, 0, 0];
                for (let j = 0; j < 3; j++) { // Sum over modes
                    const cos_t = Math.cos(omega[j] * simTime);
                    for (let k = 0; k < 3; k++) { // For each mass
                        y[k] += coeffs[j] * modes[k][j] * cos_t;
                    }
                }

                // --- Canvas drawing ---
                p.background(255);
                drawGrid(p);

                p.stroke(0);
                p.strokeWeight(2);

                const mapX = (x) => p.map(x, -0.2, 4.2, 0, p.width);
                const mapY = (y) => p.map(y, -1, 1, p.height, 0);

                // Draw string
                p.line(mapX(0), mapY(0), mapX(1), mapY(y[0]));
                p.line(mapX(1), mapY(y[0]), mapX(2), mapY(y[1]));
                p.line(mapX(2), mapY(y[1]), mapX(3), mapY(y[2]));
                p.line(mapX(3), mapY(y[2]), mapX(4), mapY(0));

                // Draw masses
                p.noStroke();
                p.fill(255, 0, 0);
                const baseRadius = 15;
                p.circle(mapX(1), mapY(y[0]), baseRadius * Math.sqrt(params.m1));
                p.circle(mapX(2), mapY(y[1]), baseRadius * Math.sqrt(params.m2));
                p.circle(mapX(3), mapY(y[2]), baseRadius * Math.sqrt(params.m3));

                // --- Update Plotly ---
                const update = { 'shapes[0].x0': simTime, 'shapes[0].x1': simTime };
                Plotly.relayout('displacement-plot-container', update);
                Plotly.relayout('velocity-plot-container', update);
            };

            p.windowResized = () => {
                const container = document.getElementById('main-animation-container');
                p.resizeCanvas(container.offsetWidth, container.offsetHeight);
            };

            function drawGrid(p) {
                p.stroke(220);
                p.strokeWeight(1);

                // Horizontal lines
                for(let y_val of [-0.5, 0, 0.5]) {
                    let y_px = p.map(y_val, -1, 1, p.height, 0);
                    p.line(0, y_px, p.width, y_px);
                }
                // Vertical lines
                 for(let x_val of [0, 1, 2, 3, 4]) {
                    let x_px = p.map(x_val, -0.2, 4.2, 0, p.width);
                    p.line(x_px, 0, x_px, p.height);
                }

                // Axes
                p.stroke(0);
                p.strokeWeight(1);
                p.line(p.map(-0.2, -0.2, 4.2, 0, p.width), p.map(0, -1, 1, p.height, 0), p.map(4.2, -0.2, 4.2, 0, p.width), p.map(0, -1, 1, p.height, 0)); // x-axis
                p.line(p.map(0, -0.2, 4.2, 0, p.width), p.map(-1, -1, 1, p.height, 0), p.map(0, -0.2, 4.2, 0, p.width), p.map(1, -1, 1, p.height, 0)); // y-axis

                // Labels
                p.noStroke();
                p.fill(100);
                p.textAlign(p.CENTER, p.TOP);
                for(let i=0; i<=4; i++) {
                     p.text(i, p.map(i, -0.2, 4.2, 0, p.width), p.map(0, -1, 1, p.height, 0) + 5);
                }
                p.textAlign(p.RIGHT, p.CENTER);
                 for(let y_val of [-1.0, -0.5, 0.5, 1.0]) {
                     p.text(y_val.toFixed(1), p.map(0, -0.2, 4.2, 0, p.width) - 5, p.map(y_val, -1, 1, p.height, 0));
                }
            }
        };

        new p5(sketch);

    </script>
</body>
</html>
