            font-size: 14px;
        }

        button:hover:not(:disabled) {
            background-color: #e0e0e0;
            border-color: #bbb;
        }

        button.active {
            background-color: #4a90e2;
            color: white;
            border-color: #4a90e2;
        }

        button:disabled {
            background-color: #f7f7f7;
            color: #aaa;
            cursor: not-allowed;
            border-color: #e0e0e0;
        }

        #step-controls button:first-child {
            width: 60px;
        }

        #canvas-container {
            width: 700px;
            height: 500px;
            border: 1px solid #ddd;
            position: relative;
        }

        #explanation-panel {
            min-height: 60px;
            padding: 10px;
            border-top: 1px solid #eee;
        }

        #explanation-text {
            margin: 0;
            line-height: 1.5;
            font-size: 16px;
        }
    </style>
</head>
<body>

    <div id="app-container">
        <div id="header-controls">
            <button id="btn-move">move the points</button>
            <button id="btn-start-algo">start the algorithm</button>
        </div>

        <div id="step-controls">
            <div>
                <button disabled>step 1</button>
                <button id="btn-step1-explain">explain</button>
                <button id="btn-step1-do">do it</button>
            </div>
            <div>
                <button disabled>step 2</button>
                <button id="btn-step2-explain">explain</button>
                <button id="btn-step2-do">do it</button>
            </div>
            <div id="step3-controls-container">
                <button disabled>step 3</button>
                <button id="btn-step3-explain">explain</button>
                <div id="step3-actions" style="display: inline-block;"></div>
            </div>
        </div>

        <div id="canvas-container"></div>

        <div id="explanation-panel">
            <p id="explanation-text"></p>
        </div>
    </div>

    <script>
        // --- DOM Elements ---
        const btnMove = document.getElementById('btn-move');
        const btnStartAlgo = document.getElementById('btn-start-algo');
        const stepControls = document.getElementById('step-controls');
        const step1ExplainBtn = document.getElementById('btn-step1-explain');
        const step1DoBtn = document.getElementById('btn-step1-do');
        const step2ExplainBtn = document.getElementById('btn-step2-explain');
        const step2DoBtn = document.getElementById('btn-step2-do');
        const step3ExplainBtn = document.getElementById('btn-step3-explain');
        const step3ActionsContainer = document.getElementById('step3-actions');
        const explanationText = document.getElementById('explanation-text');

        // --- State Variables ---
        let mode = 'move';
        let points = [];
        let draggedPoint = null;

        const initialAlgorithmState = {
            step: 0,
            hullPointsIndices: [],
            fullHull: [],
            leftmostPointIndex: null,
            currentPointIndex: null,
            candidateLines: [],
            explanationSteps: [],
        };
        let algorithmState = { ...initialAlgorithmState };
        let step3Buttons = [];
        let currentStep3ActionIndex = -1;

        // --- P5.js Sketch ---
        const sketch = (p) => {
            const canvasWidth = 700;
            const canvasHeight = 500;
            const pointRadius = 12;

            p.setup = () => {
                const canvas = p.createCanvas(canvasWidth, canvasHeight);
                canvas.parent('canvas-container');
                p.pixelDensity(1); // Ensure consistent sizing
                initializePoints();
                setupEventListeners();
                updateUIForMode();
            };

            p.draw = () => {
                p.background(255);

                // Draw canvas border
                p.stroke(220);
                p.noFill();
                p.rect(0, 0, p.width - 1, p.height - 1);

                // Draw candidate lines
                p.strokeWeight(2);
                p.stroke(60, 150, 140); // Teal
                algorithmState.candidateLines.forEach(lineIndices => {
                    const p1 = points[lineIndices[0]];
                    const p2 = points[lineIndices[1]];
                    p.line(p1.x, p1.y, p2.x, p2.y);
                });

                // Draw hull edges
                p.strokeWeight(3);
                p.stroke(45, 60, 110); // Dark Blue
                for (let i = 0; i < algorithmState.hullPointsIndices.length - 1; i++) {
                    const p1 = points[algorithmState.hullPointsIndices[i]];
                    const p2 = points[algorithmState.hullPointsIndices[i + 1]];
                    p.line(p1.x, p1.y, p2.x, p2.y);
                }
                // Draw closing edge if hull is complete
                if (algorithmState.hullPointsIndices.length === algorithmState.fullHull.length && algorithmState.fullHull.length > 2) {
                     const first = points[algorithmState.hullPointsIndices[0]];
                     const last = points[algorithmState.hullPointsIndices[algorithmState.hullPointsIndices.length-1]];
                     p.line(first.x, first.y, last.x, last.y);
                }


                // Draw points
                points.forEach((pt, index) => {
                    const isHullPoint = algorithmState.hullPointsIndices.includes(index);

                    if (isHullPoint) {
                        p.fill(45, 60, 110); // Dark Blue
                    } else {
                        p.fill(200, 220, 100); // Light Green
                    }

                    p.stroke(0);
                    p.strokeWeight(1);
                    p.ellipse(pt.x, pt.y, pointRadius, pointRadius);

                    // Draw crosshair
                    p.line(pt.x - pointRadius / 2, pt.y, pt.x + pointRadius / 2, pt.y);
                    p.line(pt.x, pt.y - pointRadius / 2, pt.x, pt.y + pointRadius / 2);
                });
            };

            p.mousePressed = () => {
                if (mode !== 'move' || p.mouseX < 0 || p.mouseX > p.width || p.mouseY < 0 || p.mouseY > p.height) return;

                for (let i = points.length - 1; i >= 0; i--) {
                    if (p.dist(p.mouseX, p.mouseY, points[i].x, points[i].y) < pointRadius / 2) {
                        draggedPoint = i;
                        break;
                    }
                }
            };

            p.mouseDragged = () => {
                if (draggedPoint !== null) {
                    points[draggedPoint].x = p.constrain(p.mouseX, 0, p.width);
                    points[draggedPoint].y = p.constrain(p.mouseY, 0, p.height);
                }
            };

            p.mouseReleased = () => {
                draggedPoint = null;
            };

            function initializePoints() {
                points = [
                    { x: 150, y: 350 }, { x: 250, y: 150 }, { x: 350, y: 250 }, 
                    { x: 550, y: 180 }, { x: 480, y: 320 }, { x: 420, y: 400 }
                ].map(pt => p.createVector(pt.x, pt.y));
            }
        };

        // --- Core Logic & Event Handlers ---

        function setupEventListeners() {
            btnMove.onclick = () => setMode('move');
            btnStartAlgo.onclick = () => setMode('algorithm');

            step1ExplainBtn.onclick = () => addExplanation("Step 1. Find the leftmost point.");
            step1DoBtn.onclick = doStep1;

            step2ExplainBtn.onclick = () => addExplanation("Step 2. Find the point clockwise furthest to the right relative to the leftmost point.");
            step2DoBtn.onclick = doStep2;

            step3ExplainBtn.onclick = () => addExplanation("Step 3. Repeat finding the point clockwise furthest to the right relative to the previously found point, until the leftmost point is reached again.");
        }

        function setMode(newMode) {
            mode = newMode;
            resetAlgorithmState();
            if (mode === 'algorithm') {
                precalculateHull();
                setupStep3Buttons();
            }
            updateUIForMode();
        }

        function updateUIForMode() {
            btnMove.classList.toggle('active', mode === 'move');
            btnStartAlgo.classList.toggle('active', mode === 'algorithm');

            const isAlgoMode = mode === 'algorithm';
            stepControls.style.visibility = isAlgoMode ? 'visible' : 'hidden';

            if (isAlgoMode) {
                // Enable first step
                step1ExplainBtn.disabled = false;
                step1DoBtn.disabled = false;
            }
        }

        function resetAlgorithmState() {
            algorithmState = { ...initialAlgorithmState };
            updateExplanationText();
            step3ActionsContainer.innerHTML = '';
            step3Buttons = [];
            currentStep3ActionIndex = -1;

            // Disable all step buttons
            [step1ExplainBtn, step1DoBtn, step2ExplainBtn, step2DoBtn, step3ExplainBtn].forEach(btn => btn.disabled = true);
        }

        function addExplanation(text) {
            if (!algorithmState.explanationSteps.includes(text)) {
                algorithmState.explanationSteps.push(text);
            }
            updateExplanationText();
        }

        function updateExplanationText() {
            explanationText.innerHTML = algorithmState.explanationSteps.join('<br>');
        }

        // --- Algorithm Steps ---

        function precalculateHull() {
            algorithmState.fullHull = jarvisMarch(points);
        }

        function setupStep3Buttons() {
            step3ActionsContainer.innerHTML = '';
            step3Buttons = [];
            currentStep3ActionIndex = -1;

            const numRepeats = algorithmState.fullHull.length - 2;
            if (numRepeats < 0) return;

            const doItBtn = document.createElement('button');
            doItBtn.textContent = 'do it';
            doItBtn.onclick = () => doStep3();
            doItBtn.disabled = true;
            step3ActionsContainer.appendChild(doItBtn);
            step3Buttons.push(doItBtn);

            for(let i=0; i<numRepeats; i++) {
                const repeatBtn = document.createElement('button');
                repeatBtn.id = `btn-step3-repeat-${i}`;
                repeatBtn.textContent = 'repeat';
                repeatBtn.onclick = () => doStep3();
                repeatBtn.disabled = true;
                step3ActionsContainer.appendChild(repeatBtn);
                step3Buttons.push(repeatBtn);
            }
        }

        function doStep1() {
            const leftmostIndex = algorithmState.fullHull[0];
            algorithmState.leftmostPointIndex = leftmostIndex;
            algorithmState.currentPointIndex = leftmostIndex;
            algorithmState.hullPointsIndices.push(leftmostIndex);

            step1ExplainBtn.disabled = true;
            step1DoBtn.disabled = true;
            step2ExplainBtn.disabled = false;
            step2DoBtn.disabled = false;
        }

        function doStep2() {
            handleNextHullStep();
            step2ExplainBtn.disabled = true;
            step2DoBtn.disabled = true;
            step3ExplainBtn.disabled = false;

            if (step3Buttons.length > 0) {
                step3Buttons[0].disabled = false;
                currentStep3ActionIndex = 0;
            }
        }

        function doStep3() {
            handleNextHullStep();
            // Disable current button, enable next
            if (currentStep3ActionIndex >= 0 && currentStep3ActionIndex < step3Buttons.length) {
                step3Buttons[currentStep3ActionIndex].disabled = true;
            }
            currentStep3ActionIndex++;
             if (currentStep3ActionIndex < step3Buttons.length) {
                step3Buttons[currentStep3ActionIndex].disabled = false;
            } else {
                // All steps done, disable explain button too
                step3ExplainBtn.disabled = true;
            }
        }

        function handleNextHullStep() {
            const currentHullSize = algorithmState.hullPointsIndices.length;
            if (currentHullSize >= algorithmState.fullHull.length) return;

            const currentPointIdx = algorithmState.currentPointIndex;
            const nextPointIdx = algorithmState.fullHull[currentHullSize];

            // Show candidate lines
            algorithmState.candidateLines = [];
            for (let i = 0; i < points.length; i++) {
                if (i !== currentPointIdx && !algorithmState.hullPointsIndices.includes(i)) {
                    algorithmState.candidateLines.push([currentPointIdx, i]);
                }
            }

            // After a delay, commit the step
            setTimeout(() => {
                algorithmState.candidateLines = [];
                algorithmState.hullPointsIndices.push(nextPointIdx);
                algorithmState.currentPointIndex = nextPointIdx;
            }, 750);
        }

        // --- Jarvis March Implementation ---

        function orientation(p, q, r) {
            const val = (q.y - p.y) * (r.x - q.x) - (q.x - p.x) * (r.y - q.y);
            if (val === 0) return 0; // Collinear
            return (val > 0) ? 1 : 2; // Clockwise or Counter-clockwise
        }

        function jarvisMarch(pts) {
            const n = pts.length;
            if (n < 3) return Array.from(Array(n).keys());

            const hull = [];

            // Find the leftmost point
            let l = 0;
            for (let i = 1; i < n; i++) {
                if (pts[i].x < pts[l].x) {
                    l = i;
                }
            }

            let p = l, q;
            do {
                hull.push(p);
                q = (p + 1) % n;
                for (let i = 0; i < n; i++) {
                    if (orientation(pts[p], pts[i], pts[q]) === 2) {
                        q = i;
                    }
                }
                p = q;
            } while (p !== l);

            return hull;
        }

        // --- Initialize p5 sketch ---
        new p5(sketch);

    </script>
</body>
</html>
