            <div class="control-group" id="plot-selector-group">
                <label for="select-plot">plot</label>
                <select id="select-plot">
                    <option value="fed batch" selected>fed batch</option>
                    <option value="alpha">α</option>
                </select>
            </div>
        </div>

        <div id="visualization">
            <div id="plot-top" class="plot-container"></div>
            <div id="plot-bottom" class="plot-container"></div>
        </div>
    </div>

    <script>
        document.addEventListener('DOMContentLoaded', () => {
            // --- DOM Element References ---
            const sliders = {
                alphaMax: document.getElementById('slider-alpha-max'),
                tMin: document.getElementById('slider-t-min'),
                tMax: document.getElementById('slider-t-max'),
                enzymeControl: document.getElementById('slider-enzyme-control'),
                feedRate: document.getElementById('slider-feed-rate'),
                substrateFeedConc: document.getElementById('slider-substrate-feed-conc'),
                feedTime: document.getElementById('slider-feed-time'),
            };

            const values = {
                alphaMax: document.getElementById('value-alpha-max'),
                tMin: document.getElementById('value-t-min'),
                tMax: document.getElementById('value-t-max'),
                enzymeControl: document.getElementById('value-enzyme-control'),
                feedRate: document.getElementById('value-feed-rate'),
                substrateFeedConc: document.getElementById('value-substrate-feed-conc'),
                feedTime: document.getElementById('value-feed-time'),
            };

            const plotSelector = document.getElementById('select-plot');

            let simulationData = {};

            // --- Model Constants ---
            const modelParams = {
                // Growth kinetics
                mu_max_G: 0.45,   // max growth rate on glucose
                mu_max_E: 0.18,   // max growth rate on ethanol
                K_G: 1.0,       // Monod constant for glucose
                K_E: 0.8,       // Monod constant for ethanol
                // Yield coefficients
                Y_XG: 0.5,      // biomass from glucose
                Y_XE: 0.6,      // biomass from ethanol
                Y_EG: 0.45,     // ethanol from glucose
                // Enzyme kinetics
                k_s0: 0.01, k_d0: 0.01, // E0 (constitutive)
                k_s1: 0.8,  k_d1: 0.2, K_i1: 1.0,  // E1 (fermentative, induced)
                k_s2: 1.2,  k_d2: 0.1, K_r2: 0.5,  // E2 (respirative, repressed)
                // Other
                n_alpha: 4.0,   // Hill coefficient for alpha switch
            };

            // --- ODE Solver (RK4) ---
            function rk4(derivs, y0, t0, tf, h) {
                const t = [];
                const y = [];
                let y_curr = [...y0];
                let t_curr = t0;

                while (t_curr <= tf) {
                    t.push(t_curr);
                    y.push([...y_curr]);

                    const k1 = derivs(t_curr, y_curr);
                    const k2 = derivs(t_curr + h / 2, y_curr.map((val, i) => val + (h / 2) * k1[i]));
                    const k3 = derivs(t_curr + h / 2, y_curr.map((val, i) => val + (h / 2) * k2[i]));
                    const k4 = derivs(t_curr + h, y_curr.map((val, i) => val + h * k3[i]));

                    y_curr = y_curr.map((val, i) => val + (h / 6) * (k1[i] + 2 * k2[i] + 2 * k3[i] + k4[i]));
                    t_curr += h;
                }
                return { t, y };
            }

            // --- ODE System Definition ---
            function odeSystem(t, y, params) {
                const [X, Glu, Eta, E0, E1, E2, V] = y;
                const {
                    alpha_max, K_I, F_rate, G_in, t_feed
                } = params;

                // Clamp concentrations to be non-negative
                const G = Math.max(0, Glu);
                const E = Math.max(0, Eta);

                // Feed rate is time-dependent
                const F = (t <= t_feed) ? F_rate : 0;
                const D = V > 0 ? F / V : 0; // Dilution rate

                // Fraction of respiring biomass (alpha)
                const alpha = alpha_max * (Math.pow(K_I, modelParams.n_alpha) / (Math.pow(K_I, modelParams.n_alpha) + Math.pow(G, modelParams.n_alpha)));

                // Specific growth rates (Monod)
                const mu_G = modelParams.mu_max_G * G / (modelParams.K_G + G);
                const mu_E = modelParams.mu_max_E * E / (modelParams.K_E + E);

                // Reaction rates (volumetric)
                const r_X = ((1 - alpha) * mu_G + alpha * mu_E) * X;
                const r_Glu = -(mu_G / modelParams.Y_XG) * X;
                const r_Eta = (mu_G * modelParams.Y_EG - mu_E / modelParams.Y_XE) * X;

                // Enzyme synthesis/degradation rates
                const r_E0 = modelParams.k_s0 - modelParams.k_d0 * E0;
                const r_E1 = modelParams.k_s1 * (G / (modelParams.K_i1 + G)) - modelParams.k_d1 * E1;
                const r_E2 = modelParams.k_s2 * (modelParams.K_r2 / (modelParams.K_r2 + G)) - modelParams.k_d2 * E2;

                // Mass balance ODEs
                const dX_dt = r_X - D * X;
                const dGlu_dt = r_Glu + D * (G_in - G);
                const dEta_dt = r_Eta - D * E;
                const dE0_dt = r_E0 - D * E0;
                const dE1_dt = r_E1 - D * E1;
                const dE2_dt = r_E2 - D * E2;
                const dV_dt = F;

                return [dX_dt, dGlu_dt, dEta_dt, dE0_dt, dE1_dt, dE2_dt, dV_dt];
            }

            // --- Simulation Logic ---
            function runSimulation() {
                const simParams = {
                    alpha_max: parseFloat(sliders.alphaMax.value),
                    K_I: parseFloat(sliders.enzymeControl.value),
                    F_rate: parseFloat(sliders.feedRate.value),
                    G_in: parseFloat(sliders.substrateFeedConc.value),
                    t_feed: parseFloat(sliders.feedTime.value),
                };

                const y0 = [0.1, 60, 0, 1.0, 0, 0, 1.0]; // X, Glu, Eta, E0, E1, E2, V
                const t0 = parseFloat(sliders.tMin.value);
                const tf = parseFloat(sliders.tMax.value);
                const h = 0.1;

                const results = rk4((t, y) => odeSystem(t, y, simParams), y0, t0, tf, h);

                // Transpose data for easier plotting
                simulationData.t = results.t;
                simulationData.X = results.y.map(v => v[0]);
                simulationData.Glu = results.y.map(v => v[1]);
                simulationData.Eta = results.y.map(v => v[2]);
                simulationData.E0 = results.y.map(v => v[3]);
                simulationData.E1 = results.y.map(v => v[4]);
                simulationData.E2 = results.y.map(v => v[5]);

                // Calculate alpha for each time step for plotting
                simulationData.alpha = results.y.map(v => {
                    const G = Math.max(0, v[1]);
                    return simParams.alpha_max * (Math.pow(simParams.K_I, modelParams.n_alpha) / (Math.pow(simParams.K_I, modelParams.n_alpha) + Math.pow(G, modelParams.n_alpha)));
                });

                drawPlots();
            }

            // --- Plotting Logic ---
            function drawPlots() {
                const plotChoice = plotSelector.value;

                const commonLayout = {
                    margin: { t: 40, r: 20, b: 50, l: 70 },
                    xaxis: { title: 'time (h)', gridcolor: '#ddd', zerolinecolor: '#ccc' },
                    yaxis: { gridcolor: '#ddd', zerolinecolor: '#ccc' },
                    legend: { x: 1.02, y: 1, xanchor: 'left' },
                    plot_bgcolor: '#fff',
                    paper_bgcolor: '#e9ecef',
                };

                let topTraces, topLayout, bottomTraces, bottomLayout;

                if (plotChoice === 'fed batch') {
                    topTraces = [
                        { x: simulationData.t, y: simulationData.X, name: 'X(t)', type: 'scatter', line: { color: 'blue' } },
                        { x: simulationData.t, y: simulationData.Glu, name: 'Glu(t)', type: 'scatter', line: { color: 'red' } },
                        { x: simulationData.t, y: simulationData.Eta, name: 'Eta(t)', type: 'scatter', line: { color: 'green' } },
                    ];
                    topLayout = {
                        ...commonLayout,
                        title: 'diauxic behavior',
                        yaxis: { ...commonLayout.yaxis, title: 'concentration [mol/m³]' },
                    };

                    bottomTraces = [
                        { x: simulationData.t, y: simulationData.E2, name: 'E₂(t)', type: 'scatter', line: { color: 'black' } },
                        { x: simulationData.t, y: simulationData.E1, name: 'E₁(t)', type: 'scatter', line: { color: 'magenta' } },
                        { x: simulationData.t, y: simulationData.E0, name: 'E₀(t)', type: 'scatter', line: { color: 'orange' } },
                    ];
                    bottomLayout = {
                        ...commonLayout,
                        title: 'enzyme concentration',
                        yaxis: { ...commonLayout.yaxis, title: 'enzyme concentration [mol/m³]' },
                    };
                } else { // 'alpha'
                    topTraces = [
                        { x: simulationData.t, y: simulationData.Glu, name: 'Glu(t)', type: 'scatter', line: { color: 'red' } },
                    ];
                    topLayout = {
                        ...commonLayout,
                        title: 'glucose concentration',
                        yaxis: { ...commonLayout.yaxis, title: 'glucose concentration [mol/m³]' },
                    };

                    bottomTraces = [
                        { x: simulationData.t, y: simulationData.alpha, name: 'α', type: 'scatter', line: { color: 'blue' } },
                    ];
                    bottomLayout = {
                        ...commonLayout,
                        title: 'fraction of respiring biomass',
                        yaxis: { ...commonLayout.yaxis, title: 'α' },
                    };
                }

                Plotly.react('plot-top', topTraces, topLayout, {responsive: true});
                Plotly.react('plot-bottom', bottomTraces, bottomLayout, {responsive: true});
            }

            // --- Event Listeners ---
            function setupEventListeners() {
                // Sliders updating their value display and triggering simulation
                for (const key in sliders) {
                    sliders[key].addEventListener('input', (event) => {
                        values[key].textContent = event.target.value;

                        // Special logic for t_max updating feed_time max
                        if (key === 'tMax') {
                            const newTMax = parseFloat(event.target.value);
                            sliders.feedTime.max = newTMax;
                            if (parseFloat(sliders.feedTime.value) > newTMax) {
                                sliders.feedTime.value = newTMax;
                                values.feedTime.textContent = newTMax;
                            }
                        }

                        runSimulation();
                    });
                }

                // Plot selector changing the displayed plots
                plotSelector.addEventListener('change', drawPlots);
            }

            // --- Initialisation ---
            setupEventListeners();
            runSimulation(); // Initial run on page load
        });
    </script>
</body>
