            // --- Ground View Objects ---
            // Ground plane
            const groundTopMat = new THREE.MeshPhongMaterial({ color: 0x006400 });
            const groundSideMat = new THREE.MeshPhongMaterial({ color: 0x00008b });
            const groundGeom = new THREE.CylinderGeometry(GROUND_RADIUS, GROUND_RADIUS, 1, 64);
            const ground = new THREE.Mesh(groundGeom, [groundSideMat, groundTopMat, groundTopMat]);
            ground.position.y = -0.5;
            groundViewObjects.add(ground);

            // Celestial Dome
            const domeMaterial = new THREE.MeshBasicMaterial({ color: 0x87ceeb, side: THREE.BackSide, transparent: true, opacity: 0.5 });
            const dome = new THREE.Mesh(new THREE.SphereGeometry(GROUND_RADIUS + 2, 32, 16, 0, Math.PI*2, 0, Math.PI/2), domeMaterial);
            dome.name = "celestialDome";
            groundViewObjects.add(dome);

            // Cardinal Points
            const points = { N: [0, 0, -GROUND_RADIUS], E: [GROUND_RADIUS, 0, 0], S: [0, 0, GROUND_RADIUS], W: [-GROUND_RADIUS, 0, 0] };
            for(const p in points) {
                const sprite = createTextSprite(p, {fontsize: 64});
                sprite.position.set(points[p][0], 0.5, points[p][1]);
                groundViewObjects.add(sprite);
            }
            // Observer marker
            const obsGeom = new THREE.ConeGeometry(0.2, 0.5, 8);
            obsGeom.translate(0, 0.25, 0);
            const obsMat = new THREE.MeshBasicMaterial({ color: 0xff0000 });
            const observer = new THREE.Mesh(obsGeom, obsMat);
            observer.position.y = 0;
            groundViewObjects.add(observer);

            // Sun path line
            const sunPathMat = new THREE.LineBasicMaterial({ color: 0xffff00, linewidth: 2 });
            const sunPathGeom = new THREE.BufferGeometry();
            const sunPathLine = new THREE.Line(sunPathGeom, sunPathMat);
            sunPathLine.name = "sunPathLine";
            groundViewObjects.add(sunPathLine);


            // --- Space View Objects ---
            // Earth
            const earthGroup = new THREE.Group();
            earthGroup.name = "earthGroup";
            earthGroup.rotation.z = degToRad(EARTH_TILT); // Axial tilt
            const earthMat = new THREE.MeshPhongMaterial({ color: 0x4682b4 });
            const earthMesh = new THREE.Mesh(new THREE.SphereGeometry(EARTH_RADIUS, 32, 32), earthMat);
            earthGroup.add(earthMesh);

            // Earth Axis
            const axisMat = new THREE.LineBasicMaterial({ color: 0xff0000 });
            const axisPoints = [new THREE.Vector3(0, -EARTH_RADIUS*1.5, 0), new THREE.Vector3(0, EARTH_RADIUS*1.5, 0)];
            const axisGeom = new THREE.BufferGeometry().setFromPoints(axisPoints);
            const earthAxis = new THREE.Line(axisGeom, axisMat);
            earthGroup.add(earthAxis);
            spaceViewObjects.add(earthGroup);

            // Latitude Parallel
            const parallelGeom = new THREE.TorusGeometry(1, 0.05, 16, 64);
            const parallelMat = new THREE.MeshBasicMaterial({ color: 0xffff00 });
            const latitudeParallel = new THREE.Mesh(parallelGeom, parallelMat);
            latitudeParallel.name = "latitudeParallel";
            spaceViewObjects.add(latitudeParallel);

            // Day/Night on parallel
            const dayParallelMat = new THREE.LineBasicMaterial({ color: 0x00ff00, linewidth: 5 });
            const dayParallelGeom = new THREE.BufferGeometry();
            const dayNightOnParallel = new THREE.Line(dayParallelGeom, dayParallelMat);
            dayNightOnParallel.name = "dayNightOnParallel";
            spaceViewObjects.add(dayNightOnParallel);

            // Day/Night Division (Terminator)
            const terminatorGeom = new THREE.PlaneGeometry(EARTH_RADIUS * 3, EARTH_RADIUS * 3);
            const terminatorMat = new THREE.MeshBasicMaterial({ color: 0x000000, transparent: true, opacity: 0.25, side: THREE.DoubleSide });
            const terminatorPlane = new THREE.Mesh(terminatorGeom, terminatorMat);
            terminatorPlane.name = "terminatorPlane";
            spaceViewObjects.add(terminatorPlane);
        }

        // --- Update Logic ---
        function updateAll() {
            const day = parseFloat(controls.date.value);
            const lat = parseFloat(controls.latitude.value);
            const time = parseFloat(controls.time.value);

            // Update labels
            labels.date.textContent = dayOfYearToDateString(day);
            labels.latitude.textContent = `${lat}°`;
            labels.time.textContent = formatHours(time);

            // Calculations
            const dec = solarDeclination(day);
            const sunData = getSunriseSunset(lat, dec);

            // Update readouts
            readouts.dayLength.textContent = formatHourMinute(sunData.dayLength);
            readouts.nightLength.textContent = formatHourMinute(sunData.nightLength);
            readouts.sunriseTime.textContent = sunData.sunrise ? formatHours(sunData.sunrise) : "---";
            readouts.sunriseDirection.textContent = sunData.azRise ? `${Math.round(sunData.azRise)}°` : "---";
            readouts.sunsetTime.textContent = sunData.sunset ? formatHours(sunData.sunset) : "---";
            readouts.sunsetDirection.textContent = sunData.azSet ? `${Math.round(sunData.azSet)}°` : "---";

            updateSceneFromControls();
            needsRender = true;
        }

        function updateSceneFromControls() {
            const day = parseFloat(controls.date.value);
            const lat = parseFloat(controls.latitude.value);
            const time = parseFloat(controls.time.value);
            const view = controls.view.value;
            const dec = solarDeclination(day);
            const sunData = getSunriseSunset(lat, dec);

            // --- View specific updates ---
            if (view === "ground") {
                // Sun path
                const sunPathLine = groundViewObjects.getObjectByName("sunPathLine");
                if (sunData.sunrise !== null) {
                    const pathPoints = [];
                    for (let t = sunData.sunrise; t <= sunData.sunset; t += 0.25) {
                        pathPoints.push(getSunPosition(lat, dec, t).pos);
                    }
                    if (pathPoints.length > 0) pathPoints.push(getSunPosition(lat, dec, sunData.sunset).pos);
                    sunPathLine.geometry.setFromPoints(pathPoints);
                    sunPathLine.geometry.computeBoundingSphere();
                } else { // Polar day/night
                    sunPathLine.geometry.setFromPoints([]);
                }

                // Sun position
                const { pos: sunPos, alt } = getSunPosition(lat, dec, time);
                sun.position.copy(sunPos);

                // Dome color
                const dome = groundViewObjects.getObjectByName("celestialDome");
                const nightFactor = Math.max(0, Math.min(1, (0 - alt) / 10)); // Fade to dark when sun is below horizon
                dome.material.opacity = 0.3 + nightFactor * 0.6;
                dome.material.color.setHSL(0.57, 0.5, 0.5 - nightFactor * 0.4);
                ambientLight.intensity = 2 - nightFactor * 1.5;

            } else { // "space" view
                // Earth rotation
                const earthGroup = spaceViewObjects.getObjectByName("earthGroup");
                const isStationary = controls.stationaryEarth.checked;
                const timeAngle = degToRad(15 * time); // 15 deg/hr

                if (isStationary) {
                    earthGroup.rotation.y = 0; // Earth fixed
                } else {
                    earthGroup.rotation.y = timeAngle; // Earth rotates
                }

                // Sun orbital position (based on date)
                const orbitalAngle = 2 * Math.PI * (day - 80) / 365; // Day 80 is approx. vernal equinox

                const sunX = SUN_DISTANCE * Math.cos(orbitalAngle);
                const sunZ = SUN_DISTANCE * Math.sin(orbitalAngle);

                if (isStationary) { // Sun moves for time of day if earth is stationary
                    const sunPos = new THREE.Vector3(sunX, 0, sunZ);
                    sunPos.applyAxisAngle(new THREE.Vector3(0, 1, 0), -timeAngle);
                    sun.position.set(sunPos.x, sunPos.y, sunPos.z);
                } else {
                    sun.position.set(sunX, 0, sunZ);
                }

                directionalLight.position.set(0,0,0);
                directionalLight.target.position.copy(earthGroup.position);

                // Latitude parallel
                const latRad = degToRad(lat);
                const parallelRadius = EARTH_RADIUS * Math.cos(latRad);
                const parallelY = EARTH_RADIUS * Math.sin(latRad);
                const latitudeParallel = spaceViewObjects.getObjectByName("latitudeParallel");
                latitudeParallel.scale.set(parallelRadius, parallelRadius, 1);
                latitudeParallel.position.set(0, parallelY, 0);
                latitudeParallel.quaternion.setFromAxisAngle(new THREE.Vector3(1, 0, 0), Math.PI / 2);
                latitudeParallel.position.applyQuaternion(earthGroup.quaternion); // Tilt with earth
                latitudeParallel.quaternion.multiply(earthGroup.quaternion);

                // Terminator plane
                const terminatorPlane = spaceViewObjects.getObjectByName("terminatorPlane");
                terminatorPlane.position.copy(earthGroup.position);
                terminatorPlane.lookAt(sun.position);

                // Day/Night on parallel
                const dayNightOnParallel = spaceViewObjects.getObjectByName("dayNightOnParallel");
                const parallelWorldPos = new THREE.Vector3();
                latitudeParallel.getWorldPosition(parallelWorldPos);

                const sunDir = new THREE.Vector3().subVectors(earthGroup.position, sun.position).normalize();
                const H = Math.acos(-Math.tan(degToRad(lat)) * Math.tan(degToRad(dec)));

                if (isFinite(H)) {
                    const points = [];
                    const segments = 64;
                    for(let i=0; i<=segments; i++) {
                        const angle = -H + (2 * H * i / segments);
                        const pointOnCircle = new THREE.Vector3(
                             parallelRadius * Math.cos(angle),
                             parallelRadius * Math.sin(angle),
                             0
                        );
                        const pointIn3D = pointOnCircle.clone();
                        pointIn3D.applyAxisAngle(new THREE.Vector3(1, 0, 0), Math.PI/2); // to XY plane
                        pointIn3D.add(new THREE.Vector3(0, parallelY, 0)); // move to latitude
                        pointIn3D.applyQuaternion(earthGroup.quaternion); // tilt axis
                        pointIn3D.applyQuaternion(earthGroup.quaternion.clone().premultiply(new THREE.Quaternion().setFromAxisAngle(new THREE.Vector3(0,1,0), earthGroup.rotation.y))); // rotate for time
                        points.push(pointIn3D);
                    }
                    dayNightOnParallel.geometry.setFromPoints(points);
                } else {
                    dayNightOnParallel.geometry.setFromPoints([]);
                }
            }

            // --- Update visibilities based on checkboxes ---
            groundViewObjects.getObjectByName("sunPathLine").visible = controls.sunPath.checked;
            const constellationsGroup = sharedObjects.getObjectByName("constellationsGroup");
            const dome = groundViewObjects.getObjectByName("celestialDome");
            if(view === 'ground'){
                const { alt } = getSunPosition(lat, dec, time);
                const isDay = alt > -5; // A bit of twilight
                constellationsGroup.visible = controls.constellationsDay.checked || !isDay;
                constellationsGroup.children.forEach(c => c.material.opacity = isDay && !controls.constellationsDay.checked ? 0 : 0.5);
                dome.visible = true;
            } else {
                constellationsGroup.visible = controls.constellations.checked;
                constellationsGroup.children.forEach(c => c.material.opacity = 0.5);
                dome.visible = false;
            }

            spaceViewObjects.getObjectByName("terminatorPlane").visible = controls.dayNightDivision.checked;
            spaceViewObjects.getObjectByName("dayNightOnParallel").visible = controls.dayNightParallel.checked;

            needsRender = true;
        }

        function switchView() {
            const view = controls.view.value;
            if (view === 'ground') {
                camera.position.set(0, 1.5, GROUND_RADIUS * 0.8);
                orbitControls.target.set(0, 2, 0);
                groundViewObjects.visible = true;
                spaceViewObjects.visible = false;
                options.ground.style.display = 'block';
                options.space.style.display = 'none';
            } else { // 'space'
                camera.position.set(0, 15, 30);
                orbitControls.target.set(0, 0, 0);
                groundViewObjects.visible = false;
                spaceViewObjects.visible = true;
                options.ground.style.display = 'none';
                options.space.style.display = 'block';
            }
            updateAll();
        }

        function setupEventListeners() {
            Object.values(controls).forEach(control => {
                control.addEventListener('input', updateAll);
            });
            controls.view.addEventListener('change', switchView);
            window.addEventListener('resize', () => {
                camera.aspect = visContainer.clientWidth / visContainer.clientHeight;
                camera.updateProjectionMatrix();
                renderer.setSize(visContainer.clientWidth, visContainer.clientHeight);
                needsRender = true;
            });
        }

        // --- Animation Loop ---
        function animate() {
            requestAnimationFrame(animate);
            orbitControls.update();
            if (needsRender) {
                renderer.render(scene, camera);
                needsRender = false;
            }
        }

        // --- Start ---
        init();
        switchView(); // Set initial view

    </script>
</body>
</html>
