<body>

    <div id="main-container">
        <h1>Unsteady-State Diffusion in a Slab</h1>
        <p>
            This demonstration visualizes the one-dimensional, unsteady-state diffusion of a solute into a solid slab. The slab has a thickness L. The concentration at one surface (x=L) is held constant at c<sub>s</sub>, while the other surface (x=0) is impermeable (zero flux). The slab has an initial linear concentration profile. The process is governed by Fick's second law of diffusion. Use the sliders to adjust the elapsed time and the material's diffusivity to see how they affect the concentration profile.
        </p>
        <div id="control-panel">
            <div class="control-row">
                <label for="time-slider">time</label>
                <input type="range" id="time-slider" min="0" max="2500" step="1" value="100">
                <span id="time-value"></span>
            </div>
            <div class="control-row">
                <label for="diffusivity-slider">diffusivity in m<sup>2</sup>/s</label>
                <input type="range" id="diffusivity-slider" min="1e-10" max="1e-8" step="1e-10" value="4e-9">
                <span id="diffusivity-value"></span>
            </div>
        </div>
        <div id="plot-div"></div>
    </div>

    <script>
        document.addEventListener('DOMContentLoaded', function () {
            // Get DOM elements
            const timeSlider = document.getElementById('time-slider');
            const timeValue = document.getElementById('time-value');
            const diffusivitySlider = document.getElementById('diffusivity-slider');
            const diffusivityValue = document.getElementById('diffusivity-value');
            const plotDiv = document.getElementById('plot-div');

            // --- Formatting Helpers ---
            function formatTime(val) {
                return parseFloat(val).toFixed(1);
            }

            function formatDiffusivity(val) {
                const value = parseFloat(val);
                if (value === 0) return "0.0 x 10<sup>0</sup>";
                const parts = value.toExponential(1).split('e');
                return `${parts[0]} x 10<sup>${parts[1]}</sup>`;
            }

            // --- Update Value Displays ---
            function updateDisplays() {
                timeValue.textContent = formatTime(timeSlider.value);
                diffusivityValue.innerHTML = formatDiffusivity(diffusivitySlider.value);
            }

            // --- Core Calculation and Plotting ---
            function updatePlot() {
                // Get current values from sliders
                const t = parseFloat(timeSlider.value);
                const D = parseFloat(diffusivitySlider.value);

                // Define physical and simulation constants
                const L = 0.004; // Slab thickness (m)
                const c_s = 4.0e-3; // Surface concentration
                const nPoints = 200; // Number of points for the plot
                const N_series = 100; // Number of terms in the series solution

                // Generate arrays for plotting
                const x_data = [];
                const c_data = [];

                for (let i = 0; i < nPoints; i++) {
                    const x = i * L / (nPoints - 1);
                    x_data.push(x);

                    let c;
                    if (t === 0) {
                        // Case 1: Initial concentration profile (t=0)
                        c = (1.0 + 250 * x) * 1e-3;
                    } else {
                        // Case 2: Series solution for t > 0
                        let u = 0;
                        for (let n = 0; n < N_series; n++) {
                            const k_n = ((n + 0.5) * Math.PI) / L;
                            const B_n = (2 / L) * 1e-3 * (((250 * Math.pow(-1, n)) / (k_n * k_n)) - (3 / k_n));
                            const term = B_n * Math.sin(k_n * x) * Math.exp(-k_n * k_n * D * t);
                            u += term;
                        }
                        c = u + c_s;
                    }
                    c_data.push(c);
                }

                // Plotly data and layout configuration
                const trace = {
                    x: x_data,
                    y: c_data,
                    type: 'scatter',
                    mode: 'lines',
                    line: {
                        color: '#3498db',
                        width: 2.5
                    }
                };

                const layout = {
                    xaxis: {
                        title: 'position',
                        range: [0, 0.004],
                        tickformat: '.3f',
                        dtick: 0.001,
                        showgrid: true,
                        gridcolor: '#e1e1e1',
                        minor: { showgrid: true, gridcolor: '#f0f0f0' }
                    },
                    yaxis: {
                        title: 'concentration',
                        range: [0.001, 0.0042],
                        tickformat: '.4f',
                        dtick: 0.0005,
                        showgrid: true,
                        gridcolor: '#e1e1e1',
                        minor: { showgrid: true, gridcolor: '#f0f0f0' }
                    },
                    margin: { l: 80, r: 20, t: 20, b: 50 },
                    // Use datarevision for efficient updates
                    datarevision: (new Date()).getTime()
                };

                Plotly.react(plotDiv, [trace], layout, {responsive: true});
            }

            // --- Event Listeners ---
            timeSlider.addEventListener('input', () => {
                timeValue.textContent = formatTime(timeSlider.value);
                updatePlot();
            });

            diffusivitySlider.addEventListener('input', () => {
                diffusivityValue.innerHTML = formatDiffusivity(diffusivitySlider.value);
                updatePlot();
            });

            // --- Initial Setup ---
            updateDisplays();
            updatePlot();
        });
    </script>

</body>
</html>
