                <span>type</span>
                <button class="type-btn" data-type="left">left</button>
                <button class="type-btn active" data-type="right">right</button>
                <button class="type-btn" data-type="midpoint">midpoint</button>
                <button class="type-btn" data-type="overestimate">overestimate</button>
                <button class="type-btn" data-type="underestimate">underestimate</button>
                <button class="type-btn" data-type="trapezoidal">trapezoidal</button>
            </div>
            <div id="slider-a-controls">
                <label for="slider-a">upper limit a</label>
                <input type="range" id="slider-a" min="0.1" max="8" step="0.01" value="5">
                <span id="value-a">5.00</span>
            </div>
            <div id="slider-n-controls">
                <label for="slider-n">number of quadrilaterals</label>
                <input type="range" id="slider-n" min="1" max="50" step="1" value="3">
                <span id="value-n">3</span>
            </div>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        const state = {
            funcKey: 'x',
            type: 'right',
            a: 5,
            n: 3
        };

        const functions = {
            'x': {
                f: x => x,
                integralF: x => 0.5 * x * x,
            },
            'x-sq': {
                f: x => Math.pow(x - 3, 2),
                integralF: x => (1 / 3) * Math.pow(x - 3, 3) - (1/3) * Math.pow(-3, 3),
            },
            'x-cub': {
                f: x => Math.pow(x - 3, 3) + 27,
                integralF: x => (1 / 4) * Math.pow(x - 3, 4) + 27 * x - (1 / 4) * Math.pow(-3, 4),
            },
            'sqrt': {
                f: x => Math.sqrt(x),
                integralF: x => (2 / 3) * Math.pow(x, 1.5),
            },
            'sin': {
                f: x => Math.sin(x) + 1,
                integralF: x => -Math.cos(x) + x - (-Math.cos(0) + 0),
            },
            'exp': {
                f: x => Math.exp(x / 3),
                integralF: x => 3 * Math.exp(x / 3) - 3 * Math.exp(0),
            }
        };

        const sketch = (p) => {
            const logicalWidth = 9.5;
            const logicalXMin = -1;
            const padding = { top: 70, right: 20, bottom: 40, left: 50 };

            let canvasWidth, canvasHeight;
            let xScale, yScale;
            let logicalYMin, logicalYMax;

            p.setup = () => {
                canvasWidth = document.getElementById('canvas-container').clientWidth;
                canvasHeight = 600;
                let cnv = p.createCanvas(canvasWidth, canvasHeight);
                cnv.parent('canvas-container');
                p.noLoop();
                setupEventListeners();
                updateUI();
                p.redraw();
            };

            function setupEventListeners() {
                // Function buttons
                document.querySelectorAll('.function-btn').forEach(btn => {
                    btn.addEventListener('click', () => {
                        state.funcKey = btn.dataset.function;
                        updateUI();
                        p.redraw();
                    });
                });

                // Type buttons
                document.querySelectorAll('.type-btn').forEach(btn => {
                    btn.addEventListener('click', () => {
                        state.type = btn.dataset.type;
                        updateUI();
                        p.redraw();
                    });
                });

                // Slider A
                const sliderA = document.getElementById('slider-a');
                sliderA.addEventListener('input', () => {
                    state.a = parseFloat(sliderA.value);
                    updateUI();
                    p.redraw();
                });

                // Slider N
                const sliderN = document.getElementById('slider-n');
                sliderN.addEventListener('input', () => {
                    state.n = parseInt(sliderN.value, 10);
                    updateUI();
                    p.redraw();
                });
            }

            function updateUI() {
                // Update buttons
                document.querySelectorAll('.function-btn').forEach(btn => {
                    btn.classList.toggle('active', btn.dataset.function === state.funcKey);
                });
                document.querySelectorAll('.type-btn').forEach(btn => {
                    btn.classList.toggle('active', btn.dataset.type === state.type);
                });

                // Update sliders and values
                document.getElementById('slider-a').value = state.a;
                document.getElementById('value-a').textContent = state.a.toFixed(2);
                document.getElementById('slider-n').value = state.n;
                document.getElementById('value-n').textContent = state.n;
            }

            // Coordinate mapping functions
            const mapX = (x) => p.map(x, logicalXMin, logicalXMin + logicalWidth, padding.left, canvasWidth - padding.right);
            const mapY = (y) => p.map(y, logicalYMin, logicalYMax, canvasHeight - padding.bottom, padding.top);

            function setScale() {
                const currentFunc = functions[state.funcKey].f;
                let yMin = 0, yMax = 0;

                // Find min/max y for the visible range
                for (let x = logicalXMin; x <= logicalXMin + logicalWidth; x += 0.01) {
                    if (x < 0 && state.funcKey === 'sqrt') continue;
                    const y = currentFunc(x);
                    if (y < yMin) yMin = y;
                    if (y > yMax) yMax = y;
                }

                // Add padding
                const yRange = yMax - yMin;
                logicalYMin = yMin - yRange * 0.1;
                logicalYMax = yMax + yRange * 0.1;
                if (logicalYMin > -0.5) logicalYMin = -0.5; // Ensure origin is often visible
            }

            function drawAxes() {
                p.stroke(204);
                p.strokeWeight(1);
                p.textSize(12);
                p.fill(120);
                p.textAlign(p.CENTER, p.CENTER);

                // Y-axis
                p.line(mapX(0), padding.top, mapX(0), canvasHeight - padding.bottom);
                // X-axis
                p.line(padding.left, mapY(0), canvasWidth - padding.right, mapY(0));

                // Y-axis ticks
                const yStep = Math.pow(10, Math.floor(Math.log10(logicalYMax - logicalYMin))) / 2 || 1;
                for (let y = Math.floor(logicalYMin / yStep) * yStep; y <= logicalYMax; y += yStep) {
                    if (Math.abs(y) < 1e-9) continue;
                    p.line(mapX(0) - 4, mapY(y), mapX(0) + 4, mapY(y));
                    p.text(y.toPrecision(2).replace(/\.0+$/, ''), mapX(0) - 25, mapY(y));
                }

                // X-axis ticks
                for (let x = Math.ceil(logicalXMin); x < logicalXMin + logicalWidth; x++) {
                    if (x === 0) continue;
                    p.line(mapX(x), mapY(0) - 4, mapX(x), mapY(0) + 4);
                    p.text(x, mapX(x), mapY(0) + 20);
                }
                p.text(0, mapX(0) - 10, mapY(0) + 15);
            }

            function drawFunctionCurve() {
                const currentFunc = functions[state.funcKey].f;
                p.noFill();
                p.stroke(0);
                p.strokeWeight(2.5);
                p.beginShape();
                for (let px = padding.left; px <= canvasWidth - padding.right; px++) {
                    let x = p.map(px, padding.left, canvasWidth - padding.right, logicalXMin, logicalXMin + logicalWidth);
                    if (x < 0 && state.funcKey === 'sqrt') continue;
                    let y = currentFunc(x);
                    p.vertex(mapX(x), mapY(y));
                }
                p.endShape();
            }

            function drawEstimationShapes() {
                const f = functions[state.funcKey].f;
                const integralF = functions[state.funcKey].integralF;
                const deltaX = state.a / state.n;
                let totalEstimatedArea = 0;

                p.stroke(80);
                p.strokeWeight(1);

                for (let i = 0; i < state.n; i++) {
                    const x_i = i * deltaX;
                    const x_i_plus_1 = (i + 1) * deltaX;

                    let height = 0;
                    let shapeArea = 0;
                    const trueIntervalIntegral = integralF(x_i_plus_1) - integralF(x_i);

                    p.noStroke();
                    p.stroke(80);
                    p.strokeWeight(1);

                    if (state.type === 'trapezoidal') {
                        const h1 = f(x_i);
                        const h2 = f(x_i_plus_1);
                        shapeArea = (h1 + h2) / 2 * deltaX;

                        const color = shapeArea > trueIntervalIntegral ? p.color(173, 216, 230, 180) : p.color(144, 238, 144, 180);
                        p.fill(color);

                        p.quad(mapX(x_i), mapY(0), mapX(x_i_plus_1), mapY(0), mapX(x_i_plus_1), mapY(h2), mapX(x_i), mapY(h1));

                    } else { // All rectangle types
                        switch (state.type) {
                            case 'left': height = f(x_i); break;
                            case 'right': height = f(x_i_plus_1); break;
                            case 'midpoint': height = f(x_i + deltaX / 2); break;
                            case 'underestimate':
                            case 'overestimate':
                                let extremeY = state.type === 'underestimate' ? Infinity : -Infinity;
                                for (let j = 0; j <= 10; j++) {
                                    const sampleX = x_i + j / 10 * deltaX;
                                    const sampleY = f(sampleX);
                                    if (state.type === 'underestimate') {
                                        extremeY = Math.min(extremeY, sampleY);
                                    } else {
                                        extremeY = Math.max(extremeY, sampleY);
                                    }
                                }
                                height = extremeY;
                                break;
                        }

                        shapeArea = height * deltaX;
                        const color = shapeArea > trueIntervalIntegral ? p.color(173, 216, 230, 180) : p.color(144, 238, 144, 180);
                        p.fill(color);

                        p.rect(mapX(x_i), mapY(0), mapX(x_i_plus_1) - mapX(x_i), mapY(height) - mapY(0));
                    }

                    totalEstimatedArea += shapeArea;
                }
                return totalEstimatedArea;
            }

            function drawTextOverlay(estimatedArea) {
                const trueIntegral = functions[state.funcKey].integralF(state.a);

                p.noStroke();
                p.fill(80);
                p.textAlign(p.CENTER, p.TOP);
                p.textSize(16);
                p.text(`estimated area: ${estimatedArea.toFixed(5)}`, canvasWidth / 2, 15);
                p.text(`integral: ${trueIntegral.toFixed(5)}`, canvasWidth / 2, 38);
            }

            p.draw = () => {
                p.background(248, 248, 248);
                setScale();
                drawAxes();

                const estimatedArea = drawEstimationShapes();

                drawFunctionCurve();

                // Draw upper limit marker
                p.stroke(255, 0, 0);
                p.strokeWeight(1.5);
                p.line(mapX(state.a), padding.top, mapX(state.a), canvasHeight - padding.bottom);

                drawTextOverlay(estimatedArea);
            };
        };

        new p5(sketch);
    </script>
</body>
</html>
