    </div>

<script>
let p5sketch;

const initialValues = {
    current: 0.431,
    earthField: 0.0000194,
    radius: 0.5,
    turns: 25,
};

let controls = {};

function sketch(p) {
    const mu0 = 4 * Math.PI * 1e-7;
    const canvasSize = 600;
    const padding = 60;
    const worldMin = -0.5;
    const worldMax = 0.5;

    p.setup = function() {
        const canvas = p.createCanvas(canvasSize, canvasSize);
        canvas.parent('p5-canvas');
        p.colorMode(p.HSB, 360, 100, 100);
        p.noLoop();

        controls.current = p.select('#slider-current');
        controls.earthField = p.select('#slider-earth-field');
        controls.radius = p.select('#slider-radius');
        controls.turns = p.select('#slider-turns');

        controls.valCurrent = p.select('#value-current');
        controls.valEarthField = p.select('#value-earth-field');
        controls.valRadius = p.select('#value-radius');
        controls.valTurns = p.select('#value-turns');

        controls.btnReset = p.select('#btn-reset');

        controls.current.input(updateAndRedraw);
        controls.earthField.input(updateAndRedraw);
        controls.radius.input(updateAndRedraw);
        controls.turns.input(updateAndRedraw);
        controls.btnReset.mousePressed(resetParameters);

        resetParameters();
    };

    function updateAndRedraw() {
        const currentVal = parseFloat(controls.current.value());
        const earthFieldVal = parseFloat(controls.earthField.value());
        const radiusVal = parseFloat(controls.radius.value());
        const turnsVal = parseInt(controls.turns.value());

        controls.valCurrent.html(currentVal.toFixed(3));

        if (earthFieldVal < 1e-5 && earthFieldVal > 0) {
            controls.valEarthField.html(earthFieldVal.toExponential(1));
        } else {
            controls.valEarthField.html(earthFieldVal.toFixed(7));
        }

        controls.valRadius.html(radiusVal.toFixed(2));
        controls.valTurns.html(turnsVal);

        p.redraw();
    }

    function resetParameters() {
        controls.current.value(initialValues.current);
        controls.earthField.value(initialValues.earthField);
        controls.radius.value(initialValues.radius);
        controls.turns.value(initialValues.turns);
        updateAndRedraw();
    }

    p.draw = function() {
        p.background(255);

        const params = {
            I: parseFloat(controls.current.value()),
            B_earth: parseFloat(controls.earthField.value()),
            R: parseFloat(controls.radius.value()),
            N: parseInt(controls.turns.value())
        };

        if (params.R === 0) return;

        drawAxes();

        const gridStep = (worldMax - worldMin) / 20;
        let magnitudes = [];
        for (let y = worldMin; y <= worldMax; y += gridStep) {
            for (let z = worldMin; z <= worldMax; z += gridStep) {
                const B = getTotalField(y, z, params);
                magnitudes.push(B.mag());
            }
        }

        const minMag = p.min(magnitudes);
        const maxMag = p.max(magnitudes);

        const startPoints = [];
        const streamlineGridSize = 25;
        for (let i = 0; i <= streamlineGridSize; i++) {
            for (let j = 0; j <= streamlineGridSize; j++) {
                startPoints.push({
                    y: p.map(i, 0, streamlineGridSize, worldMin, worldMax),
                    z: p.map(j, 0, streamlineGridSize, worldMin, worldMax)
                });
            }
        }

        for (const pt of startPoints) {
            drawStreamline(pt.y, pt.z, params, minMag, maxMag);
        }
    };

    function drawStreamline(y0, z0, params, minMag, maxMag) {
        const trace = (dir) => {
            let y = y0;
            let z = z0;
            const stepSize = 0.005;
            const numSteps = 200;

            for (let i = 0; i < numSteps; i++) {
                if (y < worldMin || y > worldMax || z < worldMin || z > worldMax) break;

                const B = getTotalField(y, z, params);
                const mag = B.mag();
                if (mag === 0) break;

                const B_norm = B.copy().div(mag);
                const nextY = y + dir * B_norm.y * stepSize;
                const nextZ = z + dir * B_norm.z * stepSize;

                const hue = p.map(mag, minMag, maxMag, 60, 270);
                p.stroke(hue, 90, 80);
                p.strokeWeight(1.5);
                p.noFill();

                const p1 = worldToPixel(y, z);
                const p2 = worldToPixel(nextY, nextZ);
                p.line(p1.x, p1.y, p2.x, p2.y);

                if (i > 0 && i % 15 === 0) {
                     drawArrow(p1, p2, p.color(hue, 90, 80));
                }

                y = nextY;
                z = nextZ;
            }
        }
        trace(1);
        trace(-1);
    }

    function drawArrow(p1, p2, color) {
        p.push();
        p.stroke(color);
        p.fill(color);
        p.strokeWeight(1);
        const angle = p.atan2(p2.y - p1.y, p2.x - p1.x);
        p.translate(p2.x, p2.y);
        p.rotate(angle);
        p.triangle(0, 0, -6, -3, -6, 3);
        p.pop();
    }

    function getTotalField(y, z, params) {
        const { R, I, N, B_earth } = params;
        // Field from two coils at z = -R/2 and z = +R/2
        const B1 = getFieldOneCoil(y, z, R, I, N, -R/2);
        const B2 = getFieldOneCoil(y, z, R, I, N, R/2);

        const B_total = p.createVector(0, B1.y + B2.y, B1.z + B2.z);
        B_total.z += B_earth; // Earth field is along Z

        return B_total;
    }

    function getFieldOneCoil(y, z, R, I, N, z_offset) {
        // Using formulas involving complete elliptic integrals
        const zp = z - z_offset;

        if (Math.abs(y) < 1e-9 && Math.abs(zp) < 1e-9) {
            return p.createVector(0, 0, 0); // At the center of the coil
        }

        const R2 = R*R;
        const y2 = y*y;
        const zp2 = zp*zp;

        const denom_m = (R + y)**2 + zp2;
        if (denom_m === 0) return p.createVector(0, 0, 0);

        const m = (4 * R * y) / denom_m;

        if (m > 1) { // Physically should not happen for y inside coil
           return p.createVector(0,0,0);
        }

        const { K, E } = ellipticKE(m);

        const common_factor_1 = (mu0 * I * N) / (2 * Math.PI);
        const common_factor_2 = 1 / Math.sqrt(denom_m);

        let By = 0;
        if (Math.abs(y) > 1e-9) { // Avoid division by zero
            const denom_E_term = (R - y)**2 + zp2;
            if(denom_E_term > 0) {
               By = common_factor_1 * (zp / y) * common_factor_2 * ( ((R2 + y2 + zp2) / denom_E_term) * E - K );
            }
        }

        let Bz = 0;
        const denom_E_term_z = (R - y)**2 + zp2;
        if(denom_E_term_z > 0) {
            Bz = common_factor_1 * common_factor_2 * (K - ((R2 - y2 - zp2) / denom_E_term_z) * E);
        }

        return p.createVector(0, By, Bz);
    }

    // Computes Complete Elliptic Integrals K(m) and E(m) using AGM method
    function ellipticKE(m) {
        if (m >= 1.0) m = 0.9999999999999999;
        if (m < 0) return { K: NaN, E: NaN };

        let a = 1.0;
        let b = Math.sqrt(1.0 - m);
        let c = Math.sqrt(m);
        let s = 0.0;
        let twon = 1.0;

        while (Math.abs(c) > 1e-15) {
            let temp_a = a;
            a = (a + b) / 2.0;
            b = Math.sqrt(temp_a * b);
            c = (temp_a - b) / 2.0;
            s += twon * c * c;
            twon *= 2.0;
        }

        const K = Math.PI / (2.0 * a);
        const E = K * (1.0 - s / 2.0);

        return { K, E };
    }

    function drawAxes() {
        p.push();
        p.stroke('#ff0000');
        p.fill('#ff0000');
        p.strokeWeight(1);
        p.textSize(14);

        // Y-axis (horizontal)
        let y_axis_pos = worldToPixel(0, 0);
        p.line(padding, y_axis_pos.y, canvasSize - padding, y_axis_pos.y);
        p.textAlign(p.CENTER, p.TOP);
        p.text('Y', canvasSize / 2, y_axis_pos.y + 10);

        // Z-axis (vertical)
        let z_axis_pos = worldToPixel(0, 0);
        p.line(z_axis_pos.x, padding, z_axis_pos.x, canvasSize - padding);
        p.textAlign(p.RIGHT, p.CENTER);
        p.text('Z', z_axis_pos.x - 10, canvasSize / 2);

        // Ticks
        for (let i = -0.4; i <= 0.4; i += 0.2) {
            const val = parseFloat(i.toFixed(1));
            // Y ticks
            let tick_y_pos = worldToPixel(val, 0);
            p.line(tick_y_pos.x, y_axis_pos.y - 5, tick_y_pos.x, y_axis_pos.y + 5);
            if (val !== 0) {
                 p.text(val, tick_y_pos.x, y_axis_pos.y + 10);
            }

            // Z ticks
            let tick_z_pos = worldToPixel(0, val);
            p.line(z_axis_pos.x - 5, tick_z_pos.y, z_axis_pos.x + 5, tick_z_pos.y);
             if (val !== 0) {
                p.textAlign(p.RIGHT, p.CENTER);
                p.text(val, z_axis_pos.x - 10, tick_z_pos.y);
            }
        }
        p.textAlign(p.CENTER, p.TOP);
        p.text("0.0", y_axis_pos.x + 15, y_axis_pos.y + 10);

        p.pop();
    }

    function worldToPixel(y, z) {
        const px = p.map(y, worldMin, worldMax, padding, canvasSize - padding);
        const py = p.map(z, worldMin, worldMax, canvasSize - padding, padding); // Inverted Z for screen Y
        return { x: px, y: py };
    }
}

p5sketch = new p5(sketch);

</script>
