<script>
document.addEventListener('DOMContentLoaded', () => {
    // --- STATE ---
    let currentMessage = "";
    let currentCiphertext = "";
    const WORD_LIST = ['THE', 'IDES', 'OF', 'MARCH', 'ARE', 'COME', 'EVEN', 'YOU', 'BRUTUS', 'CAESAR', 'CIPHER', 'HELLO', 'WORLD', 'SECRET', 'MESSAGE', 'ATTACK', 'DAWN', 'I', 'A'];
    // Sort by length, descending, for optimal matching
    WORD_LIST.sort((a, b) => b.length - a.length);
    const ALPHABET = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";

    // --- DOM ELEMENTS ---
    const btnToggleEncoding = document.getElementById('btn-toggle-encoding');
    const btnToggleDecoding = document.getElementById('btn-toggle-decoding');
    const panelEncoding = document.getElementById('panel-encoding');
    const panelDecoding = document.getElementById('panel-decoding');

    // Encoding Panel Elements
    const selectMessage = document.getElementById('select-message');
    const btnClearEncoding = document.getElementById('btn-clear-encoding');
    const sliderEncodingOffset = document.getElementById('slider-encoding-offset');
    const spanEncodingOffsetValue = document.getElementById('span-encoding-offset-value');
    const keyboardEncoding = document.getElementById('keyboard-encoding');
    const displayMessage = document.getElementById('display-message');
    const displayPlaintext = document.getElementById('display-plaintext');
    const displayEncodingAlphabetFrom = document.getElementById('display-encoding-alphabet-from');
    const displayEncodingAlphabetTo = document.getElementById('display-encoding-alphabet-to');
    const displayCiphertext = document.getElementById('display-ciphertext');

    // Decoding Panel Elements
    const btnIntercept = document.getElementById('btn-intercept');
    const btnClearDecoding = document.getElementById('btn-clear-decoding');
    const sliderDecodingOffset = document.getElementById('slider-decoding-offset');
    const spanDecodingOffsetValue = document.getElementById('span-decoding-offset-value');
    const keyboardDecoding = document.getElementById('keyboard-decoding');
    const displayIntercept = document.getElementById('display-intercept');
    const displayDecodingAlphabetFrom = document.getElementById('display-decoding-alphabet-from');
    const displayDecodingAlphabetTo = document.getElementById('display-decoding-alphabet-to');
    const tableDecodingOutputBody = document.querySelector('#table-decoding-output tbody');

    // --- HELPER FUNCTIONS ---
    const generateKeyboard = (container, keys, onKeyPress) => {
        container.innerHTML = ''; // Clear existing keys
        keys.forEach(key => {
            const keyElement = document.createElement('div');
            keyElement.className = 'key';
            keyElement.textContent = key;
            if (key === 'SPACE' || key === 'BACKSPACE') {
                keyElement.classList.add('special');
            }
            keyElement.addEventListener('click', () => onKeyPress(key));
            container.appendChild(keyElement);
        });
    };

    const shiftCharacter = (char, offset) => {
        if (!/^[A-Z]$/.test(char)) return char; // Only shift letters
        const code = char.charCodeAt(0);
        // Add 26 to handle negative offsets correctly in modulo
        const newCode = ((code - 65 + offset) % 26 + 26) % 26 + 65;
        return String.fromCharCode(newCode);
    };

    const formatAlphabet = (alphabet) => alphabet.split('').join(' ');

    // --- CORE LOGIC: ENCODING ---
    const updateEncoding = () => {
        const offset = parseInt(sliderEncodingOffset.value, 10);

        // 1. Generate Plaintext
        const plaintext = currentMessage.toUpperCase().replace(/ /g, 'X');
        displayPlaintext.textContent = plaintext;

        // 2. Update Alphabet Map
        const shiftedAlphabet = ALPHABET.split('').map(c => shiftCharacter(c, offset)).join('');
        displayEncodingAlphabetFrom.textContent = formatAlphabet(ALPHABET);
        displayEncodingAlphabetTo.textContent = formatAlphabet(shiftedAlphabet);

        // 3. Generate Ciphertext
        const ciphertext = plaintext.split('').map(c => shiftCharacter(c, offset)).join('');
        displayCiphertext.textContent = ciphertext;
        currentCiphertext = ciphertext; // Store for interception
    };

    // --- CORE LOGIC: DECODING ---
    const highlightWords = (text) => {
        let result = '';
        let i = 0;
        while (i < text.length) {
            let found = false;
            for (const word of WORD_LIST) {
                if (text.substring(i, i + word.length) === word) {
                    result += `<span class="highlighted">${word}</span>`;
                    i += word.length;
                    found = true;
                    break;
                }
            }
            if (!found) {
                result += text[i];
                i++;
            }
        }
        return result;
    };

    const updateDecoding = () => {
        const interceptText = displayIntercept.textContent;
        const sliderValue = parseInt(sliderDecodingOffset.value, 10);

        // 1. Update Alphabet Map
        const shiftedAlphabet = ALPHABET.split('').map(c => shiftCharacter(c, -sliderValue)).join('');
        displayDecodingAlphabetFrom.textContent = formatAlphabet(shiftedAlphabet);
        displayDecodingAlphabetTo.textContent = formatAlphabet(ALPHABET);

        // 2. Generate Decoding Table
        tableDecodingOutputBody.innerHTML = '';
        const offsetsToTest = [sliderValue - 2, sliderValue - 1, sliderValue, sliderValue + 1, sliderValue + 2];

        offsetsToTest.forEach(d => {
            const decryptedText = interceptText.split('').map(c => shiftCharacter(c, -d)).join('');
            const highlightedText = highlightWords(decryptedText);

            const row = document.createElement('tr');

            const offsetCell = document.createElement('td');
            offsetCell.textContent = d;
            row.appendChild(offsetCell);

            const decodingCell = document.createElement('td');
            decodingCell.innerHTML = highlightedText;
            row.appendChild(decodingCell);

            tableDecodingOutputBody.appendChild(row);
        });
    };

    // --- EVENT LISTENERS ---

    // Tab Controls
    btnToggleEncoding.addEventListener('click', () => {
        panelEncoding.style.display = 'block';
        panelDecoding.style.display = 'none';
        btnToggleEncoding.classList.add('active');
        btnToggleDecoding.classList.remove('active');
    });

    btnToggleDecoding.addEventListener('click', () => {
        panelEncoding.style.display = 'none';
        panelDecoding.style.display = 'block';
        btnToggleEncoding.classList.remove('active');
        btnToggleDecoding.classList.add('active');
    });

    // Encoding Panel Listeners
    selectMessage.addEventListener('change', (e) => {
        currentMessage = e.target.value;
        displayMessage.textContent = currentMessage;
        updateEncoding();
    });

    sliderEncodingOffset.addEventListener('input', (e) => {
        spanEncodingOffsetValue.textContent = e.target.value;
        updateEncoding();
    });

    const handleEncodingKeyPress = (key) => {
        if (key === 'BACKSPACE') {
            currentMessage = currentMessage.slice(0, -1);
        } else if (currentMessage.length < 32) {
            currentMessage += (key === 'SPACE' ? ' ' : key);
        }
        displayMessage.textContent = currentMessage;
        updateEncoding();
    };

    btnClearEncoding.addEventListener('click', () => {
        currentMessage = "";
        currentCiphertext = "";
        displayMessage.textContent = "";
        displayPlaintext.textContent = "";
        displayCiphertext.textContent = "";
        selectMessage.value = "";
        sliderEncodingOffset.value = 0;
        spanEncodingOffsetValue.textContent = "0";
        updateEncoding();
    });

    // Decoding Panel Listeners
    btnIntercept.addEventListener('click', () => {
        displayIntercept.textContent = currentCiphertext;
        updateDecoding();
    });

    sliderDecodingOffset.addEventListener('input', (e) => {
        spanDecodingOffsetValue.textContent = e.target.value;
        updateDecoding();
    });

    const handleDecodingKeyPress = (key) => {
        let text = displayIntercept.textContent;
        if (key === 'BACKSPACE') {
            text = text.slice(0, -1);
        } else if (text.length < 32) {
            text += key;
        }
        displayIntercept.textContent = text;
        updateDecoding();
    };

    btnClearDecoding.addEventListener('click', () => {
        displayIntercept.textContent = "";
        sliderDecodingOffset.value = 0;
        spanDecodingOffsetValue.textContent = "0";
        updateDecoding();
    });


    // --- INITIALIZATION ---
    // Generate Keyboards
    const encodingKeys = [...ALPHABET.split(''), 'SPACE', 'BACKSPACE'];
    generateKeyboard(keyboardEncoding, encodingKeys, handleEncodingKeyPress);

    const decodingKeys = [...ALPHABET.split(''), 'BACKSPACE'];
    generateKeyboard(keyboardDecoding, decodingKeys, handleDecodingKeyPress);

    // Initial UI state
    displayEncodingAlphabetFrom.textContent = formatAlphabet(ALPHABET);
    displayEncodingAlphabetTo.textContent = formatAlphabet(ALPHABET);
    displayDecodingAlphabetFrom.textContent = formatAlphabet(ALPHABET);
    displayDecodingAlphabetTo.textContent = formatAlphabet(ALPHABET);
    updateEncoding();
    updateDecoding();
});
</script>

</body>
</html>
