                // Use a p5.Graphics buffer to render the text and get pixel data
                const pg = p.createGraphics(imageRes, imageRes);
                pg.background(255); // White background
                pg.fill(0); // Black text
                pg.textAlign(p.CENTER, p.CENTER);
                pg.textFont('monospace');
                pg.textSize(imageRes * 0.9);
                // Adjust text position for better centering
                pg.text(letter, pg.width / 2, pg.height / 2 + imageRes*0.05);

                pg.loadPixels();
                sourceGrid = Array(imageRes).fill(0).map(() => Array(imageRes).fill(0));
                for (let y = 0; y < imageRes; y++) {
                    for (let x = 0; x < imageRes; x++) {
                        const index = (y * imageRes + x) * 4;
                        // Use red channel, < 128 is considered black (1)
                        sourceGrid[y][x] = (pg.pixels[index] < 128) ? 1 : 0;
                    }
                }
            }

            function generateKernel() {
                const k = parseInt(convolverSize);
                const dim = 2 * k + 1;
                let kernel = Array(dim).fill(0).map(() => Array(dim).fill(0));

                if (filterType === 'blur') {
                    const val = 1.0 / (dim * dim);
                    for (let y = 0; y < dim; y++) {
                        for (let x = 0; x < dim; x++) {
                            kernel[y][x] = val;
                        }
                    }
                } else if (filterType === 'shadow') {
                    // Create an offset blur (down and to the right)
                    // The offset is relative to the kernel's center (k, k)
                    const norm = k * k;
                    if (norm > 0) {
                        for (let y = k + 1; y <= 2*k; y++) {
                             for (let x = k + 1; x <= 2*k; x++) {
                                 // Check bounds just in case
                                 if (y < dim && x < dim) {
                                      kernel[y][x] = 1.0 / norm;
                                 }
                             }
                        }
                    }
                }
                return kernel;
            }

            function performConvolution(kernel) {
                resultGrid = Array(imageRes).fill(0).map(() => Array(imageRes).fill(0));
                const k = parseInt(convolverSize);
                const dim = 2 * k + 1;

                for (let y = 0; y < imageRes; y++) {
                    for (let x = 0; x < imageRes; x++) {
                        let sum = 0;
                        for (let ky = 0; ky < dim; ky++) {
                            for (let kx = 0; kx < dim; kx++) {
                                // Find the corresponding pixel in the source grid
                                const srcX = x + (kx - k);
                                const srcY = y + (ky - k);

                                let val = 0;
                                // Handle edges: treat out-of-bounds as 0 (white)
                                if (srcX >= 0 && srcX < imageRes && srcY >= 0 && srcY < imageRes) {
                                    val = sourceGrid[srcY][srcX];
                                }

                                sum += val * kernel[ky][kx];
                            }
                        }
                        resultGrid[y][x] = sum;
                    }
                }
            }

            p.draw = () => {
                p.background(255);
                if (!sourceGrid || !resultGrid) return;

                const beforeX = padding;
                const afterX = canvasWidth / 2 + padding;
                const yPos = (canvasHeight - imgDisplaySize) / 2;

                // Draw "Before" image
                drawGrid(sourceGrid, beforeX, yPos, (val) => p.color(255 * (1 - val)));

                // Draw "After" image
                drawGrid(resultGrid, afterX, yPos, (val) => p.color(255 * (1 - val)));
            };

            function drawGrid(grid, startX, startY, colorFunc) {
                const res = grid.length;
                const pixelSize = imgDisplaySize / res;
                p.push();
                p.strokeWeight(1);
                p.stroke(220); // Light gray grid lines
                p.noFill();
                p.rect(startX, startY, imgDisplaySize, imgDisplaySize);
                p.noStroke();

                for (let y = 0; y < res; y++) {
                    for (let x = 0; x < res; x++) {
                        const val = grid[y][x];
                        p.fill(colorFunc(val));
                        p.rect(startX + x * pixelSize, startY + y * pixelSize, pixelSize, pixelSize);
                    }
                }
                p.pop();
            }
        };

        new p5(sketch);
    </script>
</body>
