<script>
document.addEventListener('DOMContentLoaded', () => {
    // --- STATE AND CONSTANTS ---
    let currentView = 'crystallography';
    const NUM_RESIDUES = 400;
    const AMINO_ACIDS = {
        'A': 'ALA', 'C': 'CYS', 'D': 'ASP', 'E': 'GLU', 'F': 'PHE',
        'G': 'GLY', 'H': 'HIS', 'I': 'ILE', 'K': 'LYS', 'L': 'LEU',
        'M': 'MET', 'N': 'ASN', 'P': 'PRO', 'Q': 'GLN', 'R': 'ARG',
        'S': 'SER', 'T': 'THR', 'V': 'VAL', 'W': 'TRP', 'Y': 'TYR'
    };
    const AMINO_ACID_CODES = Object.keys(AMINO_ACIDS);

    let proteinData = [];
    let distanceMatrix = [];

    // --- DOM Elements ---
    const controls = {
        btnCrystallography: document.getElementById('btn-crystallography'),
        btnContactMap: document.getElementById('btn-contact-map'),
        checkLateralChains: document.getElementById('checkbox-lateral-chains'),
        sliderGrayLevel: document.getElementById('slider-gray-level'),
        sliderBrowseSeq: document.getElementById('slider-browse-sequence'),
        selectResidue: document.getElementById('select-residue'),
        btnJumpUp: document.getElementById('jump-up'),
        btnJumpDown: document.getElementById('jump-down'),
        sliderResidueX: document.getElementById('slider-residue-x'),
        sliderResidueY: document.getElementById('slider-residue-y'),
        sliderRmsd: document.getElementById('slider-rmsd'),
        sliderPlotSize: document.getElementById('slider-plot-size'),
        btnReset: document.getElementById('btn-reset'),
        infoDisplay: document.getElementById('info-display'),
        canvasContainer: document.getElementById('canvas-container'),
        // Value spans
        grayLevelValue: document.getElementById('gray-level-value'),
        browseSeqValue: document.getElementById('browse-sequence-value'),
        residueXValue: document.getElementById('residue-x-value'),
        residueYValue: document.getElementById('residue-y-value'),
        rmsdValue: document.getElementById('rmsd-value'),
        plotSizeValue: document.getElementById('plot-size-value'),
    };

    // --- Visualization Objects ---
    let three = { scene: null, camera: null, renderer: null, controls: null, atoms: new THREE.Group(), boxHelper: null, animationFrameId: null };
    let p5Instance = null;

    // --- DATA GENERATION ---
    function generateProteinData(numResidues) {
        const data = [];
        let currentPos = new THREE.Vector3(0, 0, 0);
        const stepSize = 3.8; // Approx distance between CA atoms

        for (let i = 1; i <= numResidues; i++) {
            const resCode = AMINO_ACID_CODES[Math.floor(Math.random() * AMINO_ACID_CODES.length)];
            const resName = AMINO_ACIDS[resCode];

            // Random walk for CA
            const randomVec = new THREE.Vector3(Math.random() - 0.5, Math.random() - 0.5, Math.random() - 0.5).normalize();
            currentPos.add(randomVec.multiplyScalar(stepSize));

            const caAtom = { atomName: 'CA', ...currentPos };

            // Fixed offsets for other atoms relative to CA
            const atoms = [
                caAtom,
                { atomName: 'N',  x: caAtom.x + 1, y: caAtom.y + 1, z: caAtom.z },
                { atomName: 'C',  x: caAtom.x - 1, y: caAtom.y - 1, z: caAtom.z },
                { atomName: 'O',  x: caAtom.x - 2, y: caAtom.y - 1, z: caAtom.z },
                { atomName: 'CB', x: caAtom.x, y: caAtom.y, z: caAtom.z + 1.5 }
            ];

            data.push({ resSeq: i, resName, resCode, atoms });
        }
        return data;
    }

    function precomputeDistances() {
        distanceMatrix = Array(NUM_RESIDUES).fill(0).map(() => Array(NUM_RESIDUES).fill(0));
        for (let i = 0; i < NUM_RESIDUES; i++) {
            for (let j = i; j < NUM_RESIDUES; j++) {
                const ca1 = proteinData[i].atoms.find(a => a.atomName === 'CA');
                const ca2 = proteinData[j].atoms.find(a => a.atomName === 'CA');
                const v1 = new THREE.Vector3(ca1.x, ca1.y, ca1.z);
                const v2 = new THREE.Vector3(ca2.x, ca2.y, ca2.z);
                const dist = v1.distanceTo(v2);
                distanceMatrix[i][j] = dist;
                distanceMatrix[j][i] = dist;
            }
        }
    }

    // --- UI & CONTROL LOGIC ---
    function initControls() {
        // Populate select
        AMINO_ACID_CODES.forEach(code => {
            const option = document.createElement('option');
            option.value = code;
            option.textContent = code;
            controls.selectResidue.appendChild(option);
        });

        // Event Listeners
        const sliders = [
            'sliderGrayLevel', 'sliderBrowseSeq', 'sliderResidueX', 
            'sliderResidueY', 'sliderRmsd', 'sliderPlotSize'
        ];
        sliders.forEach(key => {
            controls[key].addEventListener('input', handleSliderChange);
        });

        controls.checkLateralChains.addEventListener('change', redrawCurrentView);
        controls.btnCrystallography.addEventListener('click', () => switchView('crystallography'));
        controls.btnContactMap.addEventListener('click', () => switchView('contact map'));
        controls.btnReset.addEventListener('click', resetCamera);

        controls.btnJumpUp.addEventListener('click', () => jumpToResidue(-1));
        controls.btnJumpDown.addEventListener('click', () => jumpToResidue(1));

        updateValueDisplays();
    }

    function handleSliderChange(e) {
        updateValueDisplays();
        updateInfoDisplay();
        if (e.target.id === 'slider-plot-size') {
            resizeCanvas();
        } else {
            redrawCurrentView();
        }
    }

    function updateValueDisplays() {
        controls.grayLevelValue.textContent = parseFloat(controls.sliderGrayLevel.value).toFixed(2);
        controls.browseSeqValue.textContent = controls.sliderBrowseSeq.value;
        controls.residueXValue.textContent = controls.sliderResidueX.value;
        controls.residueYValue.textContent = controls.sliderResidueY.value;
        controls.rmsdValue.textContent = parseFloat(controls.sliderRmsd.value).toFixed(1);
        controls.plotSizeValue.textContent = controls.sliderPlotSize.value;
    }

    function updateInfoDisplay() {
        const resA_idx = parseInt(controls.sliderBrowseSeq.value) - 1;
        const resX_idx = parseInt(controls.sliderResidueX.value) - 1;
        const resY_idx = parseInt(controls.sliderResidueY.value) - 1;

        const resA = proteinData[resA_idx];
        const resX = proteinData[resX_idx];
        const resY = proteinData[resY_idx];

        const formatRes = r => `${r.resName}${r.resSeq}`;

        controls.infoDisplay.textContent = `Res A: ${formatRes(resA)} | Res X: ${formatRes(resX)} | Res Y: ${formatRes(resY)}`;
    }

    function jumpToResidue(direction) {
        const targetCode = controls.selectResidue.value;
        let currentIndex = parseInt(controls.sliderBrowseSeq.value) - 1;
        let foundIndex = -1;

        for (let i = 1; i < NUM_RESIDUES; i++) {
            let nextIndex = (currentIndex + i * direction + NUM_RESIDUES) % NUM_RESIDUES;
            if (proteinData[nextIndex].resCode === targetCode) {
                foundIndex = nextIndex;
                break;
            }
        }

        if (foundIndex !== -1) {
            controls.sliderBrowseSeq.value = foundIndex + 1;
            // Manually trigger events
            controls.sliderBrowseSeq.dispatchEvent(new Event('input'));
        }
    }

    function switchView(viewName) {
        if (currentView === viewName) return;

        currentView = viewName;
        if (currentView === 'crystallography') {
            controls.btnCrystallography.classList.add('active');
            controls.btnContactMap.classList.remove('active');
            destroyP5();
            setupThree();
        } else {
            controls.btnContactMap.classList.add('active');
            controls.btnCrystallography.classList.remove('active');
            destroyThree();
            setupP5();
        }
        redrawCurrentView();
    }

    function redrawCurrentView() {
        if (currentView === 'crystallography') {
            drawCrystallography();
        } else if (p5Instance) {
            p5Instance.redraw();
        }
    }

    function resizeCanvas() {
        const size = parseInt(controls.sliderPlotSize.value);
        if (currentView === 'crystallography' && three.renderer) {
            three.camera.aspect = 1;
            three.camera.updateProjectionMatrix();
            three.renderer.setSize(size, size);
        } else if (p5Instance) {
            p5Instance.resizeCanvas(size, size);
            p5Instance.redraw();
        }
    }

    // --- 3D VIEW (Three.js) ---
    function setupThree() {
        const size = parseInt(controls.sliderPlotSize.value);

        three.scene = new THREE.Scene();
        three.scene.background = new THREE.Color(0xffffff);

        three.camera = new THREE.PerspectiveCamera(50, 1, 0.1, 1000);
        const center = new THREE.Box3().setFromObject(new THREE.Group().add(...proteinData.flatMap(r => r.atoms.map(a => new THREE.Object3D().position.set(a.x, a.y, a.z))))).getCenter(new THREE.Vector3());
        three.camera.position.z = center.z + 200;

        three.renderer = new THREE.WebGLRenderer({ antialias: true });
        three.renderer.setSize(size, size);
        controls.canvasContainer.appendChild(three.renderer.domElement);

        three.controls = new THREE.OrbitControls(three.camera, three.renderer.domElement);
        three.controls.target.copy(center);
        three.controls.update();

        const ambientLight = new THREE.AmbientLight(0x404040, 2);
        const directionalLight = new THREE.DirectionalLight(0xffffff, 1);
        directionalLight.position.set(1, 1, 1);
        three.scene.add(ambientLight, directionalLight);

        three.scene.add(three.atoms);

        const boundingBox = new THREE.Box3().setFromObject(three.atoms);
        if(!boundingBox.isEmpty()){
            three.boxHelper = new THREE.BoxHelper(boundingBox, 0x888888);
            three.scene.add(three.boxHelper);
        }

        animateThree();
    }

    function drawCrystallography() {
        if (!three.scene) return;

        // Clear previous atoms
        while(three.atoms.children.length > 0){ 
            three.atoms.remove(three.atoms.children[0]); 
        }
        if (three.boxHelper) {
            three.scene.remove(three.boxHelper);
            three.boxHelper.dispose();
        }

        const showLateralChains = controls.checkLateralChains.checked;
        const grayLevel = parseFloat(controls.sliderGrayLevel.value);
        const browseSeq = parseInt(controls.sliderBrowseSeq.value);
        const resXSeq = parseInt(controls.sliderResidueX.value);
        const resYSeq = parseInt(controls.sliderResidueY.value);

        const blueColor = new THREE.Color(0x0000ff).lerp(new THREE.Color(0x000000), 1 - grayLevel);
        const sphereGeom = new THREE.SphereGeometry(0.8, 16, 16);

        proteinData.forEach(residue => {
            const atomsToDraw = showLateralChains ? residue.atoms : [residue.atoms.find(a => a.atomName === 'CA')];
            atomsToDraw.forEach(atom => {
                let color = blueColor;
                if (residue.resSeq === browseSeq) {
                    color = 0xffff00; // Yellow
                }
                if ((residue.resSeq === resXSeq || residue.resSeq === resYSeq) && atom.atomName === 'CA') {
                    color = 0xff0000; // Red
                }

                const material = new THREE.MeshLambertMaterial({ color });
                const sphere = new THREE.Mesh(sphereGeom, material);
                sphere.position.set(atom.x, atom.y, atom.z);
                three.atoms.add(sphere);
            });
        });

        // Update bounding box
        const boundingBox = new THREE.Box3().setFromObject(three.atoms);
        if(!boundingBox.isEmpty()){
             three.boxHelper = new THREE.BoxHelper(three.atoms, 0x888888);
             three.scene.add(three.boxHelper);
        }
    }

    function animateThree() {
        three.animationFrameId = requestAnimationFrame(animateThree);
        three.controls.update();
        three.renderer.render(three.scene, three.camera);
    }

    function destroyThree() {
        if (three.animationFrameId) cancelAnimationFrame(three.animationFrameId);
        if (three.renderer) {
            three.renderer.dispose();
            controls.canvasContainer.innerHTML = '';
        }
        three = { scene: null, camera: null, renderer: null, controls: null, atoms: new THREE.Group(), boxHelper: null, animationFrameId: null };
    }

    function resetCamera() {
        if (currentView === 'crystallography' && three.controls) {
            three.controls.reset();
        }
    }

    // --- 2D VIEW (p5.js) ---
    function setupP5() {
        const sketch = (p) => {
            p.setup = () => {
                const size = parseInt(controls.sliderPlotSize.value);
                p.createCanvas(size, size);
                p.noLoop();
            };

            p.draw = () => {
                const size = p.width;
                const margin = 30;
                const plotArea = size - margin * 2;

                p.background(255);
                p.translate(margin, margin);

                // Draw axes and labels
                p.stroke(200);
                p.strokeWeight(1);
                p.line(0, plotArea, plotArea, plotArea); // Bottom
                p.line(0, 0, 0, plotArea); // Left
                p.line(plotArea, 0, plotArea, plotArea); // Right
                p.line(0, 0, plotArea, 0); // Top

                p.fill(100);
                p.noStroke();
                p.textAlign(p.CENTER, p.CENTER);
                for (let i = 0; i <= NUM_RESIDUES; i += 100) {
                    const tick = i === 0 ? 1 : i;
                    const x = p.map(tick, 1, NUM_RESIDUES, 0, plotArea);
                    p.text(tick, x, plotArea + 10); // Bottom
                    p.text(tick, x, -10); // Top
                    p.text(tick, -15, x); // Left
                    p.text(tick, plotArea + 15, x); // Right
                }

                // Draw contact points
                const rmsd = parseFloat(controls.sliderRmsd.value);
                p.stroke(0);
                p.strokeWeight(1.5);
                for (let i = 0; i < NUM_RESIDUES; i++) {
                    for (let j = 0; j < NUM_RESIDUES; j++) {
                        if (distanceMatrix[i][j] <= rmsd) {
                            const x = p.map(i + 1, 1, NUM_RESIDUES, 0, plotArea);
                            const y = p.map(j + 1, 1, NUM_RESIDUES, 0, plotArea);
                            p.point(x, y);
                        }
                    }
                }

                // Draw crosshairs
                const resX = parseInt(controls.sliderResidueX.value);
                const resY = parseInt(controls.sliderResidueY.value);
                const lineX = p.map(resX, 1, NUM_RESIDUES, 0, plotArea);
                const lineY = p.map(resY, 1, NUM_RESIDUES, 0, plotArea);

                p.stroke(255, 0, 0);
                p.strokeWeight(1);
                p.line(lineX, 0, lineX, plotArea); // Vertical
                p.line(0, lineY, plotArea, lineY); // Horizontal
            };
        };

        p5Instance = new p5(sketch, controls.canvasContainer);
    }

    function destroyP5() {
        if (p5Instance) {
            p5Instance.remove();
            p5Instance = null;
        }
    }

    // --- INITIALIZATION ---
    function main() {
        proteinData = generateProteinData(NUM_RESIDUES);
        precomputeDistances();
        initControls();
        updateInfoDisplay();
        setupThree();
        redrawCurrentView();
    }

    main();
});
</script>

</body>
</html>
