            <label for="input-b">b</label>
            <input type="number" id="input-b" value="8" min="1" max="20" step="1">
            <label for="slider-c">c</label>
            <input type="range" id="slider-c" value="24" min="1" max="112" step="1">
            <span id="display-c">24</span>
        </div>
    </div>

    <div id="visualization-area">
        <p id="equation-text"></p>
        <div id="canvas-container"></div>
    </div>
</div>

<script>
document.addEventListener('DOMContentLoaded', () => {

    const sketch = (p) => {
        // DOM Elements
        let infoToggle, infoContent;
        let checkboxMode, checkboxDisplay;
        let inputA, inputB, sliderC, displayC;
        let equationText, canvasContainer;

        // State variables
        let a, b, c;
        let isDotsMode, isHighestNonsolvableMode;
        let areCoprime;

        const margin = { top: 40, right: 40, bottom: 40, left: 40 };

        p.setup = () => {
            const canvas = p.createCanvas(550, 550);
            canvas.parent('canvas-container');
            p.noLoop(); // We will redraw manually on input change

            // Get all DOM elements
            infoToggle = document.getElementById('info-toggle');
            infoContent = document.getElementById('info-content');
            checkboxMode = document.getElementById('checkbox-mode');
            checkboxDisplay = document.getElementById('checkbox-display');
            inputA = document.getElementById('input-a');
            inputB = document.getElementById('input-b');
            sliderC = document.getElementById('slider-c');
            displayC = document.getElementById('display-c');
            equationText = document.getElementById('equation-text');

            // Attach event listeners
            infoToggle.addEventListener('click', toggleInfo);
            [inputA, inputB, sliderC, checkboxMode, checkboxDisplay].forEach(el => {
                el.addEventListener('input', updateVisualization);
            });

            // Initial update
            updateVisualization();
        };

        const gcd = (x, y) => {
            x = Math.abs(x);
            y = Math.abs(y);
            while(y) {
                let t = y;
                y = x % y;
                x = t;
            }
            return x;
        };

        const toggleInfo = () => {
            const isHidden = infoContent.style.display === 'none';
            infoContent.style.display = isHidden ? 'block' : 'none';
            infoToggle.textContent = isHidden ? '-' : '+';
        };

        const updateVisualization = () => {
            // Read all current values from controls
            a = parseInt(inputA.value) || 1;
            b = parseInt(inputB.value) || 1;
            isDotsMode = checkboxMode.checked;
            isHighestNonsolvableMode = checkboxDisplay.checked;

            areCoprime = gcd(a, b) === 1;

            if (!areCoprime) {
                equationText.textContent = "Numbers a and b should be coprime!";
                sliderC.disabled = true;
            } else {
                sliderC.max = a * b * 2;

                if (isHighestNonsolvableMode) {
                    sliderC.disabled = true;
                    const frobeniusNumber = a * b - a - b;
                    c = frobeniusNumber;
                    displayC.textContent = `F=${c}`;
                    equationText.innerHTML = `Frobenius number: F = ${a}&times;${b} - ${a} - ${b} = ${c}.`;
                } else {
                    sliderC.disabled = false;
                    c = parseInt(sliderC.value);
                    displayC.textContent = c;
                    equationText.innerHTML = `Current equation: ${a}x + ${b}y = ${c}.`;
                }
            }
            p.redraw();
        };

        p.draw = () => {
            p.background(255);

            if (!areCoprime) {
                return; // Don't draw grid if not coprime
            }

            const drawWidth = p.width - margin.left - margin.right;
            const drawHeight = p.height - margin.top - margin.bottom;

            const gridMaxX = Math.max(a, b);
            const gridMaxY = Math.max(a, b);

            const xCell = drawWidth / gridMaxX;
            const yCell = drawHeight / gridMaxY;

            p.push();
            p.translate(margin.left, p.height - margin.bottom);
            p.scale(1, -1); // Flip Y-axis to point upwards

            // Draw Axes Labels (before main loop to be underneath)
            p.stroke(0);
            p.fill(0, 0, 255); // Blue for labels
            p.textSize(12);
            p.textAlign(p.CENTER, p.CENTER);

            for (let i = 0; i <= gridMaxX; i++) {
                p.push();
                p.scale(1, -1); // Flip text back
                p.text(i, i * xCell, 20);
                p.pop();
            }
            for (let i = 0; i <= gridMaxY; i++) {
                p.push();
                p.scale(1, -1);
                p.textAlign(p.RIGHT, p.CENTER);
                p.text(i, -10, -i * yCell);
                p.pop();
            }

            // --- Visualization Logic ---
            if (isDotsMode) {
                // Draw all integer points (black dots)
                p.fill(0);
                p.noStroke();
                for (let x = 0; x <= b; x++) {
                    for (let y = 0; y <= a; y++) {
                        if (x * xCell > drawWidth + 1 || y * yCell > drawHeight + 1) continue;
                        p.ellipse(x * (drawWidth/b), y * (drawHeight/a), 5, 5);
                    }
                }

                // Draw the line ax + by = c
                p.stroke(255, 0, 0);
                p.strokeWeight(2);
                const x1 = c / a, y1 = 0;
                const x2 = 0, y2 = c / b;
                p.line(x1 * (drawWidth/b), y1 * (drawHeight/a), x2 * (drawWidth/b), y2 * (drawHeight/a));

                // Draw solution points (green dots)
                p.fill(0, 200, 0);
                p.noStroke();
                 for (let x = 0; x <= b; x++) {
                    for (let y = 0; y <= a; y++) {
                        if (a * x + b * y === c) {
                             if (x * xCell > drawWidth + 1 || y * yCell > drawHeight + 1) continue;
                             p.ellipse(x * (drawWidth/b), y * (drawHeight/a), 12, 12);
                        }
                    }
                }

            } else { // Numbers Mode
                p.textSize(14);
                p.textAlign(p.CENTER, p.CENTER);

                for (let x = 0; x <= b; x++) {
                    for (let y = 0; y <= a; y++) {
                        if (x * xCell > drawWidth + 1 || y * yCell > drawHeight + 1) continue;

                        const val = a * x + b * y;

                        if (val === c && !isHighestNonsolvableMode) {
                            p.fill(0, 180, 0); // Green for solution
                        } else {
                            p.fill(0); // Black for others
                        }

                        p.push();
                        p.scale(1, -1); // Flip text back
                        p.text(val, x * (drawWidth/b), -y * (drawHeight/a));
                        p.pop();
                    }
                }
            }

            p.pop(); // Restore original transformation
        };
    };

    new p5(sketch);
});
</script>

</body>
</html>
