                    const th = document.createElement('th');
                    th.textContent = item.name;
                    headerRow.appendChild(th);

                    const td = statusRow.insertCell();
                    td.textContent = 'False';
                    td.id = `status-cell-${index}`;
                    td.className = 'status-cell';
                });
            };

            const updateTableCell = (index) => {
                const cell = document.getElementById(`status-cell-${index}`);
                if (cell) {
                    cell.textContent = 'True';
                    cell.classList.add('found');
                }
            };

            const selectHints = () => {
                const unfound = currentData.filter(item => !item.found);
                // Shuffle unfound items
                for (let i = unfound.length - 1; i > 0; i--) {
                    const j = Math.floor(Math.random() * (i + 1));
                    [unfound[i], unfound[j]] = [unfound[j], unfound[i]];
                }
                hintedItems = unfound.slice(0, 3);
            };

            // --- EVENT HANDLERS ---

            const handleQuizTypeChange = (e) => {
                resetQuiz(e.target.value);
            };

            const handleShowSomeChange = () => {
                if(checkShowSome.checked) {
                    selectHints();
                } else {
                    hintedItems = [];
                }
                p.redraw();
            };

            window.onresize = () => {
                const canvasContainer = document.getElementById('canvas-container');
                p.resizeCanvas(canvasContainer.offsetWidth, canvasContainer.offsetHeight);
            }
        };

        new p5(sketch, document.getElementById('canvas-container'));
    </script>
</body>
