    </div>

    <script>
        document.addEventListener('DOMContentLoaded', () => {

            // --- STATE VARIABLES ---
            let currentTier = 5;
            let showBinary = false;
            let showNodeChars = true;
            let p5Instance;

            // --- DATA ---
            const treeData = [
                { id: 'I',  label: 'I', freq: 5, tier: 1, pos: {x: 100, y: 450}, children: [] },
                { id: 'S',  label: 'S', freq: 4, tier: 1, pos: {x: 180, y: 450}, children: [] },
                { id: 'P',  label: 'P', freq: 2, tier: 1, pos: {x: 260, y: 450}, children: [] },
                { id: 'R',  label: 'R', freq: 2, tier: 1, pos: {x: 340, y: 450}, children: [] },
                { id: 'M',  label: 'M', freq: 1, tier: 1, pos: {x: 420, y: 450}, children: [] },
                { id: 'V',  label: 'V', freq: 1, tier: 1, pos: {x: 500, y: 450}, children: [] },
                { id: 'E',  label: 'E', freq: 1, tier: 1, pos: {x: 580, y: 450}, children: [] },
                { id: '_',  label: '_', freq: 1, tier: 1, pos: {x: 660, y: 450}, children: [] },
                { id: 'IS', label: 'IS', freq: 9, tier: 2, pos: {x: 140, y: 350}, children: ['I', 'S'] },
                { id: 'PR', label: 'PR', freq: 4, tier: 2, pos: {x: 300, y: 350}, children: ['P', 'R'] },
                { id: 'MV', label: 'MV', freq: 2, tier: 2, pos: {x: 460, y: 350}, children: ['M', 'V'] },
                { id: 'E_', label: 'E_', freq: 2, tier: 2, pos: {x: 620, y: 350}, children: ['E', '_'] },
                { id: 'MVE_', label: 'MVE_', freq: 4, tier: 3, pos: {x: 540, y: 250}, children: ['MV', 'E_'] },
                { id: 'PRMVE_', label: 'PRMVE_', freq: 8, tier: 4, pos: {x: 420, y: 150}, children: ['PR', 'MVE_'] },
                { id: 'ROOT', label: 'ISPRMVE_', freq: 17, tier: 5, pos: {x: 280, y: 50}, children: ['IS', 'PRMVE_'] }
            ];
            const nodeMap = new Map(treeData.map(node => [node.id, node]));

            const explanationTexts = {
                1: "After determining the frequency of each letter, we draw the tree bottom up, one tier at a time.",
                2: "We continue grouping until we get to the highest tier.",
                3: "We continue grouping until we get to the highest tier.",
                4: "We continue grouping until we get to the highest tier.",
                5: "Once at the top, the total frequency becomes 17, which is the total number of characters."
            };

            const encodingTableHTML = `
                <p>I = 00 | S = 01</p>
                <p>P = 100 | R = 101</p>
                <p>M = 1100 | V = 1101</p>
                <p>E = 1110 | _ = 1111</p>
            `;
            const encodedString = "1100000101000101001001000011111010011011110101";

            // --- DOM ELEMENTS ---
            const tierButtons = document.querySelectorAll('#tiers-container button');
            const checkboxBinary = document.getElementById('checkbox-binary');
            const radioCharsYes = document.getElementById('radio-chars-yes');
            const radioCharsNo = document.getElementById('radio-chars-no');
            const explanationTextElem = document.getElementById('explanation-text');
            const encodingTableElem = document.getElementById('encoding-table');
            const encodedTextElem =
                document.getElementById('encoded-text');

            // --- P5 SKETCH ---
            const sketch = (p) => {
                p.setup = () => {
                    const canvasContainer = document.getElementById('canvas-area');
                    const canvas = p.createCanvas(800, 500);
                    canvas.parent(canvasContainer);
                    p.noLoop();
                    p5Instance = p; // Store p5 instance for redrawing
                    updateUI(); // Initial UI and canvas draw
                };

                p.draw = () => {
                    p.background(255);

                    // Draw edges
                    treeData.forEach(node => {
                        if (node.children.length > 0 && node.tier <= currentTier) {
                            const parentPos = node.pos;
                            node.children.forEach((childId, index) => {
                                const childNode = nodeMap.get(childId);
                                if (childNode && childNode.tier <= currentTier) {
                                    const childPos = childNode.pos;
                                    p.stroke('#C70000');
                                    p.strokeWeight(2);
                                    p.line(parentPos.x, parentPos.y, childPos.x, childPos.y);

                                    if (showBinary) {
                                        p.noStroke();
                                        p.fill(0);
                                        p.textSize(16);
                                        p.textAlign(p.CENTER, p.CENTER);
                                        const midX = (parentPos.x + childPos.x) / 2;
                                        const midY = (parentPos.y + childPos.y) / 2;
                                        p.push();
                                        p.translate(midX, midY);
                                        p.text(index === 0 ? '0' : '1', index === 0 ? -15 : 15, -10);
                                        p.pop();
                                    }
                                }
                            });
                        }
                    });

                    // Draw nodes
                    treeData.forEach(node => {
                        if (node.tier <= currentTier) {
                            p.stroke('#C70000');
                            p.strokeWeight(1);
                            p.fill('#FEFDD5');

                            p.textFont('monospace');
                            p.textSize(14);
                            p.textAlign(p.CENTER, p.CENTER);

                            let label;
                            if (node.children.length === 0) { // Leaf node
                                label = `${node.label}(${node.freq})`;
                            } else { // Internal node
                                if (showNodeChars) {
                                    label = `${node.label}(${node.freq})`;
                                } else {
                                    label = `(${node.freq})`;
                                }
                            }

                            const textW = p.textWidth(label) + 20;
                            const textH = 30;

                            p.rectMode(p.CENTER);
                            p.rect(node.pos.x, node.pos.y, textW, textH, 5);

                            p.fill(0);
                            p.noStroke();
                            p.text(label, node.pos.x, node.pos.y + 2); // Small y-offset for better centering
                        }
                    });
                };
            };

            // --- UI LOGIC ---
            function updateUI() {
                // Update tier buttons active state
                tierButtons.forEach(btn => {
                    if (parseInt(btn.id.split('-')[2]) === currentTier) {
                        btn.classList.add('active');
                    } else {
                        btn.classList.remove('active');
                    }
                });

                // Update explanation text
                explanationTextElem.textContent = explanationTexts[currentTier];

                // Update visibility of table and encoded text
                encodingTableElem.style.display = currentTier >= 2 ? 'block' : 'none';
                encodedTextElem.style.display = currentTier === 5 ? 'block' : 'none';

                // Redraw canvas
                if (p5Instance) {
                    p5Instance.redraw();
                }
            }

            function redrawCanvasOnly() {
                if (p5Instance) {
                    p5Instance.redraw();
                }
            }

            // --- EVENT LISTENERS ---
            tierButtons.forEach(btn => {
                btn.addEventListener('click', () => {
                    currentTier = parseInt(btn.id.split('-')[2]);
                    updateUI();
                });
            });

            checkboxBinary.addEventListener('change', (e) => {
                showBinary = e.target.checked;
                redrawCanvasOnly();
            });

            document.querySelectorAll('input[name="chars-toggle"]').forEach(radio => {
                radio.addEventListener('change', (e) => {
                    showNodeChars = e.target.value === 'yes';
                    redrawCanvasOnly();
                });
            });

            // --- INITIALIZATION ---
            document.getElementById('radio-chars-yes').checked = showNodeChars;
            document.getElementById('radio-chars-no').checked = !showNodeChars;
            document.getElementById('checkbox-binary').checked = showBinary;

            encodingTableElem.innerHTML = encodingTableHTML;
            encodedTextElem.textContent = encodedString;

            new p5(sketch);
        });
    </script>
</body>
</html>
